prompt Creating view NEW_V_MONTH_START_ZBALANCE
prompt ========================================
prompt
create or replace force view fd_account.new_v_month_start_zbalance as
select (t.record_date + 1) record_date,
       t.company_id,
       t.account_type,
       t.channel_no,
       t.channel_name,
       t.account_id,
       t.account_name,
       t.channel_status,
