       d.company_id,
       d.account_type,
       d.channel_no,
       d.channel_name,
       d.account_id,
       d.account_name,
       d.channel_status,
       d.account_status,
       d.balance,
       d.query_balance,
       d.warn_balance
  from fd_report_zero_balance d
 where d.record_date = trunc(sysdate - 1)
   and d.record_date != (trunc(sysdate, 'mm') - 1);

