prompt Creating function CZTH_F_CHANNEL_COMPANY_CHECK
prompt ==============================================
prompt
create or replace function fd_account.czth_f_channel_company_check(v_up_channel_no   varchar2,
                                                        v_down_channel_no varchar2)
  return varchar2 is
  ----------------充值特惠公司检查---------------
  l_up_company_id   number;
  l_down_company_id number;
begin
  ---1.上游公司编号获取
  select t.company_id
    into l_up_company_id
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no;

  ---2.下游公司编号获取
  select t.company_id
    into l_down_company_id
    from fd_base_down_channel t
   where t.channel_no = v_down_channel_no;

  if l_up_company_id != l_down_company_id then
    return pkg_error_code.company_error;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function CZTH_F_TRADE_BACK_MOVE
prompt ========================================
prompt
create or replace function fd_account.czth_f_trade_back_move(v_order_source   number,
                                                  v_trade_order_no varchar2,
                                                  v_order_time     varchar2)
  return varchar2 is
  ------------------充值特惠订单数据历史库转移当前库-------------------
  l_end_time     date := add_months(trunc(sysdate + 3, 'dd'), -1); --历史库保留1个月前数据
  l_bak_end_time date := add_months(trunc(sysdate + 3, 'dd'), -3) - 15; --归档库保留三个半月前数据
begin
  --1.满足转移历史表的订单需尝试从历史表转移当前表
  if to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss') < l_end_time then
    insert into czth_trade_order
      (record_id,
       fd_order_id,
       trade_order_no,
       trade_delivery_no,
       order_source,
       down_channel_no,
       down_account_id,
       up_channel_no,
       up_account_id,
       company_id,
       draw_unit,
       draw_face,
       draw_amount,
       service_rate,
       change_time,
       change_type,
       bill_type,
       business_type,
       carrier_no,
       province_no,
       memo)
      select t.record_id,
             t.fd_order_id,
             t.trade_order_no,
             t.trade_delivery_no,
             t.order_source,
             t.down_channel_no,
             t.down_account_id,
             t.up_channel_no,
             t.up_account_id,
             t.company_id,
             t.draw_unit,
             t.draw_face,
             t.draw_amount,
             t.service_rate,
             t.change_time,
             t.change_type,
             t.bill_type,
             t.business_type,
             t.carrier_no,
             t.province_no,
             t.memo
        from czth_trade_order_hs t
       where t.trade_order_no = v_trade_order_no
         and t.order_source = v_order_source;
  
  end if;

  --2.满足转移备份表的订单需尝试从备份表转移当前表
  if to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss') < l_bak_end_time then
    insert into czth_trade_order
      (record_id,
       fd_order_id,
       trade_order_no,
       trade_delivery_no,
       order_source,
       down_channel_no,
       down_account_id,
       up_channel_no,
       up_account_id,
       company_id,
       draw_unit,
       draw_face,
       draw_amount,
       service_rate,
       change_time,
       change_type,
       bill_type,
       business_type,
       carrier_no,
       province_no,
       memo)
      select t.record_id,
             t.fd_order_id,
             t.trade_order_no,
             t.trade_delivery_no,
             t.order_source,
             t.down_channel_no,
             t.down_account_id,
             t.up_channel_no,
             t.up_account_id,
             t.company_id,
             t.draw_unit,
             t.draw_face,
             t.draw_amount,
             t.service_rate,
             t.change_time,
             t.change_type,
             t.bill_type,
             t.business_type,
             t.carrier_no,
             t.province_no,
             t.memo
        from czth_trade_order_hs t ---@agentbackupdb t
       where t.trade_order_no = v_trade_order_no
         and t.order_source = v_order_source;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_BACK_MOVE2
prompt =======================================
prompt
create or replace function fd_account.fd_f_trade_back_move2(v_order_source   number,
                                                 v_trade_order_no varchar2)
  return varchar2 is
  -------------------交易资金变动由离线库转移到当前库-----------------
  l_order_add_count     number;
  l_order_del_count     number;
  l_down_fund_add_count number;
  l_down_fund_del_count number;
  l_up_fund_add_count   number;
  l_up_fund_del_count   number;
begin
  ---1.转移历史库可能存在的订单信息
  insert into fd_trade_order_main
    (fd_order_id,
     down_channel_no,
     down_account_id,
     down_company_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     business_type,
     carrier_no,
     province_no,
     city_no,
     total_face,
     recharge_unit,
     recharge_account_no,
     has_up_trade,
     down_order_amount)
    select fd_order_id,
           down_channel_no,
           down_account_id,
           down_company_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           business_type,
           carrier_no,
           province_no,
           city_no,
           total_face,
           recharge_unit,
           recharge_account_no,
           has_up_trade,
           down_order_amount
      from fd_trade_order_main_hs t ---@agentbackupdb t
     where t.trade_order_no = v_trade_order_no
       and t.order_source = v_order_source;
  l_order_add_count := sql%rowcount;

  ---2.删除离线库订单主表数据
  delete from fd_trade_order_main_hs t ---@agentbackupdb t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source;
  l_order_del_count := sql%rowcount;

  if l_order_del_count != l_order_add_count then
    return pkg_error_code.data_error;
  end if;

  ---3.下游资金变动数据
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee) ----添加手续费
    select record_id,
           fd_order_id,
           channel_no,
           account_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           change_type,
           change_amount,
           balance,
           memo,
           service_fee
      from fd_trade_down_fund_hs t ---@agentbackupdb t
     where t.trade_order_no = v_trade_order_no
       and t.order_source = v_order_source;
  l_down_fund_add_count := sql%rowcount;

  ---4.删除历史库下游资金变动数据
  delete from fd_trade_down_fund_hs t ---@agentbackupdb t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source;
  l_down_fund_del_count := sql%rowcount;

  if l_down_fund_del_count != l_down_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  ---5.上游资金变动数据
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_batch_id,
     settle_status,
     settle_time,
     memo,
     order_time,
     split_profit_status,
     out_change_time)
    select record_id,
           fd_order_id,
           trade_order_no,
           order_source,
           trade_delivery_no,
           down_channel_no,
           down_account_id,
           down_company_id,
           up_channel_no,
           up_account_id,
           up_company_id,
           down_draw_unit,
           down_draw_face,
           down_draw_amount,
           down_real_amount,
           up_draw_unit,
           up_draw_face,
           up_draw_amount,
           up_real_amount,
           change_time,
           change_type,
           bill_type,
           business_type,
           carrier_no,
           province_no,
           balance,
           profits,
           down_commission,
           up_commission,
           settle_amount,
           settle_batch_id,
           settle_status,
           settle_time,
           memo,
           order_time,
           split_profit_status,
           out_change_time
      from fd_trade_up_fund_hs t ---@agentbackupdb t
     where t.trade_order_no = v_trade_order_no
       and t.order_source = v_order_source;
  l_up_fund_add_count := sql%rowcount;

  ---6.删除历史库上游资金变动数据
  delete from fd_trade_up_fund_hs t ---@agentbackupdb t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source;
  l_up_fund_del_count := sql%rowcount;

  if l_up_fund_del_count != l_up_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  return pkg_error_code.success;
end;
/

