prompt Creating function DK_F_SYSTEM_ID_GET
prompt ====================================
prompt
create or replace function fd_account.dk_f_system_id_get(v_source_name varchar2,
                                              v_source_id   out number)
  return varchar2 is
  ---------------------通过名称获取系统编号------------------
begin

  select t.source_system_id
    into v_source_id
    from fd_base_source_system t
   where t.source_system_name = v_source_name
     and rownum <= 2;

  return pkg_error_code.success;
exception
  when others then
    fd_p_write_log('dk_f_system_id_get', '系统名称有重复项！请检查！');
    return pkg_error_code.failure;
end;
/

prompt
prompt Creating function DK_F_TRADE_AC_PAY_ACCOUNT_GET
prompt ===============================================
prompt
create or replace function fd_account.dk_f_trade_ac_pay_account_get(v_order_source  number,
                                                         v_up_channel_no varchar2,
                                                         v_up_account_id number)
  return varchar2 is

  -----------------获取上游支付账户编号------------------
  l_up_account_id number;
begin

  ---1.获取上游渠道唯一账户，当有多个账户时，请使用其它方式获取
  select t.account_id
    into l_up_account_id
    from dk_base_up_account_map t
   inner join dk_base_up_account a on t.account_id = a.account_id
   inner join dk_base_up_channel c on t.channel_no = c.channel_no
   inner join dk_base_up_system_map s on t.channel_no = s.channel_no
   where t.channel_no = v_up_channel_no
     and t.account_id = v_up_account_id
     and s.source_system_id = v_order_source
     and a.status = 0
     and c.status = 0
     and rownum <= 1;

  return pkg_error_code.success;
exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function DK_F_TRADE_DOWN_FEE_REFUND
prompt ============================================
prompt
create or replace function fd_account.dk_f_trade_down_fee_refund(v_order_source    in number,
                                                      v_down_channel_no in varchar2,
                                                      v_trade_order_no  in varchar2,
                                                      v_trade_refund_no varchar2,
                                                      v_refund_unit     number,
                                                      v_refund_face     number,
                                                      v_refund_amount   number,
                                                      v_real_refund     number,
                                                      v_order_date      varchar2,
                                                      v_service_fee     number,
                                                      v_memo            varchar2,
                                                      v_sys_paid_amount number)
  return varchar2 is
  l_dk_order_id       number;
  l_down_account_id   number;
  l_order_source      number;
  l_trade_order_no    varchar2(32);
  l_ext_order_no      varchar2(32);
  l_down_channel_no   varchar2(32);
  l_refund_count      number;
  l_total_refund      number;
  l_inner_balance     number;
  l_down_fund_id      number;
  l_down_order_face   number;
  l_down_order_amount number;
  l_result            varchar2(32);

  l_debit_count       number;
  l_total_refund_face number;
  l_business_type     number;
  l_order_total_face  number;
  ------------下游订单退款------------
begin
  --===========================检查订单重复退款、金额错误==========================
  ---1.锁下游订单
  select t.dk_order_id,
         t.order_source,
         t.down_channel_no,
         t.down_account_id,
         t.ext_order_no,
         t.trade_order_no,
         t.total_face,
         t.down_order_amount,
         t.business_type,
         t.total_face * t.recharge_times
    into l_dk_order_id,
         l_order_source,
         l_down_channel_no,
         l_down_account_id,
         l_ext_order_no,
         l_trade_order_no,
         l_down_order_face,
         l_down_order_amount,
         l_business_type,
         l_order_total_face
    from dk_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1
     for update;

  ---2.检测订单重复退款
  select count(1)
    into l_refund_count
    from dk_trade_down_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = l_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---2.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from dk_trade_down_fund t
   where t.dk_order_id = l_dk_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = l_order_source
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---3.获取订单扣款、已退款金额
  select nvl(sum(t.refund_amount), 0), nvl(sum(t.refund_face), 0)
    into l_total_refund, l_total_refund_face
    from dk_trade_down_refund t
   where t.dk_order_id = l_dk_order_id;

  ---4.检查退款金额是否超过订单总面值
  --- 总面值 yangdejian
  if (v_refund_amount > (l_order_total_face - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  --==================================修改账户余额===============================
  ---5.获取并修改账户余额
  select t.inner_balance
    into l_inner_balance
    from dk_base_down_account t
   where t.account_id = l_down_account_id
     for update;
  ----（退款金额已扣除手续费）
  update dk_base_down_account t
     set t.inner_balance = (l_inner_balance + v_refund_amount)
   where t.account_id = l_down_account_id;

  --===================添加数据变动记录=============
  ---6.添加退款记录
  select seq_trade_down_fund_id.nextval into l_down_fund_id from dual;

  insert into dk_trade_down_refund
    (record_id,
     channel_no,
     account_id,
     dk_order_id,
     trade_order_no,
     trade_refund_no,
     order_source,
     refund_time,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund, ---删掉了退款类型
     memo)
    select l_down_fund_id,
           t.down_channel_no,
           t.down_account_id,
           t.dk_order_id,
           t.trade_order_no,
           v_trade_refund_no,
           t.order_source,
           sysdate,
           t.business_type,
           t.carrier_no,
           t.province_no,
           v_refund_unit,
           v_refund_face,
           v_refund_amount,
           v_real_refund,
           v_memo
      from dk_trade_order_main t
     where t.dk_order_id = l_dk_order_id;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---7.添加下游资金变动
  insert into dk_trade_down_fund
    (record_id,
     dk_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (l_down_fund_id,
     l_dk_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     l_ext_order_no,
     v_order_source,
     sysdate,
     to_date(v_order_date, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_down_fund_change_type.refund_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount),
     v_memo,
     v_service_fee); ---退手续费

  --===========================订单差异信息记录=================================
  --- 垫付金额不能大于面值
  if (v_sys_paid_amount > v_refund_face) then
    return pkg_error_code.amount_error;
  end if;

  if (v_sys_paid_amount != 0) then
    insert into dk_trade_refund_sys_cost
      (id,
       record_date,
       dk_order_id,
       channel_no,
       account_id,
       business_type,
       sys_cost,
       trade_order_no,
       trade_refund_no)
    values
      (seq_dktraderefundsyscost_id.nextval,
       sysdate,
       l_dk_order_id,
       v_down_channel_no,
       l_down_account_id,
       l_business_type,
       v_sys_paid_amount,
       v_trade_order_no,
       v_trade_refund_no);
  end if;

  ---10调用差异调整函数
  l_result := dk_f_trade_fund_differ_add(l_dk_order_id,
                                         pkg_trade_type.down_refund,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         pkg_bill_type.front,
                                         l_down_order_face,
                                         l_down_order_amount,
                                         v_refund_face,
                                         v_refund_amount,
                                         0);

  return l_result;
end;
/

prompt
prompt Creating function DK_F_TRADE_DOWN_LOSS_REFUND
prompt =============================================
prompt
create or replace function fd_account.dk_f_trade_down_loss_refund(v_order_source    in number,
                                                       v_down_channel_no in varchar2,
                                                       v_trade_order_no  in varchar2,
                                                       v_trade_refund_no varchar2,
                                                       v_refund_unit     number,
                                                       v_refund_face     number,
                                                       v_refund_amount   number,
                                                       v_real_refund     number,
                                                       v_order_date      varchar2,
                                                       v_service_fee     number,
                                                       v_memo            varchar2,
                                                       v_sys_paid_amount number,
                                                       v_calc_profit     number)
  return varchar2 is
  l_dk_order_id       number;
  l_down_account_id   number;
  l_order_source      number;
  l_trade_order_no    varchar2(32);
  l_ext_order_no      varchar2(32);
  l_down_channel_no   varchar2(32);
  l_refund_count      number;
  l_total_refund      number;
  l_inner_balance     number;
  l_down_fund_id      number;
  l_down_order_face   number;
  l_down_order_amount number;
  l_result            varchar2(32);

  l_debit_count       number;
  l_total_refund_face number;
  l_business_type     number;
  l_order_total_face  number;
  l_down_company_id   number;
  l_province_no       varchar2(32);
  l_carrier_no        varchar2(32);
  ------------下游订单退款------------
begin
  --===========================检查订单重复退款、金额错误==========================
  ---1.锁下游订单
  select t.dk_order_id,
         t.order_source,
         t.down_channel_no,
         t.down_account_id,
         t.ext_order_no,
         t.trade_order_no,
         t.total_face,
         t.down_order_amount,
         t.business_type,
         t.total_face * t.recharge_times,
         t.down_company_id,
         t.province_no,
         t.carrier_no
    into l_dk_order_id,
         l_order_source,
         l_down_channel_no,
         l_down_account_id,
         l_ext_order_no,
         l_trade_order_no,
         l_down_order_face,
         l_down_order_amount,
         l_business_type,
         l_order_total_face,
         l_down_company_id,
         l_province_no,
         l_carrier_no
    from dk_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1
     for update;

  ---2.检测订单重复退款
  select count(1)
    into l_refund_count
    from dk_trade_down_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = l_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---2.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from dk_trade_down_fund t
   where t.dk_order_id = l_dk_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = l_order_source
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---3.获取订单扣款、已退款金额
  select nvl(sum(t.refund_amount), 0), nvl(sum(t.refund_face), 0)
    into l_total_refund, l_total_refund_face
    from dk_trade_down_refund t
   where t.dk_order_id = l_dk_order_id;

  ---4.检查退款金额是否超过订单总面值
  --- 总面值 yangdejian
  if (v_refund_amount > (l_order_total_face - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  --==================================修改账户余额===============================
  ---5.获取并修改账户余额
  select t.inner_balance
    into l_inner_balance
    from dk_base_down_account t
   where t.account_id = l_down_account_id
     for update;
  ----（退款金额已扣除手续费）
  update dk_base_down_account t
     set t.inner_balance = (l_inner_balance + v_refund_amount)
   where t.account_id = l_down_account_id;

  --===================添加数据变动记录=============
  ---6.添加退款记录
  select seq_trade_down_fund_id.nextval into l_down_fund_id from dual;

  insert into dk_trade_down_refund
    (record_id,
     channel_no,
     account_id,
     dk_order_id,
     trade_order_no,
     trade_refund_no,
     order_source,
     refund_time,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund, ---删掉了退款类型
     memo)
    select l_down_fund_id,
           t.down_channel_no,
           t.down_account_id,
           t.dk_order_id,
           t.trade_order_no,
           v_trade_refund_no,
           t.order_source,
           sysdate,
           t.business_type,
           t.carrier_no,
           t.province_no,
           v_refund_unit,
           v_refund_face,
           v_refund_amount,
           v_real_refund,
           v_memo
      from dk_trade_order_main t
     where t.dk_order_id = l_dk_order_id;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---7.添加下游资金变动
  insert into dk_trade_down_fund
    (record_id,
     dk_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (l_down_fund_id,
     l_dk_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     l_ext_order_no,
     v_order_source,
     sysdate,
     to_date(v_order_date, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_down_fund_change_type.refund_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount),
     v_memo,
     v_service_fee); ---退手续费

  --===========================下游退款金额需要计算利润=========================
  if v_calc_profit = pkg_sys_boolean.istrue then
    insert into dk_trade_up_fund
      (record_id,
       dk_order_id,
       trade_order_no,
       order_source,
       trade_delivery_no,
       down_channel_no,
       down_account_id,
       down_company_id,
       up_channel_no,
       up_account_id,
       up_company_id,
       down_draw_unit,
       down_draw_face,
       down_draw_amount,
       down_real_amount,
       up_draw_unit,
       up_draw_face,
       up_draw_amount,
       up_real_amount,
       change_time,
       order_time,
       change_type,
       bill_type,
       business_type,
       carrier_no,
       province_no,
       balance,
       memo)
    values
      (seq_trade_up_fund_id.nextval,
       l_dk_order_id,
       v_trade_order_no,
       v_order_source,
       'DREF' || v_trade_refund_no,
       l_down_channel_no,
       l_down_account_id,
       l_down_company_id,
       0,
       0,
       l_down_company_id,
       0,
       0,
       v_refund_amount,
       v_real_refund,
       0,
       0,
       0,
       0,
       sysdate,
       to_date(v_order_date, 'yyyy-mm-dd hh24:mi:ss'),
       pkg_up_fund_change_type.debit_amount,
       pkg_bill_type.front,
       l_business_type,
       l_carrier_no,
       l_province_no,
       0,
       v_memo);
  end if;
  --===========================订单差异信息记录=================================
  --- 垫付金额不能大于面值
  if (v_sys_paid_amount > v_refund_face) then
    return pkg_error_code.amount_error;
  end if;

  if (v_sys_paid_amount != 0) then
    insert into dk_trade_refund_sys_cost
      (id,
       record_date,
       dk_order_id,
       channel_no,
       account_id,
       business_type,
       sys_cost,
       trade_order_no,
       trade_refund_no)
    values
      (seq_dktraderefundsyscost_id.nextval,
       sysdate,
       l_dk_order_id,
       v_down_channel_no,
       l_down_account_id,
       l_business_type,
       v_sys_paid_amount,
       v_trade_order_no,
       v_trade_refund_no);
  end if;

  ---10调用差异调整函数
  l_result := dk_f_trade_fund_differ_add(l_dk_order_id,
                                         pkg_trade_type.down_refund,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         pkg_bill_type.front,
                                         l_down_order_face,
                                         l_down_order_amount,
                                         v_refund_face,
                                         v_refund_amount,
                                         0);

  return l_result;
end;
/

prompt
prompt Creating function DK_F_TRADE_DOWN_ORDER_FEE_MUS
prompt ===============================================
prompt
create or replace function fd_account.dk_f_trade_down_order_fee_mus(v_order_source    number,
                                                         v_down_channel_no varchar2,
                                                         v_trade_order_no  varchar2,
                                                         v_ext_order_no    varchar2,
                                                         v_order_time      varchar2,
                                                         v_settle_amount   number,
                                                         v_service_fee     number,
                                                         v_memo            varchar2)
  return varchar2 is
  -------------------下游扣款----------------------
  l_dk_order_id         number;
  l_pay_count           number;
  l_inner_balance       number;
  l_down_account_id     number;
  l_order_settle_amount number;
  l_total_face          number;
  l_business_type       number;
  l_result              varchar2(32) := pkg_error_code.success;
  l_recharge_times number;
begin

  ----===============================锁订单，检查是否重复扣款================================
  ---1.锁订单，检查扣款是否存在
  select t.dk_order_id,
         t.down_account_id,
         t.down_order_amount,
         t.total_face,
         t.business_type,
         t.recharge_times
    into l_dk_order_id,
         l_down_account_id,
         l_order_settle_amount,
         l_total_face,
         l_business_type,
         l_recharge_times
    from dk_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查是否存在扣款记录
  select count(1)
    into l_pay_count
    from dk_trade_down_fund t
   where t.dk_order_id = l_dk_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount;

  if (l_pay_count >= l_recharge_times) then
    return pkg_error_code.success;
  end if;

  ---3.检查从订单查询的扣款与本次扣款是否一致
  if l_order_settle_amount != v_settle_amount then
    return pkg_error_code.amount_error;
  end if;

  --=========================锁账户，检查余额并扣款=====================================
  ---3.获取下游账户信息
  select t.inner_balance
    into l_inner_balance
    from dk_base_down_account t
   where t.account_id = l_down_account_id
     for update;

  ---5.下游扣款（扣款金额已除去手续费）
  update dk_base_down_account t
     set t.inner_balance = (l_inner_balance - v_settle_amount)
   where t.account_id = l_down_account_id;

  ---6.添加下游资金变动
  insert into dk_trade_down_fund
    (record_id,
     dk_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (seq_trade_down_fund_id.nextval,
     l_dk_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     v_ext_order_no,
     v_order_source,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_down_fund_change_type.debit_amount,
     v_settle_amount,
     (l_inner_balance - v_settle_amount),
     v_memo,
     v_service_fee); ---加上手续费

  ---7.扣款成功记录订单差异表
  l_result := dk_f_trade_fund_differ_add(l_dk_order_id,
                                         pkg_trade_type.down_debit,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         pkg_bill_type.front,
                                         l_total_face,
                                         l_order_settle_amount,
                                         l_total_face,
                                         v_settle_amount,
                                         0);

  return l_result;
end;
/

prompt
prompt Creating function DK_F_TRADE_HS_MOVE
prompt ====================================
prompt
create or replace function fd_account.dk_f_trade_hs_move(v_order_source    number,
                                              v_down_channel_no varchar2,
                                              v_trade_order_no  varchar2)
  return varchar2 is
  -------------------历史库中的数据转移到当前库--------------------
  l_order_count         number;
  l_order_hs_count      number;
  l_dk_order_id         number;
  l_order_add_count     number;
  l_down_fund_add_count number;
  l_up_fund_add_count   number;
  l_order_del_count     number;
  l_down_fund_del_count number;
  l_up_fund_del_count   number;
begin
  ---1.检查当前库订单是否存在
  select count(1)
    into l_order_count
    from dk_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1;

  if l_order_count > 0 then
    return pkg_error_code.success;
  end if;

  ---2.当前库订单不存在，检查历史库订单是否存在
  select count(1), max(t.dk_order_id)
    into l_order_hs_count, l_dk_order_id
    from dk_trade_order_main_hs t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1;

  if l_order_hs_count = 0 then
    return pkg_error_code.order_not_exists;
  end if;

  ---3.历史库订单存在，获取历史库订单及其变动数据存入当前库
  ---3.1订单主表数据
  insert into dk_trade_order_main
    (dk_order_id,
     down_channel_no,
     down_account_id,
     down_company_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     business_type,
     carrier_no,
     province_no,
     city_no,
     total_face,
     recharge_unit,
     recharge_account_no,
     down_order_amount,
     recharge_times)
    select dk_order_id,
           down_channel_no,
           down_account_id,
           down_company_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           business_type,
           carrier_no,
           province_no,
           city_no,
           total_face,
           recharge_unit,
           recharge_account_no,
           t.down_order_amount,
           t.recharge_times
      from dk_trade_order_main_hs t
     where t.dk_order_id = l_dk_order_id;
  l_order_add_count := sql%rowcount;

  if l_order_add_count = 0 then
    return pkg_error_code.data_error;
  end if;

  ---3.2删除历史库订单主表数据
  delete from dk_trade_order_main_hs t where t.dk_order_id = l_dk_order_id;
  l_order_del_count := sql%rowcount;

  if l_order_del_count != l_order_add_count then
    return pkg_error_code.data_error;
  end if;

  ---3.3下游资金变动数据
  insert into dk_trade_down_fund
    (record_id,
     dk_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee) ----添加手续费
    select record_id,
           dk_order_id,
           channel_no,
           account_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           change_type,
           change_amount,
           balance,
           memo,
           service_fee
      from dk_trade_down_fund_hs t
     where t.dk_order_id = l_dk_order_id;
  l_down_fund_add_count := sql%rowcount;

  ---3.4删除历史库下游资金变动数据
  delete from dk_trade_down_fund_hs t where t.dk_order_id = l_dk_order_id;
  l_down_fund_del_count := sql%rowcount;

  if l_down_fund_del_count != l_down_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  ---3.5上游资金变动数据
  insert into dk_trade_up_fund
    (record_id,
     dk_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_batch_id,
     settle_status,
     settle_time,
     memo,
     order_time)
    select record_id,
           dk_order_id,
           trade_order_no,
           order_source,
           trade_delivery_no,
           down_channel_no,
           down_account_id,
           down_company_id,
           up_channel_no,
           up_account_id,
           up_company_id,
           down_draw_unit,
           down_draw_face,
           down_draw_amount,
           down_real_amount,
           up_draw_unit,
           up_draw_face,
           up_draw_amount,
           up_real_amount,
           change_time,
           change_type,
           bill_type,
           business_type,
           carrier_no,
           province_no,
           balance,
           profits,
           down_commission,
           up_commission,
           settle_amount,
           settle_batch_id,
           settle_status,
           settle_time,
           memo,
           order_time
      from dk_trade_up_fund_hs t
     where t.dk_order_id = l_dk_order_id;
  l_up_fund_add_count := sql%rowcount;

  ---3.6删除历史库上游资金变动数据
  delete from dk_trade_up_fund_hs t where t.dk_order_id = l_dk_order_id;
  l_up_fund_del_count := sql%rowcount;

  if l_up_fund_del_count != l_up_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function DK_F_TRADE_ORDER_EXIST_CHECK
prompt ==============================================
prompt
create or replace function fd_account.dk_f_trade_order_exist_check(v_order_source    number,
                                                        v_down_channel_no varchar2,
                                                        v_trade_order_no  varchar2,
                                                        v_fd_order_id     out number)
  return varchar2 is

  ----------------------------检查订单是否存在-----------------------------

begin
  ---1.检查订单是否存在
  select t.dk_order_id
    into v_fd_order_id
    from dk_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and rownum <= 1;

  return pkg_error_code.success;
exception
  when others then
    ---2.返回订单不存在
    return pkg_error_code.order_or_account_not_exists;
end;
/

prompt
prompt Creating function DK_F_TRADE_ORDER_CREATE
prompt =========================================
prompt
create or replace function fd_account.dk_f_trade_order_create(v_order_source        number,
                                                   v_down_channel_no     varchar2,
                                                   v_trade_order_no      varchar2,
                                                   v_ext_order_no        varchar2,
                                                   v_recharge_account_no varchar2,
                                                   v_business_type       number,
                                                   v_carrier_no          varchar2,
                                                   v_province_no         varchar2,
                                                   v_city_no             varchar2,
                                                   v_total_face          number,
                                                   v_deduct_unit         number,
                                                   v_settle_amount       number,
                                                   v_order_time          varchar2,
                                                   v_recharge_times      number)
  return varchar2 is

  l_result          varchar2(32);
  l_dk_order_id     number;
  l_down_account_id number;
  l_down_company_id number;
  ----------------------------检查并创建订单-----------------------------

begin
  ---1.检查订单是否存在
  l_result := dk_f_trade_order_exist_check(v_order_source,
                                           v_down_channel_no,
                                           v_trade_order_no,
                                           l_dk_order_id);

  if (l_result = pkg_error_code.success) then
    return pkg_error_code.success;
  end if;

  ---2.获取下游账户和公司
  select t.account_id, c.company_id
    into l_down_account_id, l_down_company_id
    from dk_base_down_account_map t
   inner join dk_base_down_account a on t.account_id = a.account_id
   inner join dk_base_down_channel c on t.channel_no = c.channel_no
   inner join dk_base_down_system_map s on t.channel_no = s.channel_no
   where t.channel_no = v_down_channel_no
     and s.source_system_id = v_order_source
        --  and a.status = 0
        --  and c.status = 0
     and rownum <= 2;

  ---3.添加订单
  select seq_trade_order_main_id.nextval into l_dk_order_id from dual;

  insert into dk_trade_order_main
    (dk_order_id,
     down_channel_no,
     down_account_id,
     down_company_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     business_type,
     carrier_no,
     province_no,
     city_no,
     total_face,
     recharge_unit,
     recharge_account_no,
     down_order_amount,
     recharge_times)
  values
    (l_dk_order_id,
     v_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_trade_order_no,
     v_ext_order_no,
     v_order_source,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_city_no,
     v_total_face,
     v_deduct_unit,
     v_recharge_account_no,
     v_settle_amount,
     v_recharge_times);

  return pkg_error_code.success;

EXCEPTION
  WHEN others THEN
    ---4.订单添加失败，检查是否并发添加
    l_result := dk_f_trade_order_exist_check(v_order_source,
                                             v_down_channel_no,
                                             v_trade_order_no,
                                             l_dk_order_id);
  
    if l_result != pkg_error_code.success then
      fd_p_write_log('dk_f_trade_order_create', sqlerrm);
      return pkg_error_code.order_insert_fail;
    end if;
  
    return pkg_error_code.success;
end;
/

prompt
prompt Creating function DK_F_TRADE_PAY_ACCOUNT_GET
prompt ============================================
prompt
create or replace function fd_account.dk_f_trade_pay_account_get(v_order_source  number,
                                                      v_up_channel_no varchar2,
                                                      v_up_account_id out number)
  return varchar2 is

  -----------------获取上游支付账户编号------------------
begin

  ---1.获取上游渠道唯一账户，当有多个账户时，请使用其它方式获取
  select t.account_id
    into v_up_account_id
    from dk_base_up_account_map t
   inner join dk_base_up_account a on t.account_id = a.account_id
   inner join dk_base_up_channel c on t.channel_no = c.channel_no
   inner join dk_base_up_system_map s on t.channel_no = s.channel_no
   where t.channel_no = v_up_channel_no
     and s.source_system_id = v_order_source
   --  and a.status = 0
