prompt
prompt Creating function FD_F_TRADE_DOWN_ORDER_MS_MUS
prompt ==============================================
prompt
create or replace function fd_account.fd_f_trade_down_order_ms_mus(v_order_source    number,
                                                        v_down_channel_no varchar2,
                                                        v_trade_order_no  varchar2,
                                                        v_ext_order_no    varchar2,
                                                        v_order_time      varchar2,
                                                        v_order_amount    number,
                                                        v_service_fee     number,
                                                        v_memo            varchar2)
  return varchar2 is
  -------------------------------------------
  --功能：短信平台下游扣款专用
  --创建人：周荣省
  --创建时间：2019-05-20
  -------------------------------------------
  l_credit_money      number;
  l_fd_order_id       number;
  l_pay_count         number;
  l_inner_balance     number;
  l_down_account_id   number;
  l_order_main_amount number;
  l_total_face        number;
  l_business_type     number;
  l_result            varchar2(32);
begin

  ----===============================锁订单，检查是否重复扣款================================
  ---1.锁订单，检查扣款是否存在
  select t.fd_order_id,
         t.down_account_id,
         t.down_order_amount,
         t.total_face,
         t.business_type
    into l_fd_order_id,
         l_down_account_id,
         l_order_main_amount,
         l_total_face,
         l_business_type
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查是否存在扣款记录
  select count(1)
    into l_pay_count
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and rownum <= 1;

  if (l_pay_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.检查从订单查询的扣款与本次扣款是否一致
  if l_order_main_amount != v_order_amount then
    return pkg_error_code.amount_error;
  end if;

  --=========================锁账户，检查余额并扣款=====================================
  ---3.获取下游账户信息
  select t.inner_balance, t.credit_money
    into l_inner_balance, l_credit_money
    from fd_base_down_account t
   where t.account_id = l_down_account_id
     for update;

  ---4.下游扣款检查账户余额
  if (v_order_amount > (l_inner_balance + l_credit_money)) then
    return pkg_error_code.balance_low;
  end if;

  ---5.下游扣款（扣款金额已除去手续费）
  update fd_base_down_account t
     set t.inner_balance = (l_inner_balance - v_order_amount)
   where t.account_id = l_down_account_id;

  ---6.添加下游资金变动
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (seq_trade_down_fund_id.nextval,
     l_fd_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     v_ext_order_no,
     v_order_source,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_down_fund_change_type.debit_amount,
     v_order_amount,
     (l_inner_balance - v_order_amount),
     v_memo,
     v_service_fee); ---加上手续费

  ---7.扣款成功记录订单差异表
  l_result := fd_f_trade_fund_ms_differ_add(l_fd_order_id,
                                            pkg_trade_type.down_debit,
                                            v_down_channel_no,
                                            l_down_account_id,
                                            l_business_type,
                                            pkg_bill_type.front,
                                            l_total_face,
                                            l_order_main_amount,
                                            l_total_face,
                                            v_order_amount,
                                            0);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_DOWN_REFUND
prompt ========================================
prompt
create or replace function fd_account.fd_f_trade_down_refund(v_order_source    in number,
                                                  v_down_channel_no in varchar2,
                                                  v_trade_order_no  in varchar2,
                                                  v_trade_refund_no varchar2,
                                                  v_refund_unit     number,
                                                  v_refund_face     number,
                                                  v_refund_amount   number,
                                                  v_real_refund     number,
                                                  v_memo            varchar2)
  return varchar2 is
  l_fd_order_id     number;
  l_down_account_id number;
  l_order_source    number;
  l_trade_order_no  varchar2(32);
  l_ext_order_no    varchar2(32);
  l_down_channel_no varchar2(32);
  l_order_time      date;
  l_refund_count    number;
  l_total_amount    number;
  l_total_refund    number;
  l_inner_balance   number;
  l_down_fund_id    number;

  ------------下游订单退款------------
begin
  --===========================检查订单重复退款、金额错误==========================
  ---1.锁下游订单
  select t.fd_order_id,
         t.order_source,
         t.down_channel_no,
         t.down_account_id,
         t.ext_order_no,
         t.trade_order_no,
         t.order_time
    into l_fd_order_id,
         l_order_source,
         l_down_channel_no,
         l_down_account_id,
         l_ext_order_no,
         l_trade_order_no,
         l_order_time
    from fd_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1
     for update;

  ---2.检测订单重复退款
  select count(1)
    into l_refund_count
    from fd_trade_down_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = l_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.获取订单扣款、已退款金额
  select t.change_amount
    into l_total_amount
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = l_order_source
     and rownum <= 1;

  select sum(t.change_amount)
    into l_total_refund
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.refund_amount
     and t.order_source = l_order_source;

  ---4.检查退款金额是否超过订单总金额
  if (v_refund_amount > (l_total_amount - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  --==================================修改账户余额===============================
  ---5.获取并修改账户余额
  select t.inner_balance
    into l_inner_balance
    from fd_base_down_account t
   where t.account_id = l_down_account_id
     for update;

  update fd_base_down_account t
     set t.inner_balance = (l_inner_balance + v_refund_amount)
   where t.account_id = l_down_account_id;

  --===================添加数据变动记录=============
  ---6.添加退款记录
  select seq_trade_down_fund_id.nextval into l_down_fund_id from dual;

  insert into fd_trade_down_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_refund_no,
     order_source,
     refund_time,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
    select l_down_fund_id,
           t.down_channel_no,
           t.down_account_id,
           t.fd_order_id,
           t.trade_order_no,
           v_trade_refund_no,
           t.order_source,
           sysdate,
           t.business_type,
           t.carrier_no,
           t.province_no,
           v_refund_unit,
           v_refund_face,
           v_refund_amount,
           v_real_refund,
           v_memo
      from fd_trade_order_main t
     where t.fd_order_id = l_fd_order_id;

  ---7.添加下游资金变动
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo)
  values
    (l_down_fund_id,
     l_fd_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     l_ext_order_no,
     v_order_source,
     sysdate,
     l_order_time,
     pkg_down_fund_change_type.refund_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount),
     v_memo);

  ---8.检查上游中的下游扣款与订单的下游扣款是否一致
  ---9.上游中的下游扣款大于订单的下游扣款计算差异调整金额
  ---10调用差异调整函数

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_DOWN_REFUND_T
prompt ==========================================
prompt
create or replace function fd_account.fd_f_trade_down_refund_t(v_order_source    in number,
                                                    v_down_channel_no in varchar2,
                                                    v_trade_order_no  in varchar2,
                                                    v_trade_refund_no varchar2,
                                                    v_business_type   number,
                                                    v_carrier_no      varchar2,
                                                    v_province_no     varchar2,
                                                    v_refund_unit     number,
                                                    v_refund_face     number,
                                                    v_refund_amount   number,
                                                    v_real_refund     number,
                                                    v_ext_order_no    varchar2,
                                                    v_refund_time     varchar2,
                                                    v_order_time      varchar2,
                                                    v_memo            varchar2)
  return varchar2 is
  l_fd_order_id     number;
  l_down_account_id number;
  l_order_source    number;
  l_trade_order_no  varchar2(32);
  l_ext_order_no    varchar2(32);
  l_down_channel_no varchar2(32);
  l_refund_time     date;
  l_order_time      date;
  l_refund_count    number;
  l_inner_balance   number;
  l_down_fund_id    number;
  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);

  l_cn_refund_count number;
  l_cn_fd_order_id  number;
  ------------下游订单退款------------
begin
  --===========================检查订单重复退款、金额错误==========================
  ---1.锁下游订单
  select to_date(v_refund_time, 'yyyy-mm-dd hh24:mi:ss'),
         to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss')
    into l_refund_time, l_order_time
    from dual;

  begin
    select t.fd_order_id,
           t.order_source,
           t.down_channel_no,
           t.down_account_id,
           t.ext_order_no,
           t.trade_order_no,
           t.order_time
      into l_fd_order_id,
           l_order_source,
           l_down_channel_no,
           l_down_account_id,
           l_ext_order_no,
           l_trade_order_no,
           l_order_time
      from fd_trade_order_main t
     where t.order_source = v_order_source
       and t.down_channel_no = v_down_channel_no
       and t.trade_order_no = v_trade_order_no
       and rownum <= 1
       for update;
    --===============================---
    select t.business_type, t.carrier_no, t.province_no
      into l_business_type, l_carrier_no, l_province_no
      from fd_trade_order_main t
     where t.fd_order_id = l_fd_order_id;
  exception
    when no_data_found then
      --===============================---
      select seq_trade_order_main_id.nextval into l_fd_order_id from dual;
    
      select count(1), max(t.fd_order_id)
        into l_cn_refund_count, l_cn_fd_order_id
        from fd_trade_up_fund t
       where t.trade_order_no = v_trade_order_no
         and t.order_source = v_order_source
         and rownum <= 1;
    
      if (l_cn_refund_count > 0) then
        l_fd_order_id := l_cn_fd_order_id;
      end if;
    
      select t.account_id, a.inner_balance
        into l_down_account_id, l_inner_balance
        from fd_base_down_account_map t
       inner join fd_base_down_account a on t.account_id = a.account_id
       where t.channel_no = v_down_channel_no
         and t.is_main_channel = 0;
  end;
  ---2.检测订单重复退款
  select count(1)
    into l_refund_count
    from fd_trade_down_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = l_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  /*  ---3.获取订单扣款、已退款金额
  select t.change_amount
    into l_total_amount
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = l_order_source
     and rownum <= 1;
  
  select sum(t.change_amount)
    into l_total_refund
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.refund_amount
     and t.order_source = l_order_source;
  
  ---4.检查退款金额是否超过订单总金额
  if (v_refund_amount > (l_total_amount - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;*/

  --==================================修改账户余额===============================
  ---5.获取并修改账户余额
  select t.inner_balance
    into l_inner_balance
    from fd_base_down_account t
   where t.account_id = l_down_account_id
     for update;

  update fd_base_down_account t
     set t.inner_balance = (l_inner_balance + v_refund_amount)
   where t.account_id = l_down_account_id;

  --===================添加数据变动记录=============
  ---6.添加退款记录
  select seq_trade_down_fund_id.nextval into l_down_fund_id from dual;

  insert into fd_trade_down_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_refund_no,
     order_source,
     refund_time,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  --===============================---
  values
    (l_down_fund_id,
     v_down_channel_no,
     l_down_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_refund_no,
     v_order_source,
     sysdate,
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_refund_unit,
     v_refund_face,
     v_refund_amount,
     v_real_refund,
     v_memo);
  /*    select l_down_fund_id,
        t.down_channel_no,
        t.down_account_id,
        t.fd_order_id,
        t.trade_order_no,
        v_trade_refund_no,
        t.order_source,
        sysdate,
        t.business_type,
        t.carrier_no,
        t.province_no,
        v_refund_unit,
        v_refund_face,
        v_refund_amount,
        v_real_refund,
        v_memo
   from fd_trade_order_main t
  where t.fd_order_id = l_fd_order_id;*/

  ---7.添加下游资金变动
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo)
  values
    (l_down_fund_id,
     l_fd_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     v_ext_order_no,
     v_order_source,
     l_refund_time,
     l_order_time,
     pkg_down_fund_change_type.refund_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount),
     v_memo);

  ---8.检查上游中的下游扣款与订单的下游扣款是否一致
  ---9.上游中的下游扣款大于订单的下游扣款计算差异调整金额
  ---10调用差异调整函数

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_DOWN_RV_TRADE
prompt ==========================================
prompt
create or replace function fd_account.fd_f_trade_down_rv_trade(v_down_channel_no  varchar2,
                                                    v_down_account_id  number,
                                                    v_down_company_id  number,
                                                    v_adjust_record_no varchar2,
                                                    v_down_draw_amount number,
                                                    v_down_real_amount number,
                                                    v_business_type    number,
                                                    v_bill_type        number,
                                                    v_create_user      varchar2,
                                                    v_memo             varchar2)
  return varchar2 is
  l_trade_fund_id    number;
  l_inner_balance    number;
  l_trade_up_fund_id number;
  l_adjust_count     number;

  --------------------下游渠道交易平账-----------------------
begin
  ---1.锁下游支付账户
  select t.inner_balance,
         seq_trade_down_fund_id.nextval,
         seq_trade_up_fund_id.nextval
    into l_inner_balance, l_trade_fund_id, l_trade_up_fund_id
    from fd_base_down_account t
   where t.account_id = v_down_account_id
     for update;

  ---2.检查平账是否已处理
  select count(1)
    into l_adjust_count
    from fd_trade_down_manual t
   where t.ext_record_no = v_adjust_record_no;

  if l_adjust_count > 0 then
    return pkg_error_code.success;
  end if;

  ---3.若交易扣款不等于0，则需修改账户余额
  if v_down_draw_amount != 0 then
    update fd_base_down_account t
       set t.inner_balance  = (l_inner_balance - v_down_draw_amount),
           t.last_edit_user = v_create_user,
           t.last_edit_time = sysdate
     where t.account_id = v_down_account_id;
  
  end if;

  ---4.添加平账对应的下游手工记录
  insert into fd_trade_down_manual
    (record_id,
     channel_no,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     ext_record_no)
  values
    (l_trade_fund_id,
     v_down_channel_no,
     v_down_account_id,
     0,
     0,
     pkg_down_manual_change_type.trade_revise,
     v_down_draw_amount,
     sysdate,
     (l_inner_balance - v_down_draw_amount),
     v_create_user,
     nvl2(v_memo, v_memo || ',下游交易平账', '下游交易平账'),
     v_adjust_record_no);

  ---5.添加平账对应的下游资金变动添加(通过trade_order_no关联上游资金变动)
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     change_type,
     change_amount,
     balance,
     memo)
  values
    (l_trade_fund_id,
     0,
     v_down_channel_no,
     v_down_account_id,
     'SARV' || l_trade_up_fund_id,
     pkg_down_fund_change_type.debit_amount,
     v_down_draw_amount,
     (l_inner_balance - v_down_draw_amount),
     v_memo);

  ---6.为了计算佣金利润，添加下游对应的上游资金变动（上游下游公司相同避免结算)
  insert into fd_trade_up_fund
    (record_id,
     trade_order_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     balance,
     memo)
  values
    (l_trade_up_fund_id,
     'SARV' || l_trade_up_fund_id,
     v_down_channel_no,
     v_down_account_id,
     v_down_company_id,
     0,
     0,
     v_down_company_id,
     0,
     0,
     v_down_draw_amount,
     v_down_real_amount,
     0,
     0,
     sysdate,
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     v_business_type,
     0,
     v_memo);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_DOWN_RV_TRADE_CK
prompt =============================================
prompt
create or replace function fd_account.fd_f_trade_down_rv_trade_ck(v_down_channel_no  varchar2,
                                                       v_down_draw_amount number,
                                                       v_down_real_amount number,
                                                       v_down_account_id  out number,
                                                       v_down_company_id  out number)

 return varchar2 is
begin
  ---1.检查调整金额是否正确
  if (v_down_draw_amount = 0 and v_down_real_amount = 0) then
    return pkg_error_code.amount_error;
  end if;

  ---2.检查渠道账户是否正确
  select t.account_id, c.company_id
    into v_down_account_id, v_down_company_id
    from fd_base_down_account_map t
   inner join fd_base_down_account a on t.account_id = a.account_id
   inner join fd_base_down_channel c on t.channel_no = c.channel_no
   where t.channel_no = v_down_channel_no
     and rownum <= 2;

  return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_or_account_error;
end;
/

prompt
prompt Creating function FD_F_TRADE_DOWN_TRANS_REFUND
prompt ==============================================
prompt
create or replace function fd_account.fd_f_trade_down_trans_refund(v_order_source      in number,
                                                        v_down_channel_no   in varchar2,
                                                        v_trade_order_no    in varchar2,
                                                        v_trade_refund_no   in varchar2,
                                                        v_refund_account_id in number,
                                                        v_refund_unit       in number,
                                                        v_refund_face       in number,
                                                        v_refund_amount     in number,
                                                        v_real_refund       in number,
                                                        v_service_fee       in number,
                                                        v_refund_user       in varchar2,
                                                        v_memo              in varchar2)
  return varchar2 is
  --------------------转账退款-------------------
  l_fd_order_id       number;
  l_refund_count      number;
  l_total_amount      number;
  l_total_refund      number;
  l_account_count     number;
  l_account_balance   number;
  l_down_account_id   number;
  l_inner_balance     number;
  l_down_refund_id    number;
  l_down_draw_id      number;
  l_business_type     number;
  l_carrier_no        varchar2(32);
  l_province_no       varchar2(32);
  l_ext_order_no      varchar2(32);
  l_order_time        date;
  l_down_order_face   number;
  l_down_order_amount number;
  l_debit_count       number;
  l_result            varchar2(32);
  l_total_refund_face number;
  l_cap_company_id    number;
  l_down_company_id   number;
begin
  ---0.检查金额
  if v_service_fee < 0 or v_refund_amount <= 0 then
    return pkg_error_code.amount_error;
  end if;

  ---1.检查转账退款账户
  select count(1), max(t.company_id)
    into l_account_count, l_cap_company_id
    from fd_base_company_account_cap t
   where t.account_type in (pkg_co_cap_account_type.transfer_refund,
          pkg_co_cap_account_type.manual_refund)
     and t.account_id = v_refund_account_id
     and t.status = pkg_sys_boolean.istrue;

  if l_account_count = 0 then
    return pkg_error_code.co_account_not_exists;
  end if;

  ---2.锁下游订单
  select t.fd_order_id,
         t.down_account_id,
         t.business_type,
         t.carrier_no,
         t.province_no,
         t.ext_order_no,
         t.order_time,
         t.total_face,
         t.down_order_amount,
         t.down_company_id
    into l_fd_order_id,
         l_down_account_id,
         l_business_type,
         l_carrier_no,
         l_province_no,
         l_ext_order_no,
         l_order_time,
         l_down_order_face,
         l_down_order_amount,
         l_down_company_id
    from fd_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1
     for update;

  ---2.1检查公司是否相同
  if l_cap_company_id != l_down_company_id then
    return pkg_error_code.channel_or_account_error;
  end if;

  ---2.检测订单重复退款
  select count(1)
    into l_refund_count
    from fd_trade_down_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.fd_order_id = l_fd_order_id
     and t.order_source = v_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = v_order_source
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---4.获取订单扣款、已退款金额
  select nvl(sum(t.change_amount), 0)
    into l_total_amount
    from fd_trade_down_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_down_fund_change_type.debit_amount
     and t.order_source = v_order_source
     and rownum <= 1;

  select nvl(sum(t.refund_amount), 0), nvl(sum(t.refund_face), 0)
    into l_total_refund, l_total_refund_face
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  ---5.检查退款金额是否超过订单总金额(用户在下游支付的金额比系统的高所以退款减去5元进行判断)
  /*  if (v_refund_amount-5 > (l_total_amount - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;*/

  if v_refund_face > (l_down_order_face - l_total_refund_face) then
    return pkg_error_code.amount_error;
  end if;

  ---6.锁转账退款账户
  select t.balance
    into l_account_balance
    from fd_base_company_account_cap t
   where t.account_type in (pkg_co_cap_account_type.transfer_refund,
          pkg_co_cap_account_type.manual_refund)
     and t.account_id = v_refund_account_id
     and t.status = pkg_sys_boolean.istrue
     for update;

  ---7.修改退款账户余额
  update fd_base_company_account_cap t
     set t.balance        = (l_account_balance - v_refund_amount -
                            v_service_fee),
         t.last_edit_user = v_refund_user,
         t.last_edit_time = sysdate
   where t.account_id = v_refund_account_id;

  select seq_trade_down_fund_id.nextval into l_down_refund_id from dual;
  select seq_trade_down_fund_id.nextval into l_down_draw_id from dual;

  ---8.添加转账退款资金变动
  insert into fd_trade_co_cap_fund
    (change_id,
     account_id,
     change_amount,
     change_type,
     balance,
     change_time,
     adjust_id,
     has_adjust,
     bank_batch_id,
     memo,
     record_no,
     create_user,
     fd_order_id,
     transfer_refund_id,
     transfer_draw_id)
  values
    (seq_trade_co_cap_fund_id.nextval,
     v_refund_account_id,
     v_refund_amount,
     pkg_co_cap_fund_type.minus_amount,
     (l_account_balance - v_refund_amount),
     sysdate,
     0,
     pkg_sys_boolean.isfalse,
     0,
     v_memo,
     v_trade_refund_no,
     v_refund_user,
     l_fd_order_id,
     l_down_refund_id,
     l_down_draw_id);

  ---8.1添加手续费记录
  insert into fd_trade_co_cap_fund
    (change_id,
     account_id,
     change_amount,
     change_type,
     balance,
     change_time,
     adjust_id,
     has_adjust,
     bank_batch_id,
     memo,
     record_no,
     create_user,
     fd_order_id,
     transfer_refund_id,
     transfer_draw_id)
  values
    (seq_trade_co_cap_fund_id.nextval,
     v_refund_account_id,
     v_service_fee,
     pkg_co_cap_fund_type.service_fee,
     (l_account_balance - v_refund_amount - v_service_fee),
     sysdate,
     0,
     pkg_sys_boolean.isfalse,
     0,
     v_memo,
     v_trade_refund_no,
     v_refund_user,
     0,
     0,
     0);

  ---9.获取下游渠道余额
  select t.inner_balance
    into l_inner_balance
    from fd_base_down_account t
   where t.account_id = l_down_account_id
     for update;

  ---10.添加下游退款记录
  insert into fd_trade_down_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_refund_no,
     order_source,
     refund_time,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  values
    (l_down_refund_id,
     v_down_channel_no,
     l_down_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_refund_no,
     v_order_source,
     sysdate,
     l_business_type,
     l_carrier_no,
     l_province_no,
     v_refund_unit,
     v_refund_face,
     v_refund_amount,
     v_real_refund,
     v_memo);

  ---11.添加下游退款资金变动
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (l_down_refund_id,
     l_fd_order_id,
     v_down_channel_no,
     l_down_account_id,
     v_trade_order_no,
     l_ext_order_no,
     v_order_source,
     sysdate,
     l_order_time,
     pkg_down_fund_change_type.refund_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount),
     v_memo,
     0);
  ---12.添加下游提款记录
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     create_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee)
  values
    (l_down_draw_id,
     l_fd_order_id,
     v_down_channel_no,
     l_down_account_id,
     sysdate,
     pkg_down_fund_change_type.draw_amount,
     v_refund_amount,
     (l_inner_balance + v_refund_amount - v_refund_amount),
     v_memo,
     0);
  --===========================订单差异信息记录=================================
  l_result := fd_f_trade_fund_differ_add(l_fd_order_id,
                                         pkg_trade_type.transfer_refund,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         pkg_bill_type.front,
                                         l_down_order_face,
                                         l_down_order_amount,
                                         v_refund_face,
                                         v_refund_amount,
                                         0);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_HS_MOVE
prompt ====================================
prompt
create or replace function fd_account.fd_f_trade_hs_move(v_order_source    number,
                                              v_down_channel_no varchar2,
                                              v_trade_order_no  varchar2)
  return varchar2 is
  -------------------历史库中的数据转移到当前库--------------------
  l_order_count         number;
  l_order_hs_count      number;
  l_fd_order_id         number;
  l_order_add_count     number;
  l_down_fund_add_count number;
  l_up_fund_add_count   number;
  l_order_del_count     number;
  l_down_fund_del_count number;
  l_up_fund_del_count   number;
begin
  ---1.检查当前库订单是否存在
  select count(1)
    into l_order_count
    from fd_trade_order_main t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1;

  if l_order_count > 0 then
    return pkg_error_code.success;
  end if;

  ---2.当前库订单不存在，检查历史表订单是否存在
  select count(1), max(t.fd_order_id)
    into l_order_hs_count, l_fd_order_id
    from fd_trade_order_main_hs t
   where t.order_source = v_order_source
     and t.down_channel_no = v_down_channel_no
     and t.trade_order_no = v_trade_order_no
     and rownum <= 1;

  ---2.1.历史表数据不存在，获取离线库数据
  if l_order_hs_count = 0 then
    return fd_f_trade_back_move(v_order_source,
                                v_down_channel_no,
                                v_trade_order_no);
  end if;

  ---3.历史表订单存在，获取历史库订单及其变动数据存入当前库
  ---3.1订单主表数据
  insert into fd_trade_order_main
    (fd_order_id,
     down_channel_no,
     down_account_id,
     down_company_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     business_type,
     carrier_no,
     province_no,
     city_no,
     total_face,
     recharge_unit,
     recharge_account_no)
    select fd_order_id,
           down_channel_no,
           down_account_id,
           down_company_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           business_type,
           carrier_no,
           province_no,
           city_no,
           total_face,
           recharge_unit,
           recharge_account_no
      from fd_trade_order_main_hs t
     where t.fd_order_id = l_fd_order_id;
  l_order_add_count := sql%rowcount;

  if l_order_add_count = 0 then
    return pkg_error_code.data_error;
  end if;

  ---3.2删除历史库订单主表数据
  delete from fd_trade_order_main_hs t where t.fd_order_id = l_fd_order_id;
  l_order_del_count := sql%rowcount;

  if l_order_del_count != l_order_add_count then
    return pkg_error_code.data_error;
  end if;

  ---3.3下游资金变动数据
  insert into fd_trade_down_fund
    (record_id,
     fd_order_id,
     channel_no,
     account_id,
     trade_order_no,
     ext_order_no,
     order_source,
     create_time,
     order_time,
     change_type,
     change_amount,
     balance,
     memo,
     service_fee) ----添加手续费
    select record_id,
           fd_order_id,
           channel_no,
           account_id,
           trade_order_no,
           ext_order_no,
           order_source,
           create_time,
           order_time,
           change_type,
           change_amount,
           balance,
           memo,
           service_fee
      from fd_trade_down_fund_hs t
     where t.fd_order_id = l_fd_order_id;
  l_down_fund_add_count := sql%rowcount;

  ---3.4删除历史库下游资金变动数据
  delete from fd_trade_down_fund_hs t where t.fd_order_id = l_fd_order_id;
  l_down_fund_del_count := sql%rowcount;

  if l_down_fund_del_count != l_down_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  ---3.5上游资金变动数据
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_batch_id,
     settle_status,
     settle_time,
     memo,
     order_time)
    select record_id,
           fd_order_id,
           trade_order_no,
           order_source,
           trade_delivery_no,
           down_channel_no,
           down_account_id,
           down_company_id,
           up_channel_no,
           up_account_id,
           up_company_id,
           down_draw_unit,
           down_draw_face,
           down_draw_amount,
           down_real_amount,
           up_draw_unit,
           up_draw_face,
           up_draw_amount,
           up_real_amount,
           change_time,
           change_type,
           bill_type,
           business_type,
           carrier_no,
           province_no,
           balance,
           profits,
           down_commission,
           up_commission,
           settle_amount,
           settle_batch_id,
           settle_status,
           settle_time,
           memo,
           order_time
      from fd_trade_up_fund_hs t
     where t.fd_order_id = l_fd_order_id;
  l_up_fund_add_count := sql%rowcount;

  ---3.6删除历史库上游资金变动数据
  delete from fd_trade_up_fund_hs t where t.fd_order_id = l_fd_order_id;
  l_up_fund_del_count := sql%rowcount;

  if l_up_fund_del_count != l_up_fund_add_count then
    return pkg_error_code.data_error;
  end if;

  return pkg_error_code.success;
end;
/

