prompt
prompt Creating function FD_F_TRADE_UP_ORDER_MINUS
prompt ===========================================
prompt
create or replace function fd_account.fd_f_trade_up_order_minus(v_order_source      number,
                                                     v_trade_order_no    varchar2,
                                                     v_trade_delivery_no varchar2,
                                                     v_up_channel_no     varchar2,
                                                     v_up_account_id     number,
                                                     v_up_draw_unit      number,
                                                     v_up_draw_face      number,
                                                     v_up_draw_amount    number,
                                                     v_up_real_amount    number,
                                                     v_down_draw_unit    number,
                                                     v_down_draw_face    number,
                                                     v_down_draw_amount  number,
                                                     v_down_real_amount  number,
                                                     v_bill_type         number,
                                                     v_order_time        varchar2,
                                                     v_up_service_fee    number,
                                                     v_memo              varchar2)
  return varchar2 is
  l_down_order_all_unit number;
  l_down_order_all_face number;
  l_down_debit_now_unit number;
  l_down_debit_now_face number;
  l_down_draw_unit      number;
  l_down_draw_face      number;
  l_down_draw_amount    number;
  l_down_real_amount    number;

  l_up_company_id   number;
  l_down_channel_no varchar2(32);
  l_down_company_id number;
  l_inner_balance   number;
  l_query_balance   number;
  l_fd_order_id     number;
  l_pay_count       number;
  l_settle_status   number;
  l_down_account_id number;
  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);
  l_city_no         varchar2(32);

  l_profits          number;
  l_down_commission  number;
  l_up_commission    number;
  l_refund_face      number;
  l_refund_unit      number;
  l_order_all_amount number;
  l_order_all_unit   number;
  l_order_all_face   number;
  l_result           varchar2(32);
  -------------------上游减款----------------------
begin

  --===============================检查订单支付情况==================================
  ---1.锁支付订单
  select t.fd_order_id,
         t.down_channel_no,
         t.down_account_id,
         t.down_company_id,
         t.recharge_unit,
         t.total_face,
         t.business_type,
         t.carrier_no,
         t.province_no,
         t.city_no,
         t.down_order_amount
    into l_fd_order_id,
         l_down_channel_no,
         l_down_account_id,
         l_down_company_id,
         l_order_all_unit,
         l_order_all_face,
         l_business_type,
         l_carrier_no,
         l_province_no,
         l_city_no,
         l_order_all_amount
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查订单是否已支付
  select count(1)
    into l_pay_count
    from fd_trade_up_fund t
   where t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if (l_pay_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.计算下游规格、订单金额
  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0)
    into l_down_debit_now_unit, l_down_debit_now_face
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);

  ---4.计算出下游退款金额
  select nvl(sum(t.refund_face), 0), nvl(sum(t.refund_unit), 0)
    into l_refund_face, l_refund_unit
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  ---5.计算扣款金额
  l_down_order_all_face := l_order_all_face - l_refund_face; ---订单实际总面值
  l_down_order_all_unit := l_order_all_unit - l_refund_unit; --订单实际总规格
  if ((l_down_order_all_face) <= l_down_debit_now_face) then
    l_down_draw_unit   := 0;
    l_down_draw_face   := 0;
    l_down_draw_amount := 0;
    l_down_real_amount := 0;
  elsif ((l_down_order_all_face - l_down_debit_now_face) < v_up_draw_face) then
    l_down_draw_unit   := l_down_order_all_unit - l_down_debit_now_unit;
    l_down_draw_face   := l_down_order_all_face - l_down_debit_now_face;
    l_down_draw_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_draw_amount;
    l_down_real_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_real_amount;
  else
    l_down_draw_unit   := v_up_draw_unit;
    l_down_draw_face   := v_up_draw_face;
    l_down_draw_amount := v_down_draw_amount *
                          (v_up_draw_face / v_down_draw_face);
    l_down_real_amount := v_down_real_amount *
                          (v_up_draw_face / v_down_draw_face);
  end if;

  --===================================获取公司结算信息=================================

  ---4. 是否需要结算给公司
  select t.company_id
    into l_up_company_id
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no;

  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --======================================上游减款=====================================

  ---5.1获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = v_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance = (l_inner_balance - v_up_draw_amount)
   where t.account_id = v_up_account_id;

  l_profits         := l_down_real_amount - v_up_real_amount;
  l_down_commission := l_down_draw_amount - l_down_real_amount;
  l_up_commission   := v_up_draw_amount - v_up_real_amount;

  ---6.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo)
  values
    (seq_trade_up_fund_id.nextval,
     l_fd_order_id,
     v_trade_order_no,
     v_order_source,
     v_trade_delivery_no,
     l_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     v_up_account_id,
     l_up_company_id,
     l_down_draw_unit,
     l_down_draw_face,
     l_down_draw_amount,
     l_down_real_amount,
     v_up_draw_unit,
     v_up_draw_face,
     v_up_draw_amount,
     v_up_real_amount,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     l_business_type,
     l_carrier_no,
     l_province_no,
     (l_inner_balance - v_up_draw_amount),
     l_profits,
     l_down_commission,
     l_up_commission,
     0,
     l_settle_status,
     v_memo);

  ---7.添加订单差异表数据
  l_result := fd_f_trade_fund_differ_add(l_fd_order_id,
                                         pkg_trade_type.up_debit,
                                         l_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         v_bill_type,
                                         l_order_all_face,
                                         l_order_all_amount,
                                         l_down_draw_face,
                                         l_down_draw_amount,
                                         v_up_draw_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_MINUS2
prompt ============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_minus2(v_order_source      number,
                                                      v_trade_order_no    varchar2,
                                                      v_trade_delivery_no varchar2,
                                                      v_up_channel_no     varchar2,
                                                      v_up_account_id     number,
                                                      v_up_draw_unit      number,
                                                      v_up_draw_face      number,
                                                      v_up_draw_amount    number,
                                                      v_up_real_amount    number,
                                                      v_down_draw_unit    number,
                                                      v_down_draw_face    number,
                                                      v_down_draw_amount  number,
                                                      v_down_real_amount  number,
                                                      v_bill_type         number,
                                                      v_order_time        varchar2,
                                                      v_up_service_fee    number,
                                                      v_memo              varchar2,
                                                      v_out_change_time   varchar2)
  return varchar2 is
  l_down_order_all_unit number;
  l_down_order_all_face number;
  l_down_debit_now_unit number;
  l_down_debit_now_face number;
  l_down_draw_unit      number;
  l_down_draw_face      number;
  l_down_draw_amount    number;
  l_down_real_amount    number;

  l_up_company_id   number;
  l_down_channel_no varchar2(32);
  l_down_company_id number;
  l_inner_balance   number;
  l_query_balance   number;
  l_fd_order_id     number;
  l_pay_count       number;
  l_settle_status   number;
  l_down_account_id number;
  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);
  l_city_no         varchar2(32);

  l_profits          number;
  l_down_commission  number;
  l_up_commission    number;
  l_refund_face      number;
  l_refund_unit      number;
  l_order_all_amount number;
  l_order_all_unit   number;
  l_order_all_face   number;
  l_result           varchar2(32);
  -------------------上游减款----------------------
begin

  --===============================检查订单支付情况==================================
  ---1.锁支付订单
  select t.fd_order_id,
         t.down_channel_no,
         t.down_account_id,
         t.down_company_id,
         t.recharge_unit,
         t.total_face,
         t.business_type,
         t.carrier_no,
         t.province_no,
         t.city_no,
         t.down_order_amount
    into l_fd_order_id,
         l_down_channel_no,
         l_down_account_id,
         l_down_company_id,
         l_order_all_unit,
         l_order_all_face,
         l_business_type,
         l_carrier_no,
         l_province_no,
         l_city_no,
         l_order_all_amount
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查订单是否已支付
  select count(1)
    into l_pay_count
    from fd_trade_up_fund t
   where t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if (l_pay_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.计算下游规格、订单金额
  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0)
    into l_down_debit_now_unit, l_down_debit_now_face
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in
         (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);

  ---4.计算出下游退款金额
  select nvl(sum(t.refund_face), 0), nvl(sum(t.refund_unit), 0)
    into l_refund_face, l_refund_unit
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  ---5.计算扣款金额
  l_down_order_all_face := l_order_all_face - l_refund_face; ---订单实际总面值
  l_down_order_all_unit := l_order_all_unit - l_refund_unit; --订单实际总规格
  if ((l_down_order_all_face) <= l_down_debit_now_face) then
    l_down_draw_unit   := 0;
    l_down_draw_face   := 0;
    l_down_draw_amount := 0;
    l_down_real_amount := 0;
  elsif ((l_down_order_all_face - l_down_debit_now_face) < v_up_draw_face) then
    l_down_draw_unit   := l_down_order_all_unit - l_down_debit_now_unit;
    l_down_draw_face   := l_down_order_all_face - l_down_debit_now_face;
    l_down_draw_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_draw_amount;
    l_down_real_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_real_amount;
  else
    l_down_draw_unit   := v_up_draw_unit;
    l_down_draw_face   := v_up_draw_face;
    l_down_draw_amount := v_down_draw_amount *
                          (v_up_draw_face / v_down_draw_face);
    l_down_real_amount := v_down_real_amount *
                          (v_up_draw_face / v_down_draw_face);
  end if;

  --===================================获取公司结算信息=================================

  ---4. 是否需要结算给公司
  select t.company_id
    into l_up_company_id
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no;

  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --======================================上游减款=====================================

  ---5.1获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = v_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance =
         (l_inner_balance - v_up_draw_amount)
   where t.account_id = v_up_account_id;

  l_profits         := l_down_real_amount - v_up_real_amount;
  l_down_commission := l_down_draw_amount - l_down_real_amount;
  l_up_commission   := v_up_draw_amount - v_up_real_amount;

  ---6.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo,
     out_change_time)
  values
    (seq_trade_up_fund_id.nextval,
     l_fd_order_id,
     v_trade_order_no,
     v_order_source,
     v_trade_delivery_no,
     l_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     v_up_account_id,
     l_up_company_id,
     l_down_draw_unit,
     l_down_draw_face,
     l_down_draw_amount,
     l_down_real_amount,
     v_up_draw_unit,
     v_up_draw_face,
     v_up_draw_amount,
     v_up_real_amount,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     l_business_type,
     l_carrier_no,
     l_province_no,
     (l_inner_balance - v_up_draw_amount),
     l_profits,
     l_down_commission,
     l_up_commission,
     0,
     l_settle_status,
     v_memo,
     to_date(v_out_change_time, 'yyyy-mm-dd hh24:mi:ss'));

  ---7.添加订单差异表数据
  l_result := fd_f_trade_fund_differ_add(l_fd_order_id,
                                         pkg_trade_type.up_debit,
                                         l_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         v_bill_type,
                                         l_order_all_face,
                                         l_order_all_amount,
                                         l_down_draw_face,
                                         l_down_draw_amount,
                                         v_up_draw_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_MINUS_MS
prompt ==============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_minus_ms(v_order_source      number,
                                                        v_trade_order_no    varchar2,
                                                        v_trade_delivery_no varchar2,
                                                        v_up_channel_no     varchar2,
                                                        v_up_account_id     number,
                                                        v_up_draw_unit      number,
                                                        v_up_draw_face      number,
                                                        v_up_draw_amount    number,
                                                        v_up_real_amount    number,
                                                        v_down_draw_unit    number,
                                                        v_down_draw_face    number,
                                                        v_down_draw_amount  number,
                                                        v_down_real_amount  number,
                                                        v_bill_type         number,
                                                        v_order_time        varchar2,
                                                        v_up_service_fee    number,
                                                        v_memo              varchar2,
                                                        v_out_change_time   varchar2)
  return varchar2 is
  l_down_order_all_unit   number;
  l_down_order_all_face   number;
  l_down_debit_now_unit   number;
  l_down_debit_now_face   number;
  l_down_debit_now_amount number;
  l_down_draw_unit        number;
  l_down_draw_face        number;
  l_down_draw_amount      number;
  l_down_real_amount      number;

  l_up_company_id   number;
  l_down_channel_no varchar2(32);
  l_down_company_id number;
  l_inner_balance   number;
  l_query_balance   number;
  l_fd_order_id     number;
  l_pay_count       number;
  l_settle_status   number;
  l_down_account_id number;
  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);
  l_city_no         varchar2(32);

  l_profits          number;
  l_down_commission  number;
  l_up_commission    number;
  l_refund_face      number;
  l_refund_unit      number;
  l_order_all_amount number;
  l_order_all_unit   number;
  l_order_all_face   number;
  l_result           varchar2(32);
  -----------------------------------------
  --功能：短信平台上游扣款专用
  --创建人：周荣省
  --创建时间：2019-05-20
  -----------------------------------------
begin

  --===============================检查订单支付情况==================================
  ---1.锁支付订单
  select t.fd_order_id,
         t.down_channel_no,
         t.down_account_id,
         t.down_company_id,
         t.recharge_unit,
         t.total_face,
         t.business_type,
         t.carrier_no,
         t.province_no,
         t.city_no,
         t.down_order_amount
    into l_fd_order_id,
         l_down_channel_no,
         l_down_account_id,
         l_down_company_id,
         l_order_all_unit,
         l_order_all_face,
         l_business_type,
         l_carrier_no,
         l_province_no,
         l_city_no,
         l_order_all_amount
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查订单是否已支付
  select count(1)
    into l_pay_count
    from fd_trade_up_fund t
   where t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if (l_pay_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.计算下游规格、订单金额
  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_amount),
             0)
    into l_down_debit_now_unit,
         l_down_debit_now_face,
         l_down_debit_now_amount
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);
  --4.计算下游扣款金额
  if l_down_debit_now_amount = 0 then
    --下游无扣款
    l_down_draw_unit   := v_down_draw_unit;
    l_down_draw_face   := v_down_draw_face;
    l_down_draw_amount := v_down_draw_amount;
    l_down_real_amount := v_down_real_amount;
  else
    l_down_draw_unit   := 0;
    l_down_draw_face   := 0;
    l_down_draw_amount := 0;
    l_down_real_amount := 0;
  end if;
  --===================================获取公司结算信息=================================

  ---4. 是否需要结算给公司
  select t.company_id
    into l_up_company_id
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no;

  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --======================================上游减款=====================================

  ---5.1获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = v_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance = (l_inner_balance - v_up_draw_amount)
   where t.account_id = v_up_account_id;

  l_profits         := l_down_real_amount - v_up_real_amount;
  l_down_commission := l_down_draw_amount - l_down_real_amount;
  l_up_commission   := v_up_draw_amount - v_up_real_amount;

  ---6.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo,
     out_change_time)
  values
    (seq_trade_up_fund_id.nextval,
     l_fd_order_id,
     v_trade_order_no,
     v_order_source,
     v_trade_delivery_no,
     l_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     v_up_account_id,
     l_up_company_id,
     l_down_draw_unit,
     l_down_draw_face,
     l_down_draw_amount,
     l_down_real_amount,
     v_up_draw_unit,
     v_up_draw_face,
     v_up_draw_amount,
     v_up_real_amount,
     sysdate,
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     l_business_type,
     l_carrier_no,
     l_province_no,
     (l_inner_balance - v_up_draw_amount),
     l_profits,
     l_down_commission,
     l_up_commission,
     0,
     l_settle_status,
     v_memo,
     to_date(v_out_change_time, 'yyyy-mm-dd hh24:mi:ss'));

  ---7.添加订单差异表数据
  l_result := fd_f_trade_fund_ms_differ_add(l_fd_order_id,
                                            pkg_trade_type.up_debit,
                                            l_down_channel_no,
                                            l_down_account_id,
                                            l_business_type,
                                            v_bill_type,
                                            l_order_all_face,
                                            l_order_all_amount,
                                            l_down_draw_face,
                                            l_down_draw_amount,
                                            v_up_draw_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_MINUS_T
prompt =============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_minus_t(v_order_source      number,
                                                       v_trade_order_no    varchar2,
                                                       v_trade_delivery_no varchar2,
                                                       v_up_channel_no     varchar2,
                                                       v_up_account_id     number,
                                                       v_up_draw_unit      number,
                                                       v_up_draw_face      number,
                                                       v_up_draw_amount    number,
                                                       v_up_real_amount    number,
                                                       v_down_draw_unit    number,
                                                       v_down_draw_face    number,
                                                       v_down_draw_amount  number,
                                                       v_down_real_amount  number,
                                                       v_bill_type         number,
                                                       v_pay_time          varchar2,
                                                       v_order_time        varchar2,
                                                       v_memo              varchar2)
  return varchar2 is
  l_down_order_all_unit number;
  l_down_order_all_face number;
  l_down_debit_now_unit number;
  l_down_debit_now_face number;
  l_down_draw_unit      number;
  l_down_draw_face      number;
  l_down_draw_amount    number;
  l_down_real_amount    number;

  l_up_company_id   number;
  l_down_channel_no varchar2(32);
  l_down_company_id number;
  l_inner_balance   number;
  l_query_balance   number;
  l_fd_order_id     number;
  l_pay_count       number;
  l_settle_status   number;
  l_down_account_id number;
  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);
  l_city_no         varchar2(32);

  l_profits         number;
  l_down_commission number;
  l_up_commission   number;
  l_has_up_trade    number;
  -------------------上游减款----------------------
begin

  --===============================检查订单支付情况==================================
  ---1.锁支付订单
  select t.fd_order_id,
         t.down_channel_no,
         t.down_account_id,
         t.down_company_id,
         t.recharge_unit,
         t.total_face,
         t.business_type,
         t.carrier_no,
         t.province_no,
         t.city_no,
         t.has_up_trade
    into l_fd_order_id,
         l_down_channel_no,
         l_down_account_id,
         l_down_company_id,
         l_down_order_all_unit,
         l_down_order_all_face,
         l_business_type,
         l_carrier_no,
         l_province_no,
         l_city_no,
         l_has_up_trade
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查订单是否已支付
  select count(1)
    into l_pay_count
    from fd_trade_up_fund t
   where t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if (l_pay_count > 0) then
    return pkg_error_code.success;
  end if;

  ---3.计算下游规格、订单金额
  select sum(t.down_draw_unit), sum(t.down_draw_face)
    into l_down_debit_now_unit, l_down_debit_now_face
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type = pkg_up_fund_change_type.debit_amount;

  if (l_down_order_all_face <= l_down_debit_now_face) then
    l_down_draw_unit   := 0;
    l_down_draw_face   := 0;
    l_down_draw_amount := 0;
    l_down_real_amount := 0;
  elsif ((l_down_order_all_face - l_down_debit_now_face) < v_down_draw_face) then
    l_down_draw_unit   := l_down_order_all_unit - l_down_debit_now_unit;
    l_down_draw_face   := l_down_order_all_face - l_down_debit_now_face;
    l_down_draw_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_draw_amount;
    l_down_real_amount := (l_down_draw_face / v_down_draw_face) *
                          v_down_real_amount;
  else
    l_down_draw_unit   := v_down_draw_unit;
    l_down_draw_face   := v_down_draw_face;
    l_down_draw_amount := v_down_draw_amount;
    l_down_real_amount := v_down_real_amount;
  end if;

  --===================================获取公司结算信息=================================

  ---4. 是否需要结算给公司
  select t.company_id
    into l_up_company_id
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no;

  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --======================================上游减款=====================================
  ---5.0修改订单中的下游支付状态
  if l_has_up_trade = 1 then
    update fd_trade_order_main t
       set t.has_up_trade = 0
     where t.fd_order_id = l_fd_order_id;
  end if;

  ---5.1获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = v_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance = (l_inner_balance - v_up_draw_amount)
   where t.account_id = v_up_account_id;

  l_profits         := l_down_real_amount - v_up_real_amount;
  l_down_commission := l_down_draw_amount - l_down_real_amount;
  l_up_commission   := v_up_draw_amount - v_up_real_amount;

  ---6.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo)
  values
    (seq_trade_up_fund_id.nextval,
     l_fd_order_id,
     v_trade_order_no,
     v_order_source,
     v_trade_delivery_no,
     l_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     v_up_account_id,
     l_up_company_id,
     l_down_draw_unit,
     l_down_draw_face,
     l_down_draw_amount,
     l_down_real_amount,
     v_up_draw_unit,
     v_up_draw_face,
     v_up_draw_amount,
     v_up_real_amount,
     to_date(v_pay_time, 'yyyy-mm-dd hh24:mi:ss'),
     to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     l_business_type,
     l_carrier_no,
     l_province_no,
     (l_inner_balance - v_up_draw_amount),
     l_profits,
     l_down_commission,
     l_up_commission,
     0,
     l_settle_status,
     v_memo);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_REFUND
prompt ============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_refund(v_order_source          number,
                                                      v_down_channel_no       varchar2,
                                                      v_up_channel_no         varchar2,
                                                      v_trade_order_no        varchar2,
                                                      v_trade_delivery_no     varchar2,
                                                      v_trade_refund_no       varchar2,
                                                      v_bill_type             number,
                                                      v_business_type         number,
                                                      v_carrier_no            varchar2,
                                                      v_province_no           varchar2,
                                                      v_up_refund_unit        number,
                                                      v_up_refund_face        number,
                                                      v_up_refund_amount      number,
                                                      v_up_refund_real_amount number,
                                                      v_order_time            varchar2,
                                                      v_service_fee           number,
                                                      v_memo                  varchar2)
  return varchar2 is

  ------------上游订单退款------------
  l_fd_order_id      number;
  l_refund_count     number;
  l_up_company_id    number;
  l_settle_amount    number;
  l_settle_status    number;
  l_query_balance    number;
  l_inner_balance    number;
  l_down_account_id  number;
  l_down_company_id  number;
  l_total_pay        number;
  l_total_refund     number;
  l_up_account_id    number;
  l_up_fund_id       number;
  l_down_refund_face number;
  l_up_refund_face   number;
  l_up_draw_face     number;
  l_order_face       number;
  l_face_differ      number;
  l_up_down_face     number;
  l_up_down_unit     number;
  l_up_down_amount   number;
  l_up_down_real     number;

  l_up_all_down_face   number;
  l_up_all_down_unit   number;
  l_up_all_down_amount number;
  l_up_all_down_real   number;

  l_order_amount  number;
  l_up_all_face   number;
  l_debit_count   number;
  l_business_type number;
  l_result        varchar2(32);
begin
  --=============================检查订单重复退款、金额错误==============================
  ---1.锁下游订单
  select t.fd_order_id,
         t.down_account_id,
         t.down_company_id,
         t.total_face,
         t.down_order_amount,
         t.business_type
    into l_fd_order_id,
         l_down_account_id,
         l_down_company_id,
         l_order_face,
         l_order_amount,
         l_business_type
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查重复退款
  select count(1)
    into l_refund_count
    from fd_trade_up_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = v_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---2.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---3.获取上游支付扣款、已退款金额
  select t.up_draw_amount,
         t.up_account_id,
         t.settle_amount,
         t.up_company_id,
         t.up_draw_face
    into l_total_pay,
         l_up_account_id,
         l_settle_amount,
         l_up_company_id,
         l_up_draw_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  select nvl(sum(t.up_draw_amount), 0), nvl(sum(t.up_draw_face), 0)
    into l_total_refund, l_up_refund_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.refund_amount;

  ---4.检查退款金额是否超过上游支付总金额
  if (v_up_refund_amount > (l_total_pay - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  ---5.计算上游资金变动的下游信息
  select nvl(sum(t.refund_face), 0)
    into l_down_refund_face
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.up_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_amount),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_real_amount),
             0)
    into l_up_all_face,
         l_up_all_down_unit,
         l_up_all_down_face,
         l_up_all_down_amount,
         l_up_all_down_real
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in
         (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);

  l_face_differ := l_up_all_face - (l_order_face - l_down_refund_face);
  if (l_face_differ >= v_up_refund_face) then
    l_up_down_face   := 0;
    l_up_down_unit   := 0;
    l_up_down_amount := 0;
    l_up_down_real   := 0;
  elsif l_face_differ > 0 then
    l_up_down_face   := v_up_refund_face - l_face_differ;
    l_up_down_unit   := l_up_all_down_unit *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
    l_up_down_amount := l_up_all_down_amount *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
    l_up_down_real   := l_up_all_down_real *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
  else
    l_up_down_face   := v_up_refund_face;
    l_up_down_unit   := l_up_all_down_unit *
                        (v_up_refund_face / l_up_all_down_face);
    l_up_down_amount := l_up_all_down_amount *
                        (v_up_refund_face / l_up_all_down_face);
    l_up_down_real   := l_up_all_down_real *
                        (v_up_refund_face / l_up_all_down_face);
  end if;

  --=============================获取退款结算信息=================================
  ---5.获取公司结算状态
  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --==================================上游退款===================================
  ---6.获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = l_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance =
         (l_inner_balance + v_up_refund_amount)
   where t.account_id = l_up_account_id;

  ---7.添加上游退款记录
  select seq_trade_up_fund_id.nextval into l_up_fund_id from dual;

  insert into fd_trade_up_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_delivery_no,
     trade_refund_no,
     order_source,
     refund_time,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  values
    (l_up_fund_id,
     v_up_channel_no,
     l_up_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_delivery_no,
     v_trade_refund_no,
     v_order_source,
     sysdate,
     v_bill_type,
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_up_refund_unit,
     v_up_refund_face,
     v_up_refund_amount,
     v_up_refund_real_amount,
     v_memo);

  ---8.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo)
    select l_up_fund_id,
           l_fd_order_id,
           v_trade_order_no,
           v_order_source,
           v_trade_delivery_no,
           v_down_channel_no,
           l_down_account_id,
           l_down_company_id,
           v_up_channel_no,
           l_up_account_id,
           l_up_company_id,
           l_up_down_unit,
           l_up_down_face,
           l_up_down_amount,
           l_up_down_real,
           v_up_refund_unit,
           v_up_refund_face,
           v_up_refund_amount,
           v_up_refund_real_amount,
           sysdate,
           to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
           pkg_up_fund_change_type.refund_amount,
           v_bill_type,
           v_business_type,
           v_carrier_no,
           v_province_no,
           (l_inner_balance + v_up_refund_amount),
           t.profits,
           t.down_commission,
           (v_up_refund_amount - v_up_refund_real_amount),
           l_settle_amount,
           l_settle_status,
           v_memo
      from fd_trade_up_fund t
     where t.fd_order_id = l_fd_order_id
       and t.trade_delivery_no = v_trade_delivery_no
       and t.change_type = pkg_up_fund_change_type.debit_amount
       and rownum <= 1;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---9.记录差异信息
  l_result := fd_f_trade_fund_differ_add(l_fd_order_id,
                                         pkg_trade_type.up_refund,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         v_bill_type,
                                         l_order_face,
                                         l_order_amount,
                                         l_up_down_face,
                                         l_up_down_amount,
                                         v_up_refund_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_REFUND2
prompt =============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_refund2(v_order_source          number,
                                                       v_down_channel_no       varchar2,
                                                       v_up_channel_no         varchar2,
                                                       v_trade_order_no        varchar2,
                                                       v_trade_delivery_no     varchar2,
                                                       v_trade_refund_no       varchar2,
                                                       v_bill_type             number,
                                                       v_business_type         number,
                                                       v_carrier_no            varchar2,
                                                       v_province_no           varchar2,
                                                       v_up_refund_unit        number,
                                                       v_up_refund_face        number,
                                                       v_up_refund_amount      number,
                                                       v_up_refund_real_amount number,
                                                       v_order_time            varchar2,
                                                       v_service_fee           number,
                                                       v_memo                  varchar2,
                                                       v_out_change_time       varchar2)
  return varchar2 is

  ------------上游订单退款------------
  l_fd_order_id      number;
  l_refund_count     number;
  l_up_company_id    number;
  l_settle_amount    number;
  l_settle_status    number;
  l_query_balance    number;
  l_inner_balance    number;
  l_down_account_id  number;
  l_down_company_id  number;
  l_total_pay        number;
  l_total_refund     number;
  l_up_account_id    number;
  l_up_fund_id       number;
  l_down_refund_face number;
  l_up_refund_face   number;
  l_up_draw_face     number;
  l_order_face       number;
  l_face_differ      number;
  l_up_down_face     number;
  l_up_down_unit     number;
  l_up_down_amount   number;
  l_up_down_real     number;

  l_up_all_down_face   number;
  l_up_all_down_unit   number;
  l_up_all_down_amount number;
  l_up_all_down_real   number;

  l_order_amount  number;
  l_up_all_face   number;
  l_debit_count   number;
  l_business_type number;
  l_result        varchar2(32);
begin
  --=============================检查订单重复退款、金额错误==============================
  ---1.锁下游订单
  select t.fd_order_id,
         t.down_account_id,
         t.down_company_id,
         t.total_face,
         t.down_order_amount,
         t.business_type
    into l_fd_order_id,
         l_down_account_id,
         l_down_company_id,
         l_order_face,
         l_order_amount,
         l_business_type
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查重复退款
  select count(1)
    into l_refund_count
    from fd_trade_up_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = v_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---2.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---3.获取上游支付扣款、已退款金额
  select t.up_draw_amount,
         t.up_account_id,
         t.settle_amount,
         t.up_company_id,
         t.up_draw_face
    into l_total_pay,
         l_up_account_id,
         l_settle_amount,
         l_up_company_id,
         l_up_draw_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  select nvl(sum(t.up_draw_amount), 0), nvl(sum(t.up_draw_face), 0)
    into l_total_refund, l_up_refund_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.refund_amount;

  ---4.检查退款金额是否超过上游支付总金额
  if (v_up_refund_amount > (l_total_pay - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  ---5.计算上游资金变动的下游信息
  select nvl(sum(t.refund_face), 0)
    into l_down_refund_face
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.up_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_amount),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_real_amount),
             0)
    into l_up_all_face,
         l_up_all_down_unit,
         l_up_all_down_face,
         l_up_all_down_amount,
         l_up_all_down_real
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in
         (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);

  l_face_differ := l_up_all_face - (l_order_face - l_down_refund_face);
  if (l_face_differ >= v_up_refund_face) then
    l_up_down_face   := 0;
    l_up_down_unit   := 0;
    l_up_down_amount := 0;
    l_up_down_real   := 0;
  elsif l_face_differ > 0 then
    l_up_down_face   := v_up_refund_face - l_face_differ;
    l_up_down_unit   := l_up_all_down_unit *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
    l_up_down_amount := l_up_all_down_amount *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
    l_up_down_real   := l_up_all_down_real *
                        (v_up_refund_face / l_up_all_down_face) *
                        (1 - (l_face_differ / v_up_refund_face));
  else
    l_up_down_face   := v_up_refund_face;
    l_up_down_unit   := l_up_all_down_unit *
                        (v_up_refund_face / l_up_all_down_face);
    l_up_down_amount := l_up_all_down_amount *
                        (v_up_refund_face / l_up_all_down_face);
    l_up_down_real   := l_up_all_down_real *
                        (v_up_refund_face / l_up_all_down_face);
  end if;

  --=============================获取退款结算信息=================================
  ---5.获取公司结算状态
  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --==================================上游退款===================================
  ---6.获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = l_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance =
         (l_inner_balance + v_up_refund_amount)
   where t.account_id = l_up_account_id;

  ---7.添加上游退款记录
  select seq_trade_up_fund_id.nextval into l_up_fund_id from dual;

  insert into fd_trade_up_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_delivery_no,
     trade_refund_no,
     order_source,
     refund_time,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  values
    (l_up_fund_id,
     v_up_channel_no,
     l_up_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_delivery_no,
     v_trade_refund_no,
     v_order_source,
     sysdate,
     v_bill_type,
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_up_refund_unit,
     v_up_refund_face,
     v_up_refund_amount,
     v_up_refund_real_amount,
     v_memo);

  ---8.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo,
     out_change_time)
    select l_up_fund_id,
           l_fd_order_id,
           v_trade_order_no,
           v_order_source,
           v_trade_delivery_no,
           v_down_channel_no,
           l_down_account_id,
           l_down_company_id,
           v_up_channel_no,
           l_up_account_id,
           l_up_company_id,
           l_up_down_unit,
           l_up_down_face,
           l_up_down_amount,
           l_up_down_real,
           v_up_refund_unit,
           v_up_refund_face,
           v_up_refund_amount,
           v_up_refund_real_amount,
           sysdate,
           to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
           pkg_up_fund_change_type.refund_amount,
           v_bill_type,
           v_business_type,
           v_carrier_no,
           v_province_no,
           (l_inner_balance + v_up_refund_amount),
           t.profits,
           t.down_commission,
           (v_up_refund_amount - v_up_refund_real_amount),
           l_settle_amount,
           l_settle_status,
           v_memo,
           to_date(v_out_change_time, 'yyyy-mm-dd hh24:mi:ss')
      from fd_trade_up_fund t
     where t.fd_order_id = l_fd_order_id
       and t.trade_delivery_no = v_trade_delivery_no
       and t.change_type = pkg_up_fund_change_type.debit_amount
       and rownum <= 1;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---9.记录差异信息
  l_result := fd_f_trade_fund_differ_add(l_fd_order_id,
                                         pkg_trade_type.up_refund,
                                         v_down_channel_no,
                                         l_down_account_id,
                                         l_business_type,
                                         v_bill_type,
                                         l_order_face,
                                         l_order_amount,
                                         l_up_down_face,
                                         l_up_down_amount,
                                         v_up_refund_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_REFUND_MS
prompt ===============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_refund_ms(v_order_source          number,
                                                         v_down_channel_no       varchar2,
                                                         v_up_channel_no         varchar2,
                                                         v_trade_order_no        varchar2,
                                                         v_trade_delivery_no     varchar2,
                                                         v_trade_refund_no       varchar2,
                                                         v_bill_type             number,
                                                         v_business_type         number,
                                                         v_carrier_no            varchar2,
                                                         v_province_no           varchar2,
                                                         v_up_refund_unit        number,
                                                         v_up_refund_face        number,
                                                         v_up_refund_amount      number,
                                                         v_up_refund_real_amount number,
                                                         v_order_time            varchar2,
                                                         v_service_fee           number,
                                                         v_memo                  varchar2,
                                                         v_out_change_time       varchar2)
  return varchar2 is

  ----------------------------
  --功能：短信平台上游订单退款专用
  --创建人：周荣省
  --创建时间：2019-05-20
  ----------------------------
  l_fd_order_id        number;
  l_refund_count       number;
  l_up_company_id      number;
  l_settle_amount      number;
  l_settle_status      number;
  l_query_balance      number;
  l_inner_balance      number;
  l_down_account_id    number;
  l_down_company_id    number;
  l_total_pay          number;
  l_total_refund       number;
  l_up_account_id      number;
  l_up_fund_id         number;
  l_down_refund_face   number;
  l_down_refund_amount number;
  l_up_refund_face     number;
  l_up_draw_face       number;
  l_order_face         number;
  l_face_differ        number;
  l_up_down_face       number;
  l_up_down_unit       number;
  l_up_down_amount     number;
  l_up_down_real       number;

  l_up_all_down_face   number;
  l_up_all_down_unit   number;
  l_up_all_down_amount number;
  l_up_all_down_real   number;

  l_order_amount  number;
  l_up_all_face   number;
  l_debit_count   number;
  l_business_type number;
  l_result        varchar2(32);
begin
  --=============================检查订单重复退款、金额错误==============================
  ---1.锁下游订单
  select t.fd_order_id,
         t.down_account_id,
         t.down_company_id,
         t.total_face,
         t.down_order_amount,
         t.business_type
    into l_fd_order_id,
         l_down_account_id,
         l_down_company_id,
         l_order_face,
         l_order_amount,
         l_business_type
    from fd_trade_order_main t
   where t.trade_order_no = v_trade_order_no
     and t.order_source = v_order_source
     and rownum <= 1
     for update;

  ---2.检查重复退款
  select count(1)
    into l_refund_count
    from fd_trade_up_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = v_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  ---2.1检查退款时是否有扣款
  select count(1)
    into l_debit_count
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  if l_debit_count = 0 then
    return pkg_error_code.debit_not_exists;
  end if;

  ---3.获取上游支付扣款、已退款金额
  select t.up_draw_amount,
         t.up_account_id,
         t.settle_amount,
         t.up_company_id,
         t.up_draw_face
    into l_total_pay,
         l_up_account_id,
         l_settle_amount,
         l_up_company_id,
         l_up_draw_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.debit_amount
     and rownum <= 1;

  select nvl(sum(t.up_draw_amount), 0), nvl(sum(t.up_draw_face), 0)
    into l_total_refund, l_up_refund_face
    from fd_trade_up_fund t
   where t.trade_order_no = v_trade_order_no
     and t.trade_delivery_no = v_trade_delivery_no
     and t.order_source = v_order_source
     and t.change_type = pkg_up_fund_change_type.refund_amount;

  ---4.检查退款金额是否超过上游支付总金额
  if (v_up_refund_amount > (l_total_pay - l_total_refund)) then
    return pkg_error_code.amount_error;
  end if;

  ---5.计算上游资金变动的下游信息
  select nvl(sum(t.refund_face), 0), nvl(sum(t.refund_amount), 0)
    into l_down_refund_face, l_down_refund_amount
    from fd_trade_down_refund t
   where t.fd_order_id = l_fd_order_id;

  select nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.up_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_unit),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_face),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_draw_amount),
             0),
         nvl(sum(decode(t.change_type,
                        pkg_up_fund_change_type.debit_amount,
                        1,
                        -1) * t.down_real_amount),
             0)
    into l_up_all_face,
         l_up_all_down_unit,
         l_up_all_down_face,
         l_up_all_down_amount,
         l_up_all_down_real
    from fd_trade_up_fund t
   where t.fd_order_id = l_fd_order_id
     and t.change_type in (pkg_up_fund_change_type.debit_amount,
          pkg_up_fund_change_type.refund_amount);

  if l_down_refund_amount > 0 then
    --下游已经退款
    l_up_down_face   := 0;
    l_up_down_unit   := 0;
    l_up_down_amount := 0;
    l_up_down_real   := 0;
  else
    l_up_down_face   := l_up_all_down_face;
    l_up_down_unit   := l_up_all_down_unit;
    l_up_down_amount := l_up_all_down_amount;
    l_up_down_real   := l_up_all_down_real;
  end if;

  --=============================获取退款结算信息=================================
  ---5.获取公司结算状态
  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --==================================上游退款===================================
  ---6.获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = l_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance = (l_inner_balance + v_up_refund_amount)
   where t.account_id = l_up_account_id;

  ---7.添加上游退款记录
  select seq_trade_up_fund_id.nextval into l_up_fund_id from dual;

  insert into fd_trade_up_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_delivery_no,
     trade_refund_no,
     order_source,
     refund_time,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  values
    (l_up_fund_id,
     v_up_channel_no,
     l_up_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_delivery_no,
     v_trade_refund_no,
     v_order_source,
     sysdate,
     v_bill_type,
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_up_refund_unit,
     v_up_refund_face,
     v_up_refund_amount,
     v_up_refund_real_amount,
     v_memo);

  ---8.添加上游资金变动
  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     order_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo,
     out_change_time)
    select l_up_fund_id,
           l_fd_order_id,
           v_trade_order_no,
           v_order_source,
           v_trade_delivery_no,
           v_down_channel_no,
           l_down_account_id,
           l_down_company_id,
           v_up_channel_no,
           l_up_account_id,
           l_up_company_id,
           l_up_down_unit,
           l_up_down_face,
           l_up_down_amount,
           l_up_down_real,
           v_up_refund_unit,
           v_up_refund_face,
           v_up_refund_amount,
           v_up_refund_real_amount,
           sysdate,
           to_date(v_order_time, 'yyyy-mm-dd hh24:mi:ss'),
           pkg_up_fund_change_type.refund_amount,
           v_bill_type,
           v_business_type,
           v_carrier_no,
           v_province_no,
           (l_inner_balance + v_up_refund_amount),
           t.profits,
           t.down_commission,
           (v_up_refund_amount - v_up_refund_real_amount),
           l_settle_amount,
           l_settle_status,
           v_memo,
           to_date(v_out_change_time, 'yyyy-mm-dd hh24:mi:ss')
      from fd_trade_up_fund t
     where t.fd_order_id = l_fd_order_id
       and t.trade_delivery_no = v_trade_delivery_no
       and t.change_type = pkg_up_fund_change_type.debit_amount
       and rownum <= 1;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---9.记录差异信息
  l_result := fd_f_trade_fund_ms_differ_add(l_fd_order_id,
                                            pkg_trade_type.up_refund,
                                            v_down_channel_no,
                                            l_down_account_id,
                                            l_business_type,
                                            v_bill_type,
                                            l_order_face,
                                            l_order_amount,
                                            l_up_down_face,
                                            l_up_down_amount,
                                            v_up_refund_face);

  return l_result;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_ORDER_REFUND_T
prompt ==============================================
prompt
create or replace function fd_account.fd_f_trade_up_order_refund_t(v_order_source          number,
                                                        v_down_channel_no       varchar2,
                                                        v_up_channel_no         varchar2,
                                                        v_trade_order_no        varchar2,
                                                        v_trade_delivery_no     varchar2,
                                                        v_trade_refund_no       varchar2,
                                                        v_bill_type             number,
                                                        v_business_type         number,
                                                        v_carrier_no            varchar2,
                                                        v_province_no           varchar2,
                                                        v_down_draw_unit        number,
                                                        v_down_draw_face        number,
                                                        v_down_draw_amount      number,
                                                        v_down_real_amount      number,
                                                        v_up_refund_unit        number,
                                                        v_up_refund_face        number,
                                                        v_up_refund_amount      number,
                                                        v_up_refund_real_amount number,
                                                        v_refund_time           varchar2,
                                                        v_memo                  varchar2)
  return varchar2 is

  ------------上游订单退款------------
  l_fd_order_id     number;
  l_refund_count    number;
  l_up_company_id   number;
  l_settle_amount   number;
  l_settle_status   number;
  l_query_balance   number;
  l_inner_balance   number;
  l_down_account_id number;
  l_down_company_id number;
  l_up_account_id   number;
  l_up_fund_id      number;

  l_business_type   number;
  l_carrier_no      varchar2(32);
  l_province_no     varchar2(32);
  l_cn_refund_count number;
  l_cn_fd_order_id  number;
begin
  --=============================检查订单重复退款、金额错误==============================
  ---1.锁下游订单
  begin
    select t.fd_order_id, t.down_account_id, t.down_company_id
      into l_fd_order_id, l_down_account_id, l_down_company_id
      from fd_trade_order_main t
     where t.trade_order_no = v_trade_order_no
       and t.order_source = v_order_source
       and rownum <= 1
       for update;
    --===============================---
    select t.business_type, t.carrier_no, t.province_no
      into l_business_type, l_carrier_no, l_province_no
      from fd_trade_order_main t
     where t.fd_order_id = l_fd_order_id;

    select t.up_account_id, t.up_company_id, t.settle_amount
      into l_up_account_id, l_up_company_id, l_settle_amount
      from fd_trade_up_fund t
     where t.trade_order_no = v_trade_order_no
       and t.trade_delivery_no = v_trade_delivery_no
       and t.order_source = v_order_source
       and t.change_type = pkg_up_fund_change_type.debit_amount
       and rownum <= 1;
  exception
    when no_data_found then
      --===============================---
      l_settle_amount := v_up_refund_amount;

      select seq_trade_order_main_id.nextval into l_fd_order_id from dual;

      select count(1), max(t.fd_order_id)
        into l_cn_refund_count, l_cn_fd_order_id
        from fd_trade_down_fund t
       where t.trade_order_no = v_trade_order_no
         and t.order_source = v_order_source
         and rownum <= 1;

      if (l_cn_refund_count > 0) then
        l_fd_order_id := l_cn_fd_order_id;
      end if;

      select t.account_id, c.company_id
        into l_down_account_id, l_down_company_id
        from fd_base_down_account_map t
       inner join fd_base_down_channel c on t.channel_no = c.channel_no
       where t.channel_no = v_down_channel_no
         and t.is_main_channel = 0;

      select t.account_id, c.company_id
        into l_up_account_id, l_up_company_id
        from fd_base_up_account_map t
       inner join fd_base_up_channel c on t.channel_no = c.channel_no
       where t.channel_no = v_up_channel_no
         and t.is_main_channel = 0;
  end;

  ---2.检查重复退款
  select count(1)
    into l_refund_count
    from fd_trade_up_refund t
   where t.trade_refund_no = v_trade_refund_no
     and t.order_source = v_order_source
     and rownum <= 1;

  if (l_refund_count > 0) then
    return pkg_error_code.success;
  end if;

  --=============================获取退款结算信息=================================
  ---5.获取公司结算状态
  l_settle_status := pkg_settle_status.no_need;
  if (l_up_company_id != l_down_company_id) then
    l_settle_status := pkg_settle_status.wait_settle;
  end if;

  --==================================上游退款===================================
  ---6.获取并修改账户余额
  select t.query_balance, t.inner_balance
    into l_query_balance, l_inner_balance
    from fd_base_up_account t
   where t.account_id = l_up_account_id
     for update;

  update fd_base_up_account t
     set t.inner_balance = (l_inner_balance + v_up_refund_amount)
   where t.account_id = l_up_account_id;

  ---7.添加上游退款记录
  select seq_trade_up_fund_id.nextval into l_up_fund_id from dual;

  insert into fd_trade_up_refund
    (record_id,
     channel_no,
     account_id,
     fd_order_id,
     trade_order_no,
     trade_delivery_no,
     trade_refund_no,
     order_source,
     refund_time,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     refund_unit,
     refund_face,
     refund_amount,
     real_refund,
     memo)
  values
    (l_up_fund_id,
     v_up_channel_no,
     l_up_account_id,
     l_fd_order_id,
     v_trade_order_no,
     v_trade_delivery_no,
     v_trade_refund_no,
     v_order_source,
     to_date(v_refund_time, 'yyyy-mm-dd hh24:mi:ss'),
     v_bill_type,
     v_business_type,
     v_carrier_no,
     v_province_no,
     v_up_refund_unit,
     v_up_refund_face,
     v_up_refund_amount,
     v_up_refund_real_amount,
     v_memo);

  ---8.添加上游资金变动

  insert into fd_trade_up_fund
    (record_id,
     fd_order_id,
     trade_order_no,
     order_source,
     trade_delivery_no,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     carrier_no,
     province_no,
     balance,
     profits,
     down_commission,
     up_commission,
     settle_amount,
     settle_status,
     memo)
  values
    (l_up_fund_id,
     l_fd_order_id,
     v_trade_order_no,
     v_order_source,
     v_trade_delivery_no,
     v_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     l_up_account_id,
     l_up_company_id,
     0,
     0,
     0,
     0,
     v_up_refund_unit,
     v_up_refund_face,
     v_up_refund_amount,
     v_up_refund_real_amount,
     to_date(v_refund_time, 'yyyy-mm-dd hh24:mi:ss'),
     pkg_up_fund_change_type.refund_amount,
     v_bill_type,
     v_business_type,
     v_carrier_no,
     v_province_no,
     (l_inner_balance + v_up_refund_amount),
     ((v_up_refund_face / v_down_draw_face) * v_down_real_amount -
     v_up_refund_real_amount),
     (v_up_refund_face / v_down_draw_face) *
     (v_down_draw_amount - v_down_real_amount),
     (v_up_refund_amount - v_up_refund_real_amount),
     l_settle_amount,
     l_settle_status,
     v_memo);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_RV_TRADE
prompt ========================================
prompt
create or replace function fd_account.fd_f_trade_up_rv_trade(v_up_channel_no    varchar2,
                                                  v_up_account_id    number,
                                                  v_up_company_id    number,
                                                  v_adjust_record_no varchar2,
                                                  v_up_draw_amount   number,
                                                  v_up_real_amount   number,
                                                  v_business_type    number,
                                                  v_bill_type        number,
                                                  v_create_user      varchar2,
                                                  v_memo             varchar2)
  return varchar2 is
  l_inner_balance    number;
  l_trade_up_fund_id number;
  l_adjust_count     number;
  --------------------下游渠道交易平账-----------------------
begin

  ---1.锁支付账户
  select t.inner_balance, seq_trade_up_fund_id.nextval
    into l_inner_balance, l_trade_up_fund_id
    from fd_base_up_account t
   where t.account_id = v_up_account_id
     for update;

  ---2.检查平账是否已处理
  select count(1)
    into l_adjust_count
    from fd_trade_up_manual t
   where t.ext_record_no = v_adjust_record_no;

  if l_adjust_count > 0 then
    return pkg_error_code.success;
  end if;

  ---3.交易扣款不为零，需要修改支付账户余额
  if v_up_draw_amount != 0 then
    update fd_base_up_account t
       set t.inner_balance  = (l_inner_balance - v_up_draw_amount),
           t.last_edit_user = v_create_user,
           t.last_edit_time = sysdate
     where t.account_id = v_up_account_id;
  end if;

  ---4.添加平账上游人工操作记录

  insert into fd_trade_up_manual
    (record_id,
     channel_no,
     account_id,
     adjust_id,
     manual_change_type,
     change_amount,
     change_time,
     balance,
     memo,
     create_user,
     has_adjust,
     ext_record_no)
  values
    (l_trade_up_fund_id,
     v_up_channel_no,
     v_up_account_id,
     0,
     pkg_up_manual_change_type.trade_revise,
     v_up_draw_amount,
     sysdate,
     (l_inner_balance - v_up_draw_amount),
     v_memo,
     v_create_user,
     0,
     v_adjust_record_no);

  ---5.添加平账上游资金变动（上游下游公司相同避免结算)
  insert into fd_trade_up_fund
    (record_id,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     up_draw_amount,
     up_real_amount,
     change_time,
     change_type,
     bill_type,
     business_type,
     balance,
     memo)
  values
    (l_trade_up_fund_id,
     0,
     0,
     v_up_company_id,
     v_up_channel_no,
     v_up_account_id,
     v_up_company_id,
     0,
     0,
     0,
     0,
     v_up_draw_amount,
     v_up_real_amount,
     sysdate,
     pkg_up_fund_change_type.debit_amount,
     v_bill_type,
     v_business_type,
     (l_inner_balance - v_up_draw_amount),
     v_memo);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_RV_TRADE_CK
prompt ===========================================
prompt
create or replace function fd_account.fd_f_trade_up_rv_trade_ck(v_up_channel_no  varchar2,
                                                     v_up_draw_amount number,
                                                     v_up_real_amount number,
                                                     v_up_account_id  out number,
                                                     v_up_company_id  out number)

 return varchar2 is
begin
  ---1.检查调整金额是否正确
  if (v_up_draw_amount = 0 and v_up_real_amount = 0) then
    return pkg_error_code.amount_error;
  end if;

  ---2.检查渠道账户是否正确
  select t.account_id, c.company_id
    into v_up_account_id, v_up_company_id
    from fd_base_up_account_map t
   inner join fd_base_up_account a on t.account_id = a.account_id
   inner join fd_base_up_channel c on t.channel_no = c.channel_no
   where t.channel_no = v_up_channel_no
     and rownum <= 2;

  return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_or_account_error;
end;
/

prompt
prompt Creating function FD_F_TRADE_UP_STATUS_CHECK
prompt ============================================
prompt
create or replace function fd_account.fd_f_trade_up_status_check(v_up_channel_no varchar2)
  return varchar2 is
  ----------------检查渠道状态是否为2----------------
  l_channel_status number;
begin
  select t.status
    into l_channel_status
    from fd_base_up_channel t
   where t.channel_no = v_up_channel_no
     and t.status != 2;

  return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.failure;

end;
/

prompt
prompt Creating function FD_P_APPLY_DOWN_RV_CHANNEL
prompt ============================================
prompt
create or replace function fd_account.fd_p_apply_down_rv_channel(v_apply_id        in number,
                                                      v_down_channel_no in varchar2, ---下游渠道编号
                                                      v_down_account_id in number, ---下游支付账户             
                                                      v_revise_amount   in number, ---账户调整金额（可正、可负）
                                                      v_create_user     in varchar2, ---操作人
                                                      v_memo            in varchar2 ---备注
                                                      ) return varchar2 is
  --------------平账、下游手工渠道账户平账---------------
  PRAGMA AUTONOMOUS_TRANSACTION;
  l_fund_id number;
  l_result  varchar2(32);
begin
  ---1.记录操作日志
  fd_p_write_handle_log('下游渠道平账',
                        v_create_user,
                        ('v_down_channel_no=' || v_down_channel_no ||
                        ',v_down_account_id=' || v_down_account_id ||
                        ',v_revise_amount=' || v_revise_amount));

  ---2.检查渠道与金额是否正确
  l_result := fd_f_manual_down_rv_channel_ck(v_down_channel_no,
                                             v_down_account_id,
                                             v_revise_amount);
  if (l_result != pkg_error_code.success) then
    rollback;
    return l_result;
  end if;

  ---3.渠道账户平账
  l_result := fd_f_manual_down_rv_channel(v_down_channel_no,
                                          v_down_account_id,
                                          1,
                                          v_revise_amount,
                                          v_create_user,
                                          v_memo,
                                          l_fund_id);
  if (l_result != pkg_error_code.success) then
    rollback;
    return l_result;
  end if;

  ---4.完成平账申请记录
  l_result := fd_f_apply_rv_complete(v_apply_id,
                                     v_down_channel_no,
                                     v_down_account_id,
                                     pkg_apply_rv_type.apply_balance_rv,
                                     0,
                                     0,
                                     v_revise_amount,
                                     v_revise_amount,
                                     v_create_user,
                                     l_fund_id,
                                     v_memo);
  if (l_result != pkg_error_code.success) then
    rollback;
    return l_result;
  end if;

  commit;
  return pkg_error_code.success;

exception
  when others then
    rollback;
    fd_p_write_log('fd_p_manual_down_rv_channel', sqlerrm, 1);
    return pkg_error_code.system_busy;
end;
/

