prompt
prompt Creating function SUP_P_MANUAL_UP_ADD
prompt =====================================
prompt
create or replace function fd_account.sup_p_manual_up_add(v_up_channel_no   varchar2,
                                               v_bank_account_id number,
                                               v_add_count       number,
                                               v_add_face        number,
                                               v_add_amount      number,
                                               v_service_fee     number,
                                               v_memo            varchar2,
                                               v_create_user     varchar2)
  return varchar2 is
  PRAGMA AUTONOMOUS_TRANSACTION;
  --------------sup上游渠道加款--------------
  l_result              varchar2(32);
  l_up_account_id       number;
  l_up_company_id       number;
  l_system_id           number;
  l_up_channel_name     varchar2(32);
  l_up_account_name     varchar2(32);
  l_sup_down_channel_no varchar2(32);
  l_sup_down_accout_id  number;
  l_18_up_channel_no    varchar2(32);
  l_18_up_accout_id     number;
  l_bank_card_no        varchar2(64);
  l_bank_fund_id        number;
  l_bank_service_id     number;
  l_bank_relate_id      number;
begin
  ---1.检查加款信息
  l_result := sup_f_up_add_check(v_up_channel_no,
                                 v_bank_account_id,
                                 v_add_count,
                                 v_add_face,
                                 v_add_amount,
                                 v_service_fee,
                                 l_up_account_id,
                                 l_up_company_id,
                                 l_system_id,
                                 l_up_channel_name,
                                 l_up_account_name,
                                 l_sup_down_channel_no,
                                 l_sup_down_accout_id,
                                 l_18_up_channel_no,
                                 l_18_up_accout_id);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---2.银行账户扣款
  l_result := sup_f_up_add_bank_minus(v_bank_account_id,
                                      v_add_amount,
                                      v_service_fee,
                                      l_up_channel_name,
                                      l_up_account_name,
                                      v_memo,
                                      '',
                                      v_create_user,
                                      l_bank_card_no,
                                      l_bank_fund_id,
                                      l_bank_service_id,
                                      l_bank_relate_id);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---3.sup上游渠道加款
  l_result := sup_f_up_channel_add(v_up_channel_no,
                                   l_up_account_id,
                                   l_up_company_id,
                                   l_system_id,
                                   l_bank_card_no,
                                   l_bank_fund_id,
                                   l_bank_service_id,
                                   l_bank_relate_id,
                                   v_add_count,
                                   v_add_face,
                                   v_add_amount,
                                   v_memo,
                                   v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---4.sup下游渠道加款
  l_result := sup_f_down_channel_add(l_sup_down_channel_no,
                                     l_sup_down_accout_id,
                                     l_up_company_id,
                                     l_system_id,
                                     l_bank_relate_id,
                                     v_add_face,
                                     v_add_amount,
                                     v_memo,
                                     v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---5.用卡生产系统上游渠道加款
  l_result := sup_f_pro_up_channel_add(l_18_up_channel_no,
                                       l_18_up_accout_id,
                                       l_up_company_id,
                                       l_bank_relate_id,
                                       v_add_amount,
                                       v_memo,
                                       v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---6.记录操作日志
  fd_p_write_handle_log('sup上游关联加款',
                        v_create_user,
                        ('v_up_channel_no=' || v_up_channel_no ||
                        'v_bank_account_id=' || v_bank_account_id ||
                        ',v_add_amount=' || v_add_amount));

  commit;
  return pkg_error_code.success;

exception
  when others then
    rollback;
    fd_p_write_log('sup_p_manual_up_add', sqlerrm, 1);
    return pkg_error_code.system_busy;
end;
/

prompt
prompt Creating function SUP_P_MANUAL_UP_ADD_ADJUST
prompt ============================================
prompt
create or replace function fd_account.sup_p_manual_up_add_adjust(v_add_record_id number,
                                                      v_memo          varchar2,
                                                      v_create_user   varchar2)
  return varchar2 is
  PRAGMA AUTONOMOUS_TRANSACTION;
  --------------sup上游渠道加款红冲--------------
  l_result           varchar2(32);
  l_bank_record_id   number;
  l_bank_service_id  number;
  l_bank_relate_id   number;
  l_adjust_relate_id number;
  l_up_channel_no    varchar2(32);
  l_change_time      date;
begin
  ---1.检查上游加款相关记录数据
  l_result := sup_f_up_add_adjust_check(v_add_record_id,
                                        l_up_channel_no,
                                        l_change_time,
                                        l_bank_record_id,
                                        l_bank_service_id,
                                        l_bank_relate_id);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---2.上游加款银行流水记录红冲
  l_result := sup_f_up_add_bank_adjust(l_bank_record_id,
                                       l_bank_service_id,
                                       l_bank_relate_id,
                                       v_memo,
                                       v_create_user,
                                       l_adjust_relate_id);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---3.上游加款记录红冲
  l_result := sup_f_up_add_adjust(v_add_record_id,
                                  l_adjust_relate_id,
                                  v_memo,
                                  v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---4.上游加款关联下游红冲
  l_result := sup_f_down_add_adjust(l_up_channel_no,
                                    l_bank_relate_id,
                                    l_change_time,
                                    l_adjust_relate_id,
                                    v_memo,
                                    v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---5.上游加款关联生产系统上游红冲
  l_result := sup_f_pro_up_add_adjust(l_up_channel_no,
                                      l_bank_relate_id,
                                      l_change_time,
                                      l_adjust_relate_id,
                                      v_memo,
                                      v_create_user);
  if l_result != pkg_error_code.success then
    rollback;
    return l_result;
  end if;

  ---6.记录操作日志
  fd_p_write_handle_log('sup上游加款红冲',
                        v_create_user,
                        ('v_add_record_id=' || v_add_record_id));

  commit;
  return pkg_error_code.success;

exception
  when others then
    rollback;
    fd_p_write_log('sup_p_manual_up_add_adjust', sqlerrm, 1);
    return pkg_error_code.system_busy;
end;
/

