create or replace function fd_account.yx_f_adjust_agent_draw(v_record_id   in number,
                                                  v_create_user in varchar2,
                                                  v_memo        in varchar2)

 return varchar2 is
  /*
    功能介绍:代理人提款红冲
    创建人:邓孝辉
    创建时间:2019/3/25
  */
  l_agent_id              varchar2(32);
