  l_service_change_amount number;
  l_bank_service_id       number;
  l_bank_balance          number;
  l_handle_batch_id       number;
  l_agent_balance         number;
  l_fund_record_id        number;
  l_bank_fund_id          number;

begin
  ---1. 获取提款数据
  select m.agent_id, m.change_amount, m.change_time, m.bank_fund_id
    into l_agent_id, l_change_amount, l_change_time, l_bank_batch_id
    from yx_trade_agent_manual m
   inner join yx_base_agent a
      on m.agent_id = a.agent_id
   where m.record_id = v_record_id
     and m.add_type = pkg_agent_manual_change_type.draw_amount
     and m.has_adjust = pkg_sys_boolean.isfalse;

  --2. 获取实体卡变动记录
  select t.account_id, t.record_id
    into l_bank_account_id, l_bank_fund_id
    from fd_bank_cash_fund t
   where t.service_id = l_bank_batch_id
     and t.use_type = pkg_bank_use_type.down_draw
     and t.change_type = pkg_bank_fund_change_type.minus_amount
     and t.change_time > (l_change_time - 1 / 24 / 60)
     and t.change_time < (l_change_time + 1 / 24 / 60)
     and rownum <= 2;

  ---3. 获取手续费记录数据
  select count(1), nvl(max(t.change_amount), 0), max(t.record_id)
    into l_service_count, l_service_change_amount, l_bank_service_id
    from fd_bank_cash_fund t
   where t.service_id = l_bank_batch_id
     and t.use_type = pkg_bank_use_type.service_fee
     and t.change_type = pkg_bank_fund_change_type.minus_amount
     and t.change_time > (l_change_time - 1 / 24 / 60)
     and t.change_time < (l_change_time + 1 / 24 / 60)
     and rownum <= 2;

  --=================4.实体卡手续费记录红冲====================
  ---[锁实体卡,更新实体卡余额,红冲实体卡代理人提款,是否有手续费记录,红冲实体卡手续费]---
  ---4.1 锁实体卡账户
  select a.balance
    into l_bank_balance
    from fd_bank_account_info a
   where a.account_id = l_bank_account_id
     for update;

  ---4.2 更新实体卡余额
  update fd_bank_account_info i
     set i.balance = i.balance + l_change_amount + l_service_change_amount
   where i.account_id = l_bank_account_id;

  ---4.3获取数据红冲对应的统一流水编号
  select seq_bank_service_id.nextval into l_handle_batch_id from dual;

  ---4.4 红冲实体卡代理人提款记录
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     adjust_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     has_adjust,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
    select seq_bank_cash_fund_id.nextval,
           t.account_id,
           t.record_id,
           t.company_id,
           (0 - t.change_amount),
           (l_bank_balance + l_service_change_amount + l_change_amount),
           sysdate,
           v_create_user,
           t.change_type,
           t.use_type,
           pkg_sys_boolean.istrue,
           t.link_channel_name,
           t.link_account_name,
           v_memo,
           l_handle_batch_id
      from fd_bank_cash_fund t
     where t.record_id = l_bank_fund_id
       and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---修改原实体卡提款纪录状态
  update fd_bank_cash_fund t
     set t.has_adjust = pkg_sys_boolean.istrue
   where t.record_id = l_bank_fund_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---4.5 是否有手续费记录,红冲实体卡手续费记录
  if (l_service_count > 0) then
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       adjust_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       has_adjust,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
      select seq_bank_cash_fund_id.nextval,
             t.account_id,
             t.record_id,
             t.company_id,
             (0 - t.change_amount),
             (l_bank_balance + l_service_change_amount),
             sysdate,
             v_create_user,
             t.change_type,
             t.use_type,
             pkg_sys_boolean.istrue,
             t.link_channel_name,
             t.link_account_name,
             v_memo,
             l_handle_batch_id
        from fd_bank_cash_fund t
       where t.record_id = l_bank_service_id
         and t.has_adjust = pkg_sys_boolean.isfalse;
  
    if sql%rowcount = 0 then
      return pkg_error_code.not_exists;
    end if;
  
    --- 修改原手续费记录状态
    update fd_bank_cash_fund t
       set t.has_adjust = pkg_sys_boolean.istrue
     where t.record_id = l_bank_service_id
       and t.has_adjust = pkg_sys_boolean.isfalse;
  
    if sql%rowcount = 0 then
      return pkg_error_code.not_exists;
    end if;
  end if;
  --=================5.代理人记录红冲====================
  ---[锁代理人余额,修改代理人余额,红冲代理人手工提款记录,红冲代理人提款记录]---

  ---5.1. 锁代理人余额
  select a.balance
    into l_agent_balance
    from yx_base_agent a
   where a.agent_id = l_agent_id
     for update;

  ---5.2. 修改代理人余额
  update yx_base_agent a
     set a.balance = a.balance + l_change_amount
   where a.agent_id = l_agent_id;

  --获取红冲记录序号
  select seq_yxtradeagentfund_auto_id.nextval
    into l_fund_record_id
    from dual;
  ---5.3. 红冲代理人手工提款记录
  insert into yx_trade_agent_manual
    (record_id,
     agent_id,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     bank_fund_id)
    select l_fund_record_id,
           t.agent_id,
           t.account_id,
           t.record_id,
           pkg_sys_boolean.istrue,
           pkg_agent_manual_change_type.draw_red,
           0 - t.change_amount,
           sysdate,
           l_agent_balance + l_change_amount,
           v_create_user,
           v_memo,
           l_handle_batch_id
      from yx_trade_agent_manual t
     where t.record_id = v_record_id
       and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---修改原代理人手工提款记录
  update yx_trade_agent_manual m
     set m.has_adjust = pkg_sys_boolean.istrue
   where m.record_id = v_record_id
     and m.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;
  ---5.4 红冲代理人提款记录
  insert into yx_trade_agent_fund
    (record_id,
     up_fund_id,
     down_channel_no,
     up_channel_no,
     order_source,
     agent_id,
     change_amount,
     balance,
     change_type,
     change_time)
    select l_fund_record_id,
           f.up_fund_id,
           f.down_channel_no,
           f.up_channel_no,
           f.order_source,
           f.agent_id,
           0 - l_change_amount,
           l_agent_balance + l_change_amount,
           pkg_agent_fund_change_type.draw_amount,
           sysdate
      from yx_trade_agent_fund f
     where f.record_id = v_record_id;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_ADJUST_AGENT_DRAW_CHECK
prompt ==============================================
prompt
create or replace function fd_account.yx_f_adjust_agent_draw_check(v_record_id   in number,
                                                        v_adjust_days in number)

 return varchar2 is
  /*
    功能介绍:代理人检查提款记录,账户,余额,手续费
    创建人:邓孝辉
    创建时间:2019/3/25
  */
  l_record_count number;
  l_change_time  date;
begin
  ---1. 检查代理人提款记录,账户,余额
  select count(1), max(m.change_time)
    into l_record_count, l_change_time
    from yx_trade_agent_manual m
   inner join yx_base_agent a
      on m.agent_id = a.agent_id
   inner join fd_bank_cash_fund f
      on m.bank_fund_id = f.service_id
   inner join fd_bank_account_info b
      on b.account_id = f.account_id
   where m.record_id = v_record_id
     and m.has_adjust = pkg_sys_boolean.isfalse
     and f.has_adjust = pkg_sys_boolean.isfalse
     and m.add_type = pkg_down_manual_change_type.draw_amount
     and m.change_amount = f.change_amount;

  if (l_record_count != 1 and l_record_count != 2) then
    return pkg_error_code.data_error;
  end if;

  if (l_change_time < (sysdate - v_adjust_days)) then
    return pkg_error_code.adjust_timeout;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_ADJUST_DOWN_ADD
prompt ======================================
prompt
create or replace function fd_account.yx_f_adjust_down_add(v_record_id   in number,
                                                v_create_user in varchar2,
                                                v_memo        in varchar2)
  return varchar2 is
  /**
         功能介绍:下游加款红冲
         创建人:邓孝辉
         创建时间:2019/3/25
  */
  l_channel_no         varchar2(32);
  l_account_id         number;
  l_change_amount      number;
  l_change_time        date;
  l_bank_batch_id      number;
  l_bank_balance       number;
  l_bank_account_id    number;
  l_bank_fund_id       number;
  l_handle_batch_id    number;
  l_inner_balance      number;
  l_fund_id            number;

begin

  ---1. 查询下游红冲数据
select m.channel_no,
       m.account_id,
       m.change_amount,
       m.change_time,
       m.bank_fund_id
  into l_channel_no,
       l_account_id,
       l_change_amount,
       l_change_time,
       l_bank_batch_id
  from yx_trade_down_manual m
 where m.record_id = v_record_id;

---2. 获取实体卡变动记录
select f.account_id, f.record_id
  into l_bank_account_id, l_bank_fund_id
  from fd_bank_cash_fund f
 where f.service_id = l_bank_batch_id
   and f.has_adjust = pkg_sys_boolean.isfalse
   and f.use_type = pkg_bank_use_type.down_add
   and f.change_type = pkg_bank_fund_change_type.add_amount
   and f.change_time > (l_change_time - 1 / 24 / 60)
   and f.change_time < (l_change_time + 1 / 24 / 60)
   and rownum <= 2;

--3 =====================银行卡记录红冲=============================
--[锁实体卡,更新实体卡金额,红冲银行卡记录红冲]---

---3.1 锁实体卡
select b.balance
  into l_bank_balance
  from fd_bank_account_info b
 where b.account_id = l_bank_account_id
   for update;

---3.2 修改实体卡金额
update fd_bank_account_info i
   set i.balance = i.balance - l_change_amount
 where i.account_id = l_bank_account_id;

---获取红冲序号
select seq_bank_service_id.nextval into l_handle_batch_id from dual;

---3.3 红冲实体卡下游加款
insert into fd_bank_cash_fund
  (record_id,
   account_id,
   adjust_id,
   company_id,
   change_amount,
   balance,
   change_time,
   created_user,
   change_type,
   use_type,
   has_adjust,
   link_channel_name,
   link_account_name,
   remark,
   service_id)
  select seq_bank_cash_fund_id.nextval,
         t.account_id,
         t.record_id,
         t.company_id,
         (0 - l_change_amount),
         (l_bank_balance - l_change_amount),
         sysdate,
         v_create_user,
         t.change_type,
         t.use_type,
         pkg_sys_boolean.istrue,
         t.link_channel_name,
         t.link_account_name,
         v_memo,
         l_handle_batch_id
    from fd_bank_cash_fund t
   where t.record_id = l_bank_fund_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---3.4 修改原数据

update fd_bank_cash_fund c
   set c.has_adjust = pkg_sys_boolean.istrue
 where c.record_id = l_bank_fund_id
   and c.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;
--4 =====================银行卡记录红冲=============================
--[锁渠道账户,更新渠道账户金额,红冲下游手工加款,红冲下游加款记录]---

---4.1 锁渠道账户
select a.inner_balance
  into l_inner_balance
  from yx_base_down_account a
 where a.account_id = l_account_id
   for update;

---4.2 更新渠道账户金额
update yx_base_down_account a
   set a.inner_balance = a.inner_balance - l_change_amount
 where a.account_id = l_account_id;

---获取红冲序号
select seq_yxtradedownfund_auto_id.nextval into l_fund_id from dual;

---4.3 红冲下游手工加款
insert into yx_trade_down_manual
  (record_id,
   channel_no,
   account_id,
   adjust_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   bank_fund_id,
   memo)
  select l_fund_id,
         t.channel_no,
         t.account_id,
         t.record_id,
         pkg_sys_boolean.istrue,
         pkg_down_manual_change_type.red_recharge,
         0 - l_change_amount,
         sysdate,
         l_inner_balance - l_change_amount,
         v_create_user,
         l_handle_batch_id,
         v_memo
    from yx_trade_down_manual t
   where t.record_id = v_record_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---修改原纪录
update yx_trade_down_manual t
   set t.has_adjust = pkg_sys_boolean.istrue
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---4.4 红冲下游加款变动记录
insert into yx_trade_down_fund
  (record_id,
   yx_order_id,
   trade_order_no,
   ext_order_no,
   order_source,
   down_channel_no,
   down_account_id,
   change_time,
   order_time,
   change_type,
   pay_amount,
   change_amount,
   balance,
   memo)
  select l_fund_id,
         t.yx_order_id,
         t.trade_order_no,
         t.ext_order_no,
         t.order_source,
         t.down_channel_no,
         t.down_account_id,
         sysdate,
         t.order_time,
         pkg_down_fund_change_type.add_amount,
         0 - l_change_amount,
         0 - l_change_amount,
         l_inner_balance - l_change_amount,
         v_memo
    from yx_trade_down_fund t
   where t.record_id = v_record_id;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_ADJUST_DOWN_ADD_CHECK
prompt ============================================
prompt
create or replace function fd_account.yx_f_adjust_down_add_check(v_record_id   in number,
                                                      v_adjust_days in number)
  return varchar2 is
  /*
         功能介绍:检查加款记录,账户
         创建人:邓孝辉
         创建时间:2019/3/25
  */
  l_record_count  number;
  l_change_time   date;
begin
  ---1. 下游加款检查
select count(1), max(t.change_time)
  into l_record_count, l_change_time
  from yx_trade_down_manual t
 inner join yx_base_down_account a
    on t.account_id = a.account_id
 inner join fd_bank_cash_fund f
    on f.service_id = t.bank_fund_id
 inner join fd_bank_account_info i
    on i.account_id = f.account_id
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse
   and f.has_adjust = pkg_sys_boolean.isfalse
   and t.change_amount = f.change_amount;

if (l_record_count != 1) then
  return pkg_error_code.data_error;
end if;

if (l_change_time < (sysdate - v_adjust_days)) then
  return pkg_error_code.adjust_timeout;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_ADJUST_DOWN_DRAW
prompt =======================================
prompt
create or replace function fd_account.yx_f_adjust_down_draw(v_record_id   in number,
                                                 v_create_user in varchar2,
                                                 v_memo        in varchar2)
  return varchar2 is
/*
  功能介绍:下游提款红冲
  创建人:邓孝辉
  创建时间:2019/3/25
*/
l_manual_record_id                               number;
l_channel_no                                     varchar2(32);
l_pay_account_id                                 number;
l_change_amount                                  number;
l_change_time                                    date;
l_bank_batch_id                                  number;
l_bank_fund_id                                   number;
l_bank_account_id                                number;
l_service_count                                  number;
l_service_amount                                 number;
l_service_bank_id                                number;
l_bank_balance                                   number;
l_handle_batch_id                                number;
l_inner_balance                                  number;
l_fund_id                                        number;

begin

---1. 获取红冲数据
select t.record_id,
       t.channel_no,
       t.account_id,
       t.change_amount,
       t.change_time,
       t.bank_fund_id
  into l_manual_record_id,
       l_channel_no,
       l_pay_account_id,
       l_change_amount,
       l_change_time,
       l_bank_batch_id
  from yx_trade_down_manual t
 where t.record_id = v_record_id
   and t.add_type = pkg_down_manual_change_type.draw_amount
   and t.has_adjust = pkg_sys_boolean.isfalse;

---2. 获取实体卡变动编号
select f.record_id, f.account_id
  into l_bank_fund_id, l_bank_account_id
  from fd_bank_cash_fund f
 where f.service_id = l_bank_batch_id
   and f.has_adjust = pkg_sys_boolean.isfalse
   and f.change_time > (l_change_time - 1 / 24 / 60)
   and f.change_time < (l_change_time + 1 / 24 / 60)
   and f.use_type = pkg_bank_use_type.down_draw
   and f.change_type = pkg_bank_fund_change_type.minus_amount
   and rownum <= 2;

---3.获取手续费条数
select count(1), nvl(max(t.change_amount), 0), max(t.record_id)
  into l_service_count, l_service_amount, l_service_bank_id
  from fd_bank_cash_fund t
 where t.service_id = l_bank_batch_id
   and t.change_type = pkg_bank_fund_change_type.minus_amount
   and t.use_type = pkg_bank_use_type.service_fee
   and t.change_time > (l_change_time - 1 / 24 / 60)
   and t.change_time < (l_change_time + 1 / 24 / 60)
   and rownum <= 2;

--4 ===================实体卡记录红冲====================
--[锁实体卡,修改实体卡账户,手续费红冲,银行变动红冲]--

--4.1 锁实体卡
select t.balance
  into l_bank_balance
  from fd_bank_account_info t
 where t.account_id = l_bank_account_id
   for update;

--4.2 修改实体卡账户余额
update fd_bank_account_info a
   set a.balance = a.balance + l_change_amount + l_service_amount
 where a.account_id = l_bank_account_id;

---获取红冲提款序号
select seq_bank_service_id.nextval into l_handle_batch_id from dual;

if (l_service_count > 0) then
  ---4.3 红冲手续费
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     adjust_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     has_adjust,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
    select seq_bank_cash_fund_id.nextval,
           t.account_id,
           t.record_id,
           t.company_id,
           (0 - t.change_amount),
           (l_bank_balance + l_service_amount),
           sysdate,
           v_create_user,
           t.change_type,
           t.use_type,
           pkg_sys_boolean.istrue,
           t.link_channel_name,
           t.link_account_name,
           v_memo,
           l_handle_batch_id
      from fd_bank_cash_fund t
     where t.record_id = l_service_bank_id
       and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---修改原手续费记录状态
  update fd_bank_cash_fund t
     set t.has_adjust = pkg_sys_boolean.istrue
   where t.record_id = l_service_bank_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;
end if;

---4.4 红冲实体卡下游提款记录
insert into fd_bank_cash_fund
  (record_id,
   account_id,
   adjust_id,
   company_id,
   change_amount,
   balance,
   change_time,
   created_user,
   change_type,
   use_type,
   has_adjust,
   link_channel_name,
   link_account_name,
   remark,
   service_id)
  select seq_bank_cash_fund_id.nextval,
         t.account_id,
         t.record_id,
         t.company_id,
         (0 - t.change_amount),
         (l_bank_balance + l_service_amount + l_change_amount),
         sysdate,
         v_create_user,
         t.change_type,
         t.use_type,
         pkg_sys_boolean.istrue,
         t.link_channel_name,
         t.link_account_name,
         v_memo,
         l_handle_batch_id
    from fd_bank_cash_fund t
   where t.record_id = l_bank_fund_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---修改原记录状态
update fd_bank_cash_fund t
   set t.has_adjust = pkg_sys_boolean.istrue
 where t.record_id = l_bank_fund_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

--5 =================渠道账户红冲===================
--[锁渠道,更新渠道账户,红冲下游手工扣款,红冲下游扣款变动]--

--5.1 锁渠道
select t.inner_balance
  into l_inner_balance
  from yx_base_down_account t
 where t.account_id = l_pay_account_id
   for update;

---5.2 更新渠道账户
update yx_base_down_account t
   set t.inner_balance = t.inner_balance + l_change_amount
 where t.account_id = l_pay_account_id;

---获取红冲序号
select seq_yxtradedownfund_auto_id.nextval into l_fund_id from dual;

---5.3红冲下游手工提款
insert into yx_trade_down_manual
  (record_id,
   channel_no,
   account_id,
   adjust_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   bank_fund_id,
   memo)
  select l_fund_id,
         t.channel_no,
         t.account_id,
         t.record_id,
         pkg_sys_boolean.istrue,
         pkg_down_manual_change_type.draw_red,
         0 - t.change_amount,
         sysdate,
         l_inner_balance + l_change_amount,
         v_create_user,
         l_handle_batch_id,
         v_memo
    from yx_trade_down_manual t
   where t.record_id = v_record_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---修改原手工数据
update yx_trade_down_manual t
   set t.has_adjust = pkg_sys_boolean.istrue
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

--5.4 红冲下游提款
insert into yx_trade_down_fund
  (record_id,
   yx_order_id,
   trade_order_no,
   ext_order_no,
   order_source,
   down_channel_no,
   down_account_id,
   change_time,
   order_time,
   change_type,
   pay_amount,
   change_amount,
   balance,
   memo)
select l_fund_id,
       t.yx_order_id,
       t.trade_order_no,
       t.ext_order_no,
       t.order_source,
       t.down_channel_no,
       t.down_account_id,
       sysdate,
       t.order_time,
       pkg_down_fund_change_type.draw_amount,
       0-t.pay_amount,
       0-t.change_amount,
       l_inner_balance+t.change_amount,
       v_memo
   from yx_trade_down_fund t
  where t.record_id=v_record_id;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_ADJUST_DOWN_DRAW_CHECK
prompt =============================================
prompt
create or replace function fd_account.yx_f_adjust_down_draw_check(v_record_id   in number,
                                                       v_adjust_days in number)
  return varchar2 is
/*
  功能介绍:检查提款记录,账户,余额,手续费
  创建人:邓孝辉
  创建时间:2019/3/25
*/
l_record_count    number;
l_change_time     date;
begin
  --1. 检查记录
select count(1), max(t.change_time)
  into l_record_count, l_change_time
  from yx_trade_down_manual t
 inner join yx_base_down_account a
    on a.account_id = t.account_id
 inner join fd_bank_cash_fund f
    on f.service_id = t.bank_fund_id
 inner join fd_bank_account_info i
    on f.account_id = i.account_id
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse
   and f.has_adjust = pkg_sys_boolean.isfalse
   and t.add_type = pkg_down_manual_change_type.draw_amount
   and t.change_amount = f.change_amount;

if (l_record_count != 1 and l_record_count != 2) then
  return pkg_error_code.data_error;
end if;

if (l_change_time < (sysdate - v_adjust_days)) then
  return pkg_error_code.adjust_timeout;
end if;

return pkg_error_code.success;

end;
/

prompt
prompt Creating function YX_F_ADJUST_UP_ADD
prompt ====================================
prompt
create or replace function fd_account.yx_f_adjust_up_add(v_record_id   in number,
                                              v_create_user in varchar2,
                                              v_memo        in varchar2)
  return varchar2 as
/*
  功能介绍:红冲上游加款
  创建人:邓孝辉
  创建人:2019/3/25
*/
l_channel_no                                  varchar2(32);
l_pay_account_id                              number;
l_change_amount                               number;
l_change_time                                 date;
l_bank_batch_id                               number;
l_bank_fund_id                                number;
l_bank_account_id                             number;
l_service_count                               number;
l_service_change_amount                       number;
l_bank_service_id                             number;
l_bank_balance                                number;
l_handle_batch_id                             number;
l_inner_balance                               number;
l_fund_id                                     number;

begin

---1. 红冲查询数据
select t.channel_no,
       t.account_id,
       t.change_amount,
       t.change_time,
       t.bank_fund_id
  into l_channel_no,
       l_pay_account_id,
       l_change_amount,
       l_change_time,
       l_bank_batch_id
  from yx_trade_up_manual t
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

---2. 实体卡数据
select c.record_id, c.account_id
  into l_bank_fund_id, l_bank_account_id
  from fd_bank_cash_fund c
 where c.service_id = l_bank_batch_id
   and c.has_adjust = pkg_sys_boolean.isfalse
   and c.use_type = pkg_bank_use_type.up_add
   and c.change_type = pkg_bank_fund_change_type.minus_amount
   and c.change_time > l_change_time - 1 / 24 / 60
   and c.change_time < l_change_time + 1 / 24 / 60
   and rownum <= 2;

---3. 获取手续费记录数据
select count(1), nvl(max(t.change_amount), 0), max(t.record_id)
  into l_service_count, l_service_change_amount, l_bank_service_id
  from fd_bank_cash_fund t
 where t.service_id = l_bank_batch_id
   and t.use_type = pkg_bank_use_type.service_fee
   and t.change_type = pkg_bank_fund_change_type.minus_amount
   and t.change_time > (l_change_time - 1 / 24 / 60)
   and t.change_time < (l_change_time + 1 / 24 / 60)
   and rownum <= 2;

--4. =================实体卡手续费记录红冲====================
--[锁实体卡,更新实体卡余额,红冲手续费,红冲实体卡记录]--

--4.1 锁实体卡
select i.balance
  into l_bank_balance
  from fd_bank_account_info i
 where i.account_id = l_bank_account_id
   for update;

--4.2 更新实体卡余额
update fd_bank_account_info i
   set i.balance = i.balance + l_change_amount + l_service_change_amount
 where i.account_id = l_bank_account_id;

---获取数据红冲对应的统一流水编号
select seq_bank_service_id.nextval into l_handle_batch_id from dual;

if (l_service_count > 0) then
  ---4.3 红冲实体卡手续费记录
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     adjust_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     has_adjust,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
    select seq_bank_cash_fund_id.nextval,
           t.account_id,
           t.record_id,
           t.company_id,
           (0 - t.change_amount),
           (l_bank_balance + l_service_change_amount),
           sysdate,
           v_create_user,
           t.change_type,
           t.use_type,
           pkg_sys_boolean.istrue,
           t.link_channel_name,
           t.link_account_name,
           v_memo,
           l_handle_batch_id
      from fd_bank_cash_fund t
     where t.record_id = l_bank_service_id
       and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;

  ---修改原手续费记录状态
  update fd_bank_cash_fund t
     set t.has_adjust = pkg_sys_boolean.istrue
   where t.record_id = l_bank_service_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

  if sql%rowcount = 0 then
    return pkg_error_code.not_exists;
  end if;
end if;

---4.4 红冲实体卡加款记录
insert into fd_bank_cash_fund
  (record_id,
   account_id,
   adjust_id,
   company_id,
   change_amount,
   balance,
   change_time,
   created_user,
   change_type,
   use_type,
   has_adjust,
   link_channel_name,
   link_account_name,
   remark,
   service_id)
  select seq_bank_cash_fund_id.nextval,
         t.account_id,
         t.record_id,
         t.company_id,
         (0 - t.change_amount),
         (l_bank_balance + l_service_change_amount + l_change_amount),
         sysdate,
         v_create_user,
         t.change_type,
         pkg_bank_use_type.up_add,
         pkg_sys_boolean.istrue,
         t.link_channel_name,
         t.link_account_name,
         v_memo,
         l_handle_batch_id
    from fd_bank_cash_fund t
   where t.record_id = l_bank_fund_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---修改原实体卡加款纪录状态
update fd_bank_cash_fund t
   set t.has_adjust = pkg_sys_boolean.istrue
 where t.record_id = l_bank_fund_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

--5 ========================渠道账户记录红冲==============================
--[锁渠道账户,更新渠道余额,红冲上游手工加款,红冲变动记录]--

--5.1 锁渠道
select t.inner_balance
  into l_inner_balance
  from yx_base_up_account t
 where t.account_id = l_pay_account_id
   for update;

---5.2 更新账户渠道余额
update yx_base_up_account t
   set t.inner_balance = t.inner_balance - l_change_amount
 where t.account_id = l_pay_account_id;

---红冲手工加款序号
select seq_yxtradeupfund_auto_id.nextval into l_fund_id from dual;

---5.3 红冲上游手工加款
insert into yx_trade_up_manual
  (record_id,
   channel_no,
   account_id,
   adjust_id,
   has_adjust,
   add_type,
   change_amount,
   real_amount,
   change_time,
   balance,
   create_user,
   bank_fund_id,
   memo)
  select l_fund_id,
         t.channel_no,
         t.account_id,
         t.record_id,
         pkg_sys_boolean.istrue,
         pkg_up_manual_change_type.red_recharge,
         0 - t.change_amount,
         t.real_amount,
         sysdate,
         l_inner_balance - l_change_amount,
         v_create_user,
         l_handle_batch_id,
         v_memo
    from yx_trade_up_manual t
   where t.record_id = v_record_id
     and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---修改手工加款
update yx_trade_up_manual t
   set t.has_adjust = pkg_sys_boolean.istrue
 where t.record_id = v_record_id
   and t.has_adjust = pkg_sys_boolean.isfalse;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

---5.4 红冲上游变动记录
insert into yx_trade_up_fund
  (record_id,
   yx_order_id,
   trade_order_no,
   trade_delivery_no,
   ext_delivery_no,
   order_source,
   up_channel_no,
   up_account_id,
   up_company_id,
   agent_id,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   balance,
   memo)
  select l_fund_id,
         t.yx_order_id,
         t.trade_order_no,
         t.trade_delivery_no,
         t.ext_delivery_no,
         t.order_source,
         t.up_channel_no,
         t.up_account_id,
         t.up_company_id,
         t.agent_id,
         t.up_draw_unit,
         t.up_draw_face,
         0 - t.up_draw_amount,
         0 - t.up_real_amount,
         t.up_servie_fee,
         sysdate,
         pkg_up_fund_change_type.add_amount,
         l_inner_balance - l_change_amount,
         v_memo
    from yx_trade_up_fund t
   where t.record_id = v_record_id;

if sql%rowcount = 0 then
  return pkg_error_code.not_exists;
end if;

return pkg_error_code.success;
end;
/

