prompt
prompt Creating function YX_F_GROUP_AGENT
prompt ==================================
prompt
create or replace function fd_account.yx_f_group_agent(v_down_channel_no varchar2, --下游渠道编号
                                            v_agent_count     in number, --含有的代理人数量
                                            l_agent_arr       in t_Array, --代理人编号组
                                            v_out_agent_group in varchar2, --重新组合后的外部系统代理人编号
                                            v_agent_id        out varchar2)
  return varchar2 is
  ----------------------------------------------------
  --主要功能：判断组合代理人
  --创建人：周荣省
  --创建时间;2019-05-22
  ----------------------------------------------------
  l_number    number; --代理人数量
  l_number1 number;
  l_group_agent varchar2(32); --组合代理人外部编号
begin
  --1.判断代理人数量
  select count(1)
    into l_number
    from yx_base_agent t
   where t.down_channel_no = v_down_channel_no
     and t.agent_id in (select * from table(l_agent_arr));

  if l_number <> v_agent_count then
    return pkg_yx_error_code.group_count_error;
  end if;
  --2.判断组合代理人编号是否存在
  l_group_agent := 'ZH' || v_out_agent_group;
  select count(1)
    into l_number
    from yx_base_agent t
   where t.down_channel_no = v_down_channel_no
     and t.group_agent = pkg_yx_agent_type.group_agent
     and t.out_agent_id = l_group_agent;
  if l_number<1 then
    return pkg_yx_error_code.no_group_agent;
  end if;
  select max(t.agent_id)
    into v_agent_id
    from yx_base_agent t
   where t.down_channel_no = v_down_channel_no
     and t.group_agent = pkg_yx_agent_type.group_agent
     and t.out_agent_id = l_group_agent;
  return pkg_yx_error_code.success;
end;
/

prompt
prompt Creating function YX_F_GROUP_AGENT_FEE
prompt ======================================
prompt
create or replace function fd_account.yx_f_group_agent_fee(v_agent_count     in number, --含有的代理人数量
                                                v_agency_fee_list in t_Array, --代理费金额
                                                v_agency_fee      out number --代理费总金额
                                                ) return varchar2 is
  ----------------------------------------------------
  --主要功能：计算代理费总金额
  --创建人：周荣省
  --创建时间;2019-05-22
  ----------------------------------------------------
  l_number number; --代理费数量
  l_j      number := 0;
begin
  --1.判断代理人数量
  select count(1) into l_number from table(v_agency_fee_list);

  if l_number <> v_agent_count then
    return pkg_yx_error_code.group_count_error;
  end if;
  --2.计算总和
  v_agency_fee := 0;
  for item in (select * from table(v_agency_fee_list)) loop
    begin
      l_j          := l_j + 1;
      v_agency_fee := v_agency_fee + to_number(v_agency_fee_list(l_j));
    end;
  end loop;
  return pkg_yx_error_code.success;
end;
/

prompt
prompt Creating function YX_F_GROUP_AGENT_SOLT
prompt =======================================
prompt
create or replace function fd_account.yx_f_group_agent_solt(v_agent_arr       in t_Array, --代理人编号组
                                                 v_out_agent_group out varchar2, -- 代理人编号重新组合 
                                                 v_out_agent_group_name out varchar2--组合代理人名称
                                                 ) return varchar2 is
  ----------------------------------------------------
  --主要功能：代理人编号排序
  --创建人：周荣省
  --创建时间;2019-05-22
  ----------------------------------------------------
begin
  v_out_agent_group := '';
  v_out_agent_group_name:='组合';
  for item in (select t.agent_id,t.agent_name
                 from yx_base_agent t
                where t.agent_id in (SELECT * FROM TABLE(v_agent_arr))
                order by to_number(t.agent_id) asc) loop
    begin
      v_out_agent_group := v_out_agent_group|| '_' || item.agent_id ;
      v_out_agent_group_name:=v_out_agent_group_name||'_'||item.agent_name;
    end;
  end loop;
  return pkg_yx_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_AGENT_DRAW
prompt ========================================
prompt
create or replace function fd_account.yx_f_manual_agent_draw(v_agent_id        in varchar2, ---代理人编号
                                                  v_bank_account_id in number, ---实体卡账户编号
                                                  v_amount          in number, ---提款金额
                                                  v_service_fee     in number, ---手续费
                                                  v_memo            in varchar2, ---备注
                                                  v_create_user     in varchar2, ---创建人
                                                  v_agent_name      in varchar2 ---代理人名称
                                                  )

 return varchar2 is

  /*
         功能介绍:代理人提款
         创建人:邓孝辉
         创建时间:2019/3/19
  */
  l_bank_balance   number;
  l_bank_company   number;
  l_fund_record_id number;
  l_bank_batch_id  number;
  l_agent_balance  number;
begin

  ---1. 锁实体卡
  select t.balance, t.company_id
    into l_bank_balance, l_bank_company
    from fd_bank_account_info t
   where t.account_id = v_bank_account_id
     for update;

  if (l_bank_balance < v_amount + v_service_fee) then
    return pkg_error_code.balance_low;
  end if;

  ---2.获取部分添加参数
  select seq_yxtradeagentfund_auto_id.nextval
    into l_fund_record_id
    from dual;

  ---获取银行卡资金变动统一流水号(用于红冲时便于查找)
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

  --====================3.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡提款的资金变动]===
  ---3.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance - v_amount - v_service_fee),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

  ---3.2 添加实体卡资金变动    
  if (v_service_fee > 0) then
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
    values
      (seq_bank_cash_fund_id.nextval,
       v_bank_account_id,
       l_bank_company,
       v_service_fee,
       (l_bank_balance - v_service_fee),
       sysdate,
       v_create_user,
       pkg_bank_fund_change_type.minus_amount,
       pkg_bank_use_type.service_fee,
       '-',
       '-',
       v_memo,
       l_bank_batch_id);
  end if;

  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company,
     v_amount,
     (l_bank_balance - v_amount - v_service_fee),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.minus_amount,
     pkg_bank_use_type.down_draw,
     v_agent_name,
     v_agent_name,
     v_memo,
     l_bank_batch_id);

  --==============4.更新渠道账户==================================================
  --===[锁代理人账户、更新渠道账户余额、添加代理人手工提款记录、添加代理人资金变动]=====
  ---4.1  锁代理人账户
  select a.balance
    into l_agent_balance
    from yx_base_agent a
   where a.agent_id = v_agent_id
     for update;

  ---4.2  更新代理人账户余额
  update yx_base_agent a
     set a.balance        = a.balance - v_amount,
         a.last_edit_user = v_create_user,
         a.last_edit_time = sysdate
   where a.agent_id = v_agent_id
     and a.balance >= v_amount;

  if (sql%rowcount = 0) then
    return pkg_error_code.balance_low;
  end if;

  --4.3 添加代理人手工提款记录
  insert into yx_trade_agent_manual
    (record_id,
     agent_id,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     bank_fund_id)
  values
    (l_fund_record_id,
     v_agent_id,
     0,
     0,
     1,
     pkg_agent_manual_change_type.draw_amount,
     v_amount,
     sysdate,
     l_agent_balance - v_amount,
     v_create_user,
     v_memo,
     l_bank_batch_id);

  ---4.4  添加代理人资金变动记录
  insert into yx_trade_agent_fund
    (record_id,
     up_fund_id,
     down_channel_no,
     up_channel_no,
     order_source,
     agent_id,
     change_amount,
     balance,
     change_type,
     change_time)
  values
    (l_fund_record_id,
     0,
     '-',
     '-',
     0,
     v_agent_id,
     v_amount,
     l_agent_balance - v_amount,
     pkg_agent_fund_change_type.draw_amount,
     sysdate);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_AGENT_DRAW_CHECK
prompt ==============================================
prompt
create or replace function fd_account.yx_f_manual_agent_draw_check(v_agent_id           in  varchar2,  ---代理人编号
                                                        v_bank_account_id    in  number,  ---实体卡账户编号
                                                        v_service_fee        in  number,  ---手续费
                                                        v_amount             in  number,  ---提现金额
                                                        v_agent_name         out varchar2)
       return varchar2 is
  /*
       功能介绍:代理人提款校验
       创建人:邓孝辉
       创建时间:2019/3/19
  */

l_agent_balance             number;
l_bank_count                number;
l_bank_balance              number;
l_bank_company_id           number;
l_company_id                number;
begin
  --1.校验提款金额是否大于0
  if(v_amount<=0 or v_service_fee<0) then
   return pkg_error_code.amount_error;
  end if;

  ---2. 检查代理人信息
  select a.balance,a.agent_name,a.company_id
    into l_agent_balance,v_agent_name,l_company_id
   from yx_base_agent a
   where a.agent_id=v_agent_id;

    if(l_agent_balance<v_amount) then
     return pkg_error_code.account_balance_error;
    end if;

   ---3. 检查实体卡信息
   select count(1),max(t.balance),max(t.company_id)
     into l_bank_count,l_bank_balance,l_bank_company_id
    from fd_bank_account_info t
  where t.account_id=v_bank_account_id;

  if(l_bank_company_id!=l_company_id) then
   return pkg_error_code.company_error;
  end if;

  if(l_bank_count=0) then
   return pkg_error_code.bank_account_id_error;
  end if;

  if(l_bank_balance<v_amount+v_service_fee) then
   return pkg_error_code.balance_low;
  end if;

return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_error;

end;
/

prompt
prompt Creating function YX_F_MANUAL_AGENT_RV_CHECK
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_agent_rv_check(v_agent_id  in varchar2,
                                                      v_agent_fee in varchar2)
  return varchar2 is
  ------------------代理费平账校验--------------------------
  l_number number;
begin
  --1. 代理人渠道校验
  select count(1)
    into l_number
    from yx_base_agent a
   where a.agent_id = v_agent_id
     and (a.balance + v_agent_fee) > 0;
  if l_number < 1 then
    return pkg_error_code.data_error;
  end if;
  return pkg_error_code.success;
exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_AGENT_RV_FEE
prompt ==========================================
prompt
create or replace function fd_account.yx_f_manual_agent_rv_fee(v_agent_id        in    varchar2,
                                                    v_agent_fee       in    number,
                                                    v_down_channel_no in    varchar2,
                                                    v_down_company_id in    number,
                                                    v_business_type   in    number,
                                                    v_create_user     in    varchar2,
                                                    v_memo            in    varchar2)
          -------------------------代理费平账------------------------------
          return varchar2 is
          l_agent_fund_id                           number;
          l_up_fund_id                              number;
          l_agent_balance                           number;
          begin
            --0.获取序号
select seq_yxtradeagentfund_auto_id.nextval,
       seq_yxtradeupfund_auto_id.nextval
  into l_agent_fund_id,  l_up_fund_id
  from dual;

--1 =============================锁代理人,创建代理人资金变动===================================
--1.1 锁代理人,修改代理人余额
select a.balance
  into l_agent_balance
  from yx_base_agent a
 where a.agent_id = v_agent_id
   for update;

update yx_base_agent t
   set t.balance = t.balance + v_agent_fee
 where t.agent_id = v_agent_id;

if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

--1.2 创建代理人工操作

insert into yx_trade_agent_manual
  (record_id,
   agent_id,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_agent_fund_id,
   v_agent_id,
   0,
   0,
   pkg_agent_manual_change_type.agent_revise,
   v_agent_fee,
   sysdate,
   l_agent_balance + v_agent_fee,
   v_create_user,
   v_memo);

if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

--1.3 创建资金变动(通过up_fund_id关联上游资金变动)
insert into yx_trade_agent_fund
  (record_id,
   up_fund_id,
   down_channel_no,
   up_channel_no,
   order_source,
   agent_id,
   change_amount,
   balance,
   change_type,
   change_time,
   business_type)
values
  (l_agent_fund_id,
   l_up_fund_id,
   v_down_channel_no,
   0,
   0,
   v_agent_id,
   v_agent_fee,
   l_agent_balance + v_agent_fee,
   pkg_agent_fund_change_type.debit_amount,
   sysdate,
   v_business_type);

if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

--2. ===================================创建上游资金变动=======================================
--2.1 上游资金变动（上游下游公司相同避免结算)
insert into yx_trade_up_fund
  (record_id,
   down_channel_no,
   down_account_id,
   down_company_id,
   up_channel_no,
   up_account_id,
   up_company_id,
   down_agency_fee,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   business_type,
   balance,
   memo)
values
  (l_up_fund_id,
   v_down_channel_no,
   0,
   v_down_company_id,
   0,
   0,
   v_down_company_id,
   v_agent_fee,
   0,
   0,
   0,
   0,
   0,
   sysdate,
   pkg_up_fund_change_type.debit_amount,
   v_business_type,
   0,
   v_memo);
if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_ADD
prompt ======================================
prompt
create or replace function fd_account.yx_f_manual_down_add(v_down_channel_no in varchar2, --下游渠道编号
                                                v_bank_account_id in number, --实体卡账户号
                                                v_amount          in number, --加款金额
                                                v_create_user     in varchar2, --创建人
                                                v_memo            in varchar2, --备注
                                                v_account_id      in number,   --账号编号
                                                v_account_name    in varchar2, --账号名称
                                                v_channel_name    in varchar2) --渠道名称
        return varchar2 is

/*
    功能介绍:账户手工加款(锁实体银行卡,渠道账户)
    创建人:邓孝辉
    创建时间:2019/3/18
*/

l_bank_company        number;
l_bank_balance        number;
l_fund_record_main_id number;
l_inner_balance       number;
l_bank_batch_id       number;

begin
  --================1.锁账户获取参数======================
  --=========[锁公司手续费账户、锁实体卡账户、检查实体卡余额、获取seq]=======

    --1.1 锁实体卡账户
    select t.balance,t.company_id
      into l_bank_balance,l_bank_company
    from fd_bank_account_info t
    where t.account_id=v_bank_account_id
     for update;

  ---1.2 获取部分添加参数
  select seq_yxtradedownfund_auto_id.nextval
    into l_fund_record_main_id
    from dual;

      ---获取银行卡资金变动统一流水号(为红冲时查询)
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

    --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡加款的资金变动]===
  ---2.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance + v_amount),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

     ---2.2 添加实体卡资金变动
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company,
     v_amount,
     (l_bank_balance + v_amount),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.add_amount,
     pkg_bank_use_type.down_add,
     v_channel_name,
     v_account_name,
     v_memo,
     l_bank_batch_id);

     ---==================3.更新渠道账户信息============================
     --===[锁渠道账户,更新渠道账户余额,添加下游、添加下游手工加款记录、添加下游资金变动]=====
     ---3.1 锁下游渠道账户
     select da.inner_balance
       into l_inner_balance
      from yx_base_down_account da
      where da.account_id=v_account_id
       for update;

     ---3.2 更新渠道账户余额
     update yx_base_down_account t
        set t.inner_balance   = (t.inner_balance+v_amount),
            t.last_edit_user  = v_create_user,
            t.last_edit_time  = sysdate
      where t.account_id = v_account_id;

      ---3.3 添加下游人工加款记录
        insert into yx_trade_down_manual
          (record_id,
           channel_no,
           account_id,
           adjust_id,
           has_adjust,
           add_type,
           change_amount,
           change_time,
           balance,
           create_user,
           memo,
           bank_fund_id)
        values
          (l_fund_record_main_id,
           v_down_channel_no,
           v_account_id,
           0,
           1,
           pkg_down_manual_change_type.add_amount,
           v_amount,
           sysdate,
           l_inner_balance+v_amount,
           v_create_user,
           v_memo,
           l_bank_batch_id);

      ---3.4 添加下游资金变动
      insert into yx_trade_down_fund
            (record_id,
             yx_order_id,
             trade_order_no,
             ext_order_no,
             order_source,
             down_channel_no,
             down_account_id,
             change_time,
             change_type,
             pay_amount,
             change_amount,
             balance,
             memo)
          values
            (l_fund_record_main_id,
             0,
             '-',
             '-',
             0,
             v_down_channel_no,
             v_account_id,
             sysdate,
             pkg_down_fund_change_type.add_amount,
             v_amount,
             v_amount,
             (l_inner_balance + v_amount),
             v_memo);

      return pkg_error_code.success;

end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_ADD_CHECK
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_down_add_check(v_down_channel_no in varchar2, --下游渠道编号
                                                      v_bank_account_id in number, --实体卡账户号
                                                      v_amount          in number, --加款金额
                                                      v_account_name    out varchar2, --账户名称
                                                      v_channel_name    out varchar2, --渠道名称
                                                      v_account_id      out number)   --账户编号
      return varchar2 is

/*
      功能介绍:下游加款校验
      创建人:邓孝辉
      创建时间:2019/3/18
*/

      l_bank_account    number;
      l_company_id      number;
      l_bank_company_id number;
begin
      ---1.判断金额是否大于0
      if(v_amount<=0) then
        return pkg_error_code.amount_error;
      end if;

      --2.检查下游渠道
      select  c.channel_name,a.account_name,c.account_id,c.company_id
         into v_channel_name,v_account_name,v_account_id,l_company_id
      from yx_base_down_channel c
      inner join yx_base_down_account a on c.account_id=a.account_id
      where c.channel_no=v_down_channel_no;

      ---3.检查实体卡是否存在
      select count(1),max(t.company_id)
        into l_bank_account,l_bank_company_id
      from fd_bank_account_info t
      where t.account_id=v_bank_account_id;

      if(l_bank_account=0) then
       return pkg_error_code.bank_account_id_error;
      end if;
      
      if(l_bank_company_id!=l_company_id) then
       return pkg_error_code.company_error;
      end if;

      return pkg_error_code.success;
exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_DRAW
prompt =======================================
prompt
create or replace function fd_account.yx_f_manual_down_draw(v_down_channel_no in varchar2, ---下游渠道编号
                                                 v_account_id      in number, ---下游账户编号
                                                 v_bank_account_id in number, ---实体卡账户编号
                                                 v_service_fee     in number, ---手续费
                                                 v_amount          in number, ---提款金额
                                                 v_create_user     in varchar2, ---创建者
                                                 v_memo            in varchar2, ---备注
                                                 v_account_name    in varchar2, ---账户名称
                                                 v_channel_name    in varchar2) ---渠道名称
 return varchar2 is

  /*
          功能介绍:下游手工提款操作
          创建人:邓孝辉
          创建时间:2018/3/19
  */

  l_bank_balance        number;
  l_fund_record_main_id number;
  l_bank_batch_id       number;
  l_bank_company        number;
  l_inner_balance       number;
  l_credit_money        number;
begin
  ---1.锁实体卡账户
  select t.balance, t.company_id
    into l_bank_balance, l_bank_company
    from fd_bank_account_info t
   where t.account_id = v_bank_account_id
     for update;

  if l_bank_balance < v_amount + v_service_fee then
    return pkg_error_code.balance_low;
  end if;

  ---1.2 获取部分添加参数
  select seq_yxtradedownfund_auto_id.nextval
    into l_fund_record_main_id
    from dual;

  ---获取银行卡资金变动统一流水号
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

  --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡提款的资金变动]===
  --2.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance - v_amount - v_service_fee),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

  --2.2 添加实体卡资金变动
  if (v_service_fee > 0) then
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
    values
      (seq_bank_cash_fund_id.nextval,
       v_bank_account_id,
       l_bank_company,
       v_service_fee,
       (l_bank_balance - v_service_fee),
       sysdate,
       v_create_user,
       pkg_bank_fund_change_type.minus_amount,
       pkg_bank_use_type.service_fee,
       v_channel_name,
       v_account_name,
       v_memo,
       l_bank_batch_id);
  end if;

  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company,
     v_amount,
     (l_bank_balance - v_amount - v_service_fee),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.minus_amount,
     pkg_bank_use_type.down_draw,
     v_channel_name,
     v_account_name,
     v_memo,
     l_bank_batch_id);

  --==============3.更新渠道账户==================================================
  --===[锁渠道账户、更新渠道账户余额、添加下游手工提款记录、添加下游资金变动]=====
  ---3.1 锁渠道账户
  select da.inner_balance, da.credit_money
    into l_inner_balance, l_credit_money
    from yx_base_down_account da
   where da.account_id = v_account_id
     for update;

  ---3.2 更新渠道余额
  update yx_base_down_account t
     set t.inner_balance  = (t.inner_balance - v_amount),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_account_id;

  ---3.3 添加下游手工提款记录
  insert into yx_trade_down_manual
    (record_id,
     channel_no,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     bank_fund_id)
  values
    (l_fund_record_main_id,
     v_down_channel_no,
     v_account_id,
     0,
     1,
     pkg_down_manual_change_type.draw_amount,
     v_amount,
     sysdate,
     l_inner_balance - v_amount,
     v_create_user,
     v_memo,
     l_bank_batch_id);

  ---3.4 添加下游资金变动
  insert into yx_trade_down_fund
    (record_id,
     yx_order_id,
     trade_order_no,
     ext_order_no,
     order_source,
     down_channel_no,
     down_account_id,
     change_time,
     change_type,
     pay_amount,
     change_amount,
     balance,
     memo)
  values
    (l_fund_record_main_id,
     0,
     '-',
     '-',
     0,
     v_down_channel_no,
     v_account_id,
     sysdate,
     pkg_down_fund_change_type.draw_amount,
     v_amount,
     v_amount,
     (l_inner_balance - v_amount),
     v_memo);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_DRAW_CHECK
prompt =============================================
prompt
create or replace function fd_account.yx_f_manual_down_draw_check(v_down_channel_no in  varchar2,  ---下游渠道编号
                                                       v_bank_account_id in  number,    ---实体卡账户号
                                                       v_service_fee     in  number,    ---手续费
                                                       v_amount          in  number,    ---提款金额
                                                       v_account_name    out varchar2, ---账户名称
                                                       v_channel_name    out varchar2, ---渠道名称
                                                       v_account_id      out varchar2)
      return varchar2 is

      /*
             功能介绍:下游渠道提款校验
             创建人:邓孝辉
             创建时间:2019/3/18
      */
      l_bank_account    number;
      l_bank_balance    number;
      l_company_id      number;
      l_bank_company_id number;
begin
      ---1.校验金额
      if(v_amount<=0 or v_service_fee<0 ) then
       return pkg_error_code.amount_error;
      end if;

      ---2.检查下游渠道
      select c.channel_name,c.account_id,a.account_name,c.company_id
        into v_channel_name,v_account_id,v_account_name,l_company_id
      from yx_base_down_channel c
      inner join yx_base_down_account a on c.account_id=a.account_id
      where c.channel_no=v_down_channel_no;

     ---3.检查实体卡是否存在
     select count(1),max(t.balance),max(t.company_id)
      into  l_bank_account,l_bank_balance,l_bank_company_id
     from fd_bank_account_info t
     where t.account_id=v_bank_account_id;

     if(l_company_id!=l_bank_company_id) then
      return pkg_error_code.company_error;
     end if;

     if(l_bank_account=0) then
      return pkg_error_code.bank_account_id_error;
     end if;

     if(l_bank_balance<v_amount+v_service_fee) then
      return pkg_error_code.balance_low;
     end if;
     


  return pkg_error_code.success;
exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_INNER_CHECK
prompt ==============================================
prompt
create or replace function fd_account.yx_f_manual_down_inner_check(v_out_channel_no  in  varchar2, --转出渠道
                                                        v_in_channel_no   in  varchar2, --转入渠道
                                                        v_amount          in  number,
                                                        v_out_account_id  out number,
                                                        v_in_account_id   out number)
       return varchar2 is
    l_out_balance      number;
    l_out_company_id    number;
    l_out_system       number;
    l_in_company_id     number;
    l_in_system        number;
begin

--- 校验转出金额是否正确,公司及系统是否正确
select a.inner_balance, t.company_id, t.source_system_id,t.account_id
  into l_out_balance, l_out_company_id, l_out_system,v_out_account_id
  from yx_base_down_channel t
  left join yx_base_down_account a
    on a.account_id = t.account_id
 where t.channel_no = v_out_channel_no;

select i.company_id, i.source_system_id,i.account_id
  into l_in_company_id, l_in_system,v_in_account_id
  from yx_base_down_channel i
 where i.channel_no = v_in_channel_no;

if (v_amount <= 0 or v_amount > l_out_balance) then
  return pkg_error_code.amount_error;
end if;

if (l_out_company_id != l_in_company_id or l_out_system != l_in_system or
   v_out_channel_no = v_in_channel_no or
   v_out_account_id = v_in_account_id) then
  return pkg_error_code.channel_error;
end if;

return pkg_error_code.success;
exception when others then return pkg_error_code.channel_or_account_error; end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_INNER_TSFER
prompt ==============================================
prompt
create or replace function fd_account.yx_f_manual_down_inner_tsfer(v_out_channel_no  in  varchar2,
                                                        v_in_channel_no   in  varchar2,
                                                        v_amount          in  number,
                                                        v_hander_user     in  varchar2,
                                                        v_memo            in  varchar2,
                                                        v_out_account_id  in  number,
                                                        v_in_account_id   in  number)
    return varchar2 is
    -------------------下游渠道内部转账--------------------
    l_out_balance            number;
    l_out_fund_id            number;

    l_in_balance             number;
    l_in_fund_id             number;
begin
  --===========================1.出账渠道操作=============================

--1.1 锁出账渠道账户
select oa.inner_balance
  into l_out_balance
  from yx_base_down_account oa
 where oa.account_id = v_out_account_id
   for update;

--1.2 修改出账渠道余额
update yx_base_down_account oa
   set oa.inner_balance  = oa.inner_balance - v_amount
 where oa.account_id = v_out_account_id
   and oa.inner_balance >= v_amount;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

select seq_yxtradedownfund_auto_id.nextval into l_out_fund_id from dual;

--1.3 添加出账账户提款记录
insert into yx_trade_down_manual
  (record_id,
   channel_no,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_out_fund_id,
   v_out_channel_no,
   v_out_account_id,
   0,
   pkg_down_manual_change_type.draw_amount,
   v_amount,
   sysdate,
   l_out_balance - v_amount,
   v_hander_user,
   v_memo);

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--1.4 添加出账账户资金变动
insert into yx_trade_down_fund
  (record_id,
   yx_order_id,
   order_source,
   down_channel_no,
   down_account_id,
   change_time,
   change_type,
   pay_amount,
   change_amount,
   balance,
   memo)
  select l_out_fund_id,
         0,
         0,
         oc.channel_no,
         v_out_account_id,
         sysdate,
         pkg_down_fund_change_type.draw_amount,
         v_amount,
         v_amount,
         l_out_balance - v_amount,
         v_memo
    from yx_base_down_channel oc
   where oc.channel_no = v_out_channel_no;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--===========================2.入账渠道操作=============================
--2.1 锁入账渠道账户
select i.inner_balance
  into l_in_balance
  from yx_base_down_account i
 where i.account_id = v_in_account_id;

--2.2 修改入账渠道余额
update yx_base_down_account i
   set i.inner_balance  = i.inner_balance + v_amount
 where i.account_id = v_in_account_id;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

select seq_yxtradedownfund_auto_id.nextval into l_in_fund_id from dual;

--2.3 添加入账账户提款记录
insert into yx_trade_down_manual
  (record_id,
   channel_no,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_in_fund_id,
   v_in_channel_no,
   v_in_account_id,
   0,
   pkg_down_manual_change_type.add_amount,
   v_amount,
   sysdate,
   l_in_balance + v_amount,
   v_hander_user,
   v_memo);

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--2.4 添加入账账户资金变动
insert into yx_trade_down_fund
  (record_id,
   yx_order_id,
   order_source,
   down_channel_no,
   down_account_id,
   change_time,
   change_type,
   pay_amount,
   change_amount,
   balance,
   memo)
values
  (l_in_fund_id,
   0,
   0,
   v_in_channel_no,
   v_in_account_id,
   sysdate,
   pkg_down_fund_change_type.add_amount,
   v_amount,
   v_amount,
   l_in_balance + v_amount,
   v_memo);

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_ISSUE_ADD
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_down_issue_add(v_down_channel_no in varchar2, --下游渠道编号
                                                v_bank_account_id in number, --实体卡账户号
                                                v_amount          in number, --加款金额
                                                v_create_user     in varchar2, --创建人
                                                v_memo            in varchar2, --备注
                                                v_account_id      in number,   --账号编号
                                                v_account_name    in varchar2, --账号名称
                                                v_channel_name    in varchar2) --渠道名称
        return varchar2 is

/*
    功能介绍:账户手工发行加款(锁实体银行卡,渠道账户)
    创建人:周荣省
    创建时间:2019/6/4
*/

l_bank_company        number;
l_bank_balance        number;
l_fund_record_main_id number;
l_inner_balance       number;
l_bank_batch_id       number;

begin
  --================1.锁账户获取参数======================
  --=========[锁公司手续费账户、锁实体卡账户、检查实体卡余额、获取seq]=======

    --1.1 锁实体卡账户
    select t.balance,t.company_id
      into l_bank_balance,l_bank_company
    from fd_bank_account_info t
    where t.account_id=v_bank_account_id
     for update;

  ---1.2 获取部分添加参数
  select seq_yxtradedownfund_auto_id.nextval
    into l_fund_record_main_id
    from dual;

      ---获取银行卡资金变动统一流水号(为红冲时查询)
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

    --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡加款的资金变动]===
  ---2.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance + v_amount),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

     ---2.2 添加实体卡资金变动
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company,
     v_amount,
     (l_bank_balance + v_amount),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.add_amount,
     pkg_bank_use_type.down_add,
     v_channel_name,
     v_account_name,
     v_memo,
     l_bank_batch_id);

     ---==================3.更新发行余额============================
     ---3.1 锁下游渠道
     select t.issue_balance
       into l_inner_balance
      from yx_base_down_channel t
      where t.channel_no=v_down_channel_no
       for update;

     ---3.2 更新渠道账户余额
     update yx_base_down_channel t
        set t.issue_balance   = (t.issue_balance+v_amount)
      where t.channel_no = v_down_channel_no;

      ---3.3 添加下游人工发行加款记录
        insert into yx_trade_down_manual
          (record_id,
           channel_no,
           account_id,
           adjust_id,
           has_adjust,
           add_type,
           change_amount,
           change_time,
           balance,
           create_user,
           memo,
           bank_fund_id)
        values
          (l_fund_record_main_id,
           v_down_channel_no,
           v_account_id,
           0,
           1,
           pkg_down_manual_change_type.issue_add,
           v_amount,
           sysdate,
           l_inner_balance+v_amount,
           v_create_user,
           v_memo,
           l_bank_batch_id);

      ---3.4 添加下游资金变动
      insert into yx_trade_down_fund
            (record_id,
             yx_order_id,
             trade_order_no,
             ext_order_no,
             order_source,
             down_channel_no,
             down_account_id,
             change_time,
             change_type,
             pay_amount,
             change_amount,
             balance,
             memo)
          values
            (l_fund_record_main_id,
             0,
             '-',
             '-',
             0,
             v_down_channel_no,
             v_account_id,
             sysdate,
             pkg_down_fund_change_type.add_issue,
             v_amount,
             v_amount,
             (l_inner_balance + v_amount),
             v_memo);

      return pkg_error_code.success;

end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_RV_CHANNEL
prompt =============================================
prompt
create or replace function fd_account.yx_f_manual_down_rv_channel(v_down_channel_no varchar2,
                                                       v_down_account_id number,
                                                       v_revise_type     number,
                                                       v_issue           in number, --发行类型，0：普通交易扣款，1：发行卡或优惠券扣款
                                                       v_revise_amount   number,
                                                       v_create_user     varchar2,
                                                       v_memo            varchar2,
                                                       v_trade_fund_id   out number)
  return varchar2 is
  -------------------------------------
  --功能：渠道账户余额平账
  --创建人：周荣省
  --创建时间：2019-07-30
  -------------------------------------
  l_inner_balance         number;
  l_down_manual_type      number;
  l_up_fund_type          number;
  l_down_fund_type        number;
  l_credit_money          number;
  l_balance_change_amount number;
  l_order_source          number;
begin
  ---1.获取变动记录参数
  select seq_yxtradedownfund_auto_id.nextval
    into v_trade_fund_id
    from dual;

  if (v_revise_amount > 0 and v_revise_type = pkg_sys_revise_type.balance) then
    l_down_manual_type      := pkg_down_manual_change_type.balance_revise_add;
    l_up_fund_type          := 0;
    l_down_fund_type        := pkg_down_fund_change_type.add_amount;
    l_balance_change_amount := v_revise_amount;
  elsif (v_revise_amount < 0 and
        v_revise_type = pkg_sys_revise_type.balance) then
    l_down_manual_type      := pkg_down_manual_change_type.balance_revise_minus;
    l_up_fund_type          := 0;
    l_down_fund_type        := pkg_down_fund_change_type.add_amount;
    l_balance_change_amount := v_revise_amount;
  elsif (v_revise_amount > 0 and v_revise_type = pkg_sys_revise_type.debit) then
    l_down_manual_type      := pkg_down_manual_change_type.debit_revise_add;
    l_up_fund_type          := pkg_up_fund_change_type.debit_amount;
    l_down_fund_type        := pkg_down_fund_change_type.debit_amount;
    l_balance_change_amount := 0 - v_revise_amount;
  elsif (v_revise_amount < 0 and v_revise_type = pkg_sys_revise_type.debit) then
    l_down_manual_type      := pkg_down_manual_change_type.debit_revise_minus;
    l_up_fund_type          := pkg_up_fund_change_type.debit_amount;
    l_down_fund_type        := pkg_down_fund_change_type.debit_amount;
    l_balance_change_amount := 0 - v_revise_amount;
  elsif (v_revise_amount > 0 and v_revise_type = pkg_sys_revise_type.refund) then
    l_down_manual_type      := pkg_down_manual_change_type.refund_revise_add;
    l_up_fund_type          := pkg_up_fund_change_type.refund_amount;
    l_down_fund_type        := pkg_down_fund_change_type.refund_amount;
    l_balance_change_amount := v_revise_amount;
  elsif (v_revise_amount < 0 and v_revise_type = pkg_sys_revise_type.refund) then
    l_down_manual_type      := pkg_down_manual_change_type.refund_revise_minus;
    l_up_fund_type          := pkg_up_fund_change_type.refund_amount;
    l_down_fund_type        := pkg_down_fund_change_type.refund_amount;
    l_balance_change_amount := v_revise_amount;
  else
    return pkg_error_code.data_error;
  end if;
  select t.source_system_id
    into l_order_source
    from yx_base_down_channel t
   where t.channel_no = v_down_channel_no;
  if v_issue = 0 then
    ---2.锁渠道账户
    select t.inner_balance, t.credit_money
      into l_inner_balance, l_credit_money
      from yx_base_down_account t
     where t.account_id = v_down_account_id
       for update;
  
    ---4修改账户余额添加资金变动
    update yx_base_down_account t
       set t.inner_balance  = (l_inner_balance + l_balance_change_amount),
           t.last_edit_user = v_create_user,
           t.last_edit_time = sysdate
     where t.account_id = v_down_account_id;
  
    ---5下游手工记录添加
    insert into yx_trade_down_manual
      (record_id,
       channel_no,
       account_id,
       adjust_id,
       has_adjust,
       add_type,
       change_amount,
       change_time,
       balance,
       create_user,
       memo)
    values
      (v_trade_fund_id,
       v_down_channel_no,
       v_down_account_id,
       0,
       0,
       l_down_manual_type,
       v_revise_amount,
       sysdate,
       (l_inner_balance + l_balance_change_amount),
       v_create_user,
       nvl2(v_memo, v_memo || ',下游平账', '下游平账'));
  
    ---6.下游资金变动添加
    insert into yx_trade_down_fund
      (record_id,
       yx_order_id,
       order_source,
       trade_order_no,
       down_channel_no,
       down_account_id,
       change_time,
       change_type,
       pay_amount,
       change_amount,
       balance,
       handling_fee,
       coupon_amount,
       memo)
    values
      (v_trade_fund_id,
       0,
       l_order_source,
       'RV' || v_trade_fund_id,
       v_down_channel_no,
       v_down_account_id,
       sysdate,
       l_down_fund_type,
       v_revise_amount,
       v_revise_amount,
       (l_inner_balance + l_balance_change_amount),
       0,
       0,
       v_memo);
  
    ---7.上游资金变动（不存下游公司以免结算)
    if l_up_fund_type != 0 then
      insert into yx_trade_up_fund
        (record_id,
         down_channel_no,
         down_account_id,
         down_company_id,
         up_channel_no,
         up_account_id,
         up_company_id,
         down_draw_unit,
         down_draw_face,
         down_draw_amount,
         down_real_amount,
         up_draw_amount,
         up_real_amount,
         change_time,
         change_type,
         balance,
         memo)
      values
        (seq_trade_up_fund_id.nextval,
         v_down_channel_no,
         v_down_account_id,
         0,
         0,
         0,
         0,
         0,
         0,
         v_revise_amount,
         v_revise_amount,
         0,
         0,
         sysdate,
         l_up_fund_type,
         0,
         v_memo);
    end if;
  else
    --8.锁渠道账户
    select t.issue_balance
      into l_inner_balance
      from yx_base_down_channel t
     where t.channel_no = v_down_channel_no
       for update;
  
    ---9修改账户余额添加资金变动
    update yx_base_down_channel t
       set t.issue_balance  = (l_inner_balance + l_balance_change_amount),
           t.last_edit_user = v_create_user,
           t.last_edit_time = sysdate
     where t.channel_no = v_down_channel_no;
  
    ---10下游手工记录添加
    insert into yx_trade_down_manual
      (record_id,
       channel_no,
       account_id,
       adjust_id,
       has_adjust,
       add_type,
       change_amount,
       change_time,
       balance,
       create_user,
       memo)
    values
      (v_trade_fund_id,
       v_down_channel_no,
       0,
       0,
       0,
       l_down_manual_type,
       v_revise_amount,
       sysdate,
       (l_inner_balance + l_balance_change_amount),
       v_create_user,
       nvl2(v_memo, v_memo || ',下游平账', '下游平账'));
  
    ---11.下游资金变动添加
    insert into yx_trade_down_fund
      (record_id,
       yx_order_id,
       order_source,
       trade_order_no,
       down_channel_no,
       down_account_id,
       change_time,
       change_type,
       pay_amount,
       change_amount,
       balance,
       handling_fee,
       coupon_amount,
       memo)
    values
      (v_trade_fund_id,
       0,
       l_order_source,
       'RV' || v_trade_fund_id,
       v_down_channel_no,
       v_down_account_id,
       sysdate,
       l_down_fund_type,
       v_revise_amount,
       v_revise_amount,
       (l_inner_balance + l_balance_change_amount),
       0,
       0,
       v_memo);
  
    ---12.上游资金变动（不存下游公司以免结算)
    if l_up_fund_type != 0 then
      insert into yx_trade_up_fund
        (record_id,
         down_channel_no,
         down_account_id,
         down_company_id,
         up_channel_no,
         up_account_id,
         up_company_id,
         down_draw_unit,
         down_draw_face,
         down_draw_amount,
         down_real_amount,
         up_draw_amount,
         up_real_amount,
         change_time,
         change_type,
         balance,
         memo)
      values
        (seq_trade_up_fund_id.nextval,
         v_down_channel_no,
         0,
         0,
         0,
         0,
         0,
         0,
         0,
         v_revise_amount,
         v_revise_amount,
         0,
         0,
         sysdate,
         l_up_fund_type,
         0,
         v_memo);
    end if;
  end if;
  return pkg_error_code.success;

end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_RV_CHANNEL_CK
prompt ================================================
prompt
create or replace function fd_account.yx_f_manual_down_rv_channel_ck(v_down_channel_no varchar2,
                                                          v_down_account_id number,
                                                          v_revise_amount   number)
  return varchar2 is
  ---------------------------------------
  --功能：检查下游渠道账户手工平账渠道账户、金额是否正确
  --创建人：周荣省
  --创建时间：2019-07-30
  ---------------------------------------
  l_down_account_id number;
begin
  ---1.检查调整金额是否正确
  if (v_revise_amount = 0) then
    return pkg_error_code.amount_error;
  end if;

  ---2.检查渠道账户是否正确
  select t.account_id
    into l_down_account_id
    from yx_base_down_channel t
   inner join yx_base_down_account a on t.account_id = a.account_id
   where t.channel_no = v_down_channel_no
     and t.account_id = v_down_account_id
     and rownum <= 2;

  return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_or_account_error;

end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_RV_TRADE
prompt ===========================================
prompt
create or replace function fd_account.yx_f_manual_down_rv_trade(v_channel_no     in    varchar2,
                                                     v_down_account_id  in    number,
                                                     v_down_company_id  in    number,
                                                     v_draw_amount    in    number,
                                                     v_real_amount    in    number,
                                                     v_business_type  in    number,
                                                     v_create_user    in    varchar2,
                                                     v_memo           in    varchar2)
       return varchar2 is
   ------------------下游渠道平账-------------------
   l_up_fund_id        number;
   l_down_fund_id      number;
   l_inner_balance     number;
begin
  --1. ========================获取序列号================================
select seq_yxtradedownfund_auto_id.nextval,
       seq_yxtradeupfund_auto_id.nextval
  into l_down_fund_id, l_up_fund_id
  from dual;
--2 ======================锁账户,创建下游资金变动==========================
--2.1 锁账户,修改余额
if v_draw_amount = 0 then
  select a.inner_balance
    into l_inner_balance
    from yx_base_down_account a
   where a.account_id = v_down_account_id;
else
  select a.inner_balance
    into l_inner_balance
    from yx_base_down_account a
   where a.account_id = v_down_account_id
     for update;

  update yx_base_down_account a
     set a.inner_balance = a.inner_balance - v_draw_amount
   where a.account_id = v_down_account_id;

  if (sql%rowcount = 0) then
    return pkg_error_code.data_error;
  end if;
end if;

--2.2 创建下游手工记录添加
insert into yx_trade_down_manual
  (record_id,
   channel_no,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   real_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_down_fund_id,
   v_channel_no,
   v_down_account_id,
   0,
   pkg_down_manual_change_type.trade_revise,
   v_draw_amount,
   v_real_amount,
   sysdate,
   l_inner_balance - v_draw_amount,
   v_create_user,
   v_memo);

if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;
--2.3.下游资金变动添加(通过trade_order_no关联上游资金变动)
insert into yx_trade_down_fund
  (record_id,
   yx_order_id,
   order_source,
   trade_order_no,
   down_channel_no,
   down_account_id,
   change_time,
   change_type,
   pay_amount,
   change_amount,
   balance,
   memo)
values
  (l_down_fund_id,
   0,
   0,
   'SARV' || l_up_fund_id,
   v_channel_no,
   v_down_account_id,
   sysdate,
   pkg_down_fund_change_type.debit_amount,
   0,
   v_draw_amount,
   l_inner_balance - v_draw_amount,
   v_memo);
if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

--3 =========================创建上游资金变动==============================
--3.1 上游资金变动（上游下游公司相同避免结算)
insert into yx_trade_up_fund
  (record_id,
   down_channel_no,
   down_account_id,
   down_company_id,
   up_channel_no,
   up_account_id,
   up_company_id,
   down_draw_amount,
   down_real_amount,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   business_type,
   balance,
   memo)
values
  (l_up_fund_id,
   v_channel_no,
   v_down_account_id,
   v_down_company_id,
   0,
   0,
   v_down_company_id,
   v_draw_amount,
   v_real_amount,
   0,
   0,
   0,
   0,
   0,
   sysdate,
   pkg_up_fund_change_type.debit_amount,
   v_business_type,
   l_inner_balance - v_draw_amount,
   v_memo);
if (sql%rowcount = 0) then
  return pkg_error_code.data_error;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_DOWN_RV_TRADE_CK
prompt ==============================================
prompt
create or replace function fd_account.yx_f_manual_down_rv_trade_ck(v_channel_no      in varchar2,
                                                        v_draw_amount     in number,
                                                        v_real_amount     in number,
                                                        v_down_account_id out number,
                                                        v_down_company_id out number)
  return varchar2 is
begin
  --2.检查渠道
  select a.account_id, t.company_id
    into v_down_account_id, v_down_company_id
    from yx_base_down_channel t
   inner join yx_base_down_account a on t.account_id = a.account_id
   where t.channel_no = v_channel_no
     and rownum <= 1;

  return pkg_error_code.success;
exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_RV_TRADE
prompt ======================================
prompt
create or replace function fd_account.yx_f_manual_rv_trade(v_order_source     in number, --订单来源系统编号
                                                v_up_channel_no    in varchar2, --上游
                                                v_up_draw_face     in number,
                                                v_up_account_id    in number,
                                                l_up_company_id    in number,
                                                v_up_draw_amount   in number,
                                                v_up_real_amount   in number,
                                                v_up_service_fee   in number,
                                                v_down_channel_no  in varchar2, --下游
                                                v_down_draw_face   in number,
                                                v_down_account_id  in number,
                                                l_down_company_id  in number,
                                                v_down_draw_amount in number,
                                                v_down_real_amount in number,
                                                v_down_hand_fee    in number,
                                                v_down_coupon      in number,
                                                v_agent_id         in varchar2, --代理人
                                                v_agency_fee       in number,
                                                v_business_type    in number,
                                                v_create_user      in varchar2,
                                                v_issue            in number,
                                                v_memo             in varchar2)
  return varchar2 is
  l_inner_balance      number;
  l_down_inner_balance number;
  l_agent_balance      number;
  l_fund_id            number;
  l_down_fund_id       number;
  l_agent_fund_id      number;

  l_service_fee     number;
  l_up_account_id   number;
  l_down_account_id number;

  l_change_type_debit number;
  -------------------------------------------------------
  --功能：交易平账，上游，下游，代理人，服务费，代理费，手续费，推广成本。（发行服务与一般扣款一致）
  --创建人：周荣省
  --创建时间：2019-06-13
  -------------------------------------------------------
begin
  if v_issue = 1 then
    l_change_type_debit := pkg_up_fund_change_type.debit_issue;
  else
    l_change_type_debit := pkg_up_fund_change_type.debit_amount;
  end if;
  --获取上游资金变动序列号
  select seq_yxtradedownfund_auto_id.nextval,
         seq_yxtradeupfund_auto_id.nextval,
         seq_yxtradeagentfund_auto_id.nextval
    into l_down_fund_id, l_fund_id, l_agent_fund_id
    from dual;
  if v_issue = 0 then
    l_up_account_id   := v_up_account_id;
    l_down_account_id := v_down_account_id;
  else
    l_up_account_id   := 0;
    l_down_account_id := 0;
  end if;
  --========================1. 上游。锁账户,修改账户金额===========================
  if (v_up_draw_amount = 0) then
    if v_issue = 0 then
      select a.inner_balance
        into l_inner_balance
        from yx_base_up_account a
       where a.account_id = v_up_account_id;
    else
      select t.issue_balance
        into l_inner_balance
        from yx_base_up_channel t
       where t.channel_no = v_up_channel_no;
    end if;
  else
    if v_issue = 0 then
      --普通平账
      select a.inner_balance
        into l_inner_balance
        from yx_base_up_account a
       where a.account_id = v_up_account_id
         for update;
    
      update yx_base_up_account t
         set t.inner_balance = t.inner_balance - v_up_draw_amount
       where t.account_id = v_up_account_id;
    
      if (sql%rowcount = 0) then
        return pkg_error_code.data_error;
      end if;
    elsif v_issue = 1 then
      --发行平账
      select t.issue_balance
        into l_inner_balance
        from yx_base_up_channel t
       where t.channel_no = v_up_channel_no
         for update;
      update yx_base_up_channel t
         set t.issue_balance = t.issue_balance - v_up_draw_amount
       where t.channel_no = v_up_channel_no;
    else
      return pkg_error_code.data_error;
    end if;
  end if;
  --==================2.锁服务费，修改服务费账户=========================
  if v_up_service_fee != 0 then
    --2.1锁渠道
    select t.service_balance
      into l_service_fee
      from yx_base_up_channel t
     where t.channel_no = v_up_channel_no
       for update;
  
    --2.2 修改渠道数据
    update yx_base_up_channel t
       set t.service_balance = t.service_balance + v_up_service_fee
     where t.channel_no = v_up_channel_no;
  
    if (sql%rowcount = 0) then
      return pkg_error_code.data_error;
    end if;
    insert into yx_trade_up_manual
      (record_id,
       channel_no,
       account_id,
       has_adjust,
       add_type,
       change_amount,
       real_amount,
       change_time,
       balance,
       create_user,
       memo)
    values
      (l_fund_id,
       v_up_channel_no,
       0,
       0,
       pkg_up_manual_change_type.service_revise,
       v_up_service_fee,
       v_up_service_fee,
       sysdate,
       l_service_fee + v_up_service_fee,
       v_create_user,
       v_memo);
  
    if (sql%rowcount = 0) then
      return pkg_error_code.data_error;
    end if;
  end if;
  --===================3.锁下游，修改账户余额(包括手续费和推广成本)============================
  if v_down_draw_amount = 0 and v_down_hand_fee = 0 and v_down_coupon = 0 then
    if v_issue = 0 then
      select a.inner_balance
        into l_down_inner_balance
        from yx_base_down_account a
       where a.account_id = l_down_account_id;
    else
      select t.issue_balance
        into l_down_inner_balance
        from yx_base_down_channel t
       where t.channel_no = v_down_channel_no;
    end if;
  else
    if v_issue = 0 then
      --3.1普通平账
      select a.inner_balance
        into l_down_inner_balance
        from yx_base_down_account a
       where a.account_id = l_down_account_id
         for update;
    
      update yx_base_down_account a
         set a.inner_balance = a.inner_balance - v_down_draw_amount
       where a.account_id = l_down_account_id;
    
      if (sql%rowcount = 0) then
        return pkg_error_code.data_error;
      end if;
    else
      --3.2发行平账
      select t.issue_balance
        into l_down_inner_balance
        from yx_base_down_channel t
       where t.channel_no = v_down_channel_no
         for update;
      update yx_base_down_channel t
         set t.issue_balance = t.issue_balance - v_down_draw_amount
       where t.channel_no = v_down_channel_no;
      if (sql%rowcount = 0) then
        return pkg_error_code.data_error;
      end if;
    end if;
    --3.3添加下游资金变动
    insert into yx_trade_down_fund
      (record_id,
       yx_order_id,
       order_source,
       trade_order_no,
       down_channel_no,
       down_account_id,
       change_time,
       change_type,
       pay_amount,
       change_amount,
       balance,
       handling_fee,
       coupon_amount,
       memo)
    values
      (l_down_fund_id,
       0,
       v_order_source,
       'SARV' || l_fund_id,
       v_down_channel_no,
       l_down_account_id,
       sysdate,
       l_change_type_debit,
       v_down_draw_amount,
       v_down_draw_amount - v_down_hand_fee,
       l_down_inner_balance - v_down_draw_amount,
       v_down_hand_fee,
       v_down_coupon,
       v_memo);
    if (sql%rowcount = 0) then
      return pkg_error_code.data_error;
    end if;
  end if;
  --================4.代理人，锁账户，修改账户余额==================
  if v_agent_id is not null and v_agent_id != '0' and v_agency_fee != 0 then
    --4.1 锁代理人,修改代理人余额
    select a.balance
      into l_agent_balance
      from yx_base_agent a
     where a.agent_id = v_agent_id
       for update;
  
    update yx_base_agent t
       set t.balance = t.balance + v_agency_fee
     where t.agent_id = v_agent_id;
  
    if (sql%rowcount = 0) then
      return pkg_error_code.data_error;
    end if;
    --4.2创建代理人资金变动
    insert into yx_trade_agent_fund
      (record_id,
       up_fund_id,
       down_channel_no,
       up_channel_no,
       order_source,
       agent_id,
       change_amount,
       balance,
       change_type,
       change_time,
       business_type)
    values
      (l_agent_fund_id,
       l_fund_id,
       v_down_channel_no,
       v_up_channel_no,
       v_order_source,
       v_agent_id,
       v_agency_fee,
       l_agent_balance + v_agency_fee,
       pkg_agent_fund_change_type.debit_amount,
       sysdate,
       v_business_type);
  
    if (sql%rowcount = 0) then
      return pkg_error_code.data_error;
    end if;
  end if;

  --==================5.添加上游资金变动=================================
  insert into yx_trade_up_fund
    (record_id,
     yx_order_id,
     order_source,
     down_channel_no,
     down_account_id,
     down_company_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     agent_id,
     down_draw_unit,
     down_draw_face,
     down_draw_amount,
     down_real_amount,
     income_amount,
     dowm_handling_fee,
     down_agency_fee,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     up_servie_fee,
     change_time,
     change_type,
     business_type,
     balance,
     memo,
     coupon_amount)
  values
    (l_fund_id,
     0,
     v_order_source,
     v_down_channel_no,
     l_down_account_id,
     l_down_company_id,
     v_up_channel_no,
     l_up_account_id,
     l_up_company_id,
     v_agent_id,
     v_down_draw_face,
     v_down_draw_face,
     v_down_draw_amount,
     v_down_real_amount,
     0,
     v_down_hand_fee,
     v_agency_fee,
     v_up_draw_face,
     v_up_draw_face,
     v_up_draw_amount,
     v_up_real_amount,
     v_up_service_fee,
     sysdate,
     l_change_type_debit,
     v_business_type,
     (l_inner_balance - v_up_draw_amount),
     v_memo,
     v_down_coupon);
  if (sql%rowcount = 0) then
    return pkg_error_code.data_error;
  end if;

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_ADD
prompt ====================================
prompt
create or replace function fd_account.yx_f_manual_up_add(v_up_channel_no   in varchar2, ---上有渠道编号
                                              v_up_company_id   in number, ---上游渠道公司编号
                                              v_account_id      in number, ---上游账户编号
                                              v_bank_account_id in number, ---实体账户编号
                                              v_service_fee     in number, ---手续费
                                              v_amount          in number, ---加款金额
                                              v_create_user     in varchar2, ---创建人
                                              v_memo            in varchar2, ---备注
                                              v_account_name    in varchar2, ---账户名称
                                              v_channel_name    in varchar2 ---渠道名称
                                              )

 return varchar2 is
  /*
         功能介绍:上游加款
         创建人:邓孝辉
         创建时间:2019/3/19
  */
  l_bank_balance    number;
  l_bank_company_id number;
  l_fund_id         number;
  l_bank_batch_id   number;
  l_inner_balance   number;
begin
  --===============1.锁实体账户获取参数==========================================
  --=========[锁实体卡账户、检查实体卡余额、获取seq]=======
  ---1.1 锁实体卡账户
  select t.balance, t.company_id
    into l_bank_balance, l_bank_company_id
    from fd_bank_account_info t
   where t.account_id = v_bank_account_id
     for update;

  ---1.2 检查相关金额（银行余额需大于加款金额）
  if (l_bank_balance < v_amount + v_service_fee) then
    return pkg_error_code.balance_low;
  end if;

  ---1.3 获取资金变动主键
  ---手续费手费资金变动编号，小于人工加款的资金变动编号
  select seq_yxtradeupfund_auto_id.nextval into l_fund_id from dual;
  ---获取银行卡加款变动统一流水号(用于红冲查询)
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

  --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡加款的资金变动]===
  ---2.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance - v_amount - v_service_fee),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

  ---2.2 添加实体卡加款资金变动 
  if (v_service_fee > 0) then
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
    values
      (seq_bank_cash_fund_id.nextval,
       v_bank_account_id,
       l_bank_company_id,
       v_service_fee,
       (l_bank_balance - v_service_fee),
       sysdate,
       v_create_user,
       pkg_bank_fund_change_type.minus_amount,
       pkg_bank_use_type.service_fee,
       v_channel_name,
       v_account_name,
       v_memo,
       l_bank_batch_id);
  end if;
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company_id,
     v_amount,
     (l_bank_balance - v_amount - v_service_fee),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.minus_amount,
     pkg_bank_use_type.up_add,
     v_channel_name,
     v_account_name,
     v_memo,
     l_bank_batch_id);

  --==============3.更新渠道账户==================================================
  --===[锁渠道账户、更新渠道账户余额、添加上游手工加款记录、添加上游资金变动]=====

  ---3.1 锁渠道账户
  select a.inner_balance
    into l_inner_balance
    from yx_base_up_account a
   where a.account_id = v_account_id
     for update;

  ---3.2 更新渠道账户余额
  update yx_base_up_account t
     set t.inner_balance  = t.inner_balance + v_amount,
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_account_id;

  ---3.3 添加上游手工加款记录
  insert into yx_trade_up_manual
    (record_id,
     channel_no,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     bank_fund_id)
  values
    (l_fund_id,
     v_up_channel_no,
     v_account_id,
     0,
     1,
     pkg_up_manual_change_type.add_amount,
     v_amount,
     sysdate,
     l_inner_balance + v_amount,
     v_create_user,
     v_memo,
     l_bank_batch_id);

  ---3.4 添加上游资金变动
  insert into yx_trade_up_fund
    (record_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     up_servie_fee,
     change_time,
     change_type,
     balance,
     memo,
     out_change_time)
  values
    (l_fund_id,
     v_up_channel_no,
     v_account_id,
     v_up_company_id,
     0,
     0,
     v_amount,
     v_amount,
     0,
     sysdate,
     pkg_up_fund_change_type.add_amount,
     (l_inner_balance + v_amount),
     v_memo,
     sysdate);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_ADD_CHECK
prompt ==========================================
prompt
create or replace function fd_account.yx_f_manual_up_add_check(v_up_channel_no    in  varchar2,  ---上游渠道编号
                                                    v_bank_account_id  in  number,    ---实体卡账户编号
                                                    v_service_fee      in  number,    ---手续费
                                                    v_amount           in  number,    ---加款金额
                                                    v_account_id       out number,    ---账户编号
                                                    v_account_name     out varchar2,  ---账户名称
                                                    v_channel_name     out varchar2,  ---渠道名称
                                                    v_up_company_id    out number     ---上游渠道公司编号
                                                    )

       return varchar2 is
       /*
              功能介绍:上游加款校验
              创建人:邓孝辉
              创建时间:2019/3/19
       */
       l_bank_count        number;
       l_bank_balance      number;
       l_company_id        number;
       l_bank_company_id   number;
begin
       --1. 校验金额
       if(v_amount<=0 or v_service_fee<0)then
        return pkg_error_code.amount_error;
       end if;

       --2. 检查上游账户信息
       select c.company_id,c.channel_name,c.account_id,a.account_name,c.company_id
         into v_up_company_id,v_channel_name,v_account_id,v_account_name,l_company_id
        from yx_base_up_channel c
        inner join yx_base_up_account a on c.account_id=a.account_id
        where c.channel_no=v_up_channel_no;

       ---3. 检查实体账户信息
       select count(1),max(t.balance),max(t.company_id)
         into l_bank_count,l_bank_balance,l_bank_company_id
        from fd_bank_account_info t
        where t.account_id=v_bank_account_id;


        if(l_bank_count=0) then
         return pkg_error_code.bank_account_id_error;
        end if;
        
        if(l_bank_company_id!=l_company_id) then
         return pkg_error_code.company_error;
        end if;
        
        if(l_bank_balance<v_amount+v_service_fee) then
         return pkg_error_code.balance_low;
        end if;

        return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_DRAW
prompt =====================================
prompt
create or replace function fd_account.yx_f_manual_up_draw(v_up_channel_no     in varchar2,   ---上有渠道编号
                                               v_up_company_id     in number,     ---上游渠道公司编号
                                               v_account_id        in number,     ---上游账户编号
                                               v_bank_account_id   in number,     ---实体账户编号
                                               v_amount            in number,     ---加款金额
                                               v_create_user       in varchar2,   ---创建人
                                               v_memo              in varchar2,   ---备注
                                               v_account_name      in varchar2,   ---账户名称
                                               v_channel_name      in varchar2    ---渠道名称
                                               )

       return varchar2 is
       /*
              功能介绍:上游人工扣款
              创建人:邓孝辉
              创建时间:2019/3/19
       */
  l_bank_balance       number;
  l_bank_company_id    number;
  l_fund_id            number;
  l_bank_batch_id      number;
  l_inner_balance      number;
begin
  --===============1.锁实体账户获取参数==========================================
    --=========[锁实体卡账户、检查实体卡余额、获取seq]=======
      ---1.1 锁实体卡账户
  select t.balance, t.company_id
    into l_bank_balance, l_bank_company_id
    from fd_bank_account_info t
   where t.account_id = v_bank_account_id
     for update;



  ---1.3 获取资金变动主键
  ---人工扣款的资金变动编号
  select seq_yxtradeupfund_auto_id.nextval into l_fund_id from dual;
  ---获取银行卡加款变动统一流水号
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

    --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡扣款的资金变动]===
  ---2.1 更新实体卡账户余额
    update fd_bank_account_info t
     set t.balance        = (l_bank_balance + v_amount),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

  ---2.2 添加实体卡扣款资金变动
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
    values
      (seq_bank_cash_fund_id.nextval,
       v_bank_account_id,
       l_bank_company_id,
       v_amount,
       (l_bank_balance + v_amount),
       sysdate,
       v_create_user,
       pkg_bank_fund_change_type.add_amount,
       pkg_bank_use_type.up_draw,
       v_channel_name,
       v_account_name,
       v_memo,
       l_bank_batch_id);

   --==============3.更新渠道账户==================================================
  --===[锁渠道账户、更新渠道账户余额、添加上游手工扣款记录、添加上游资金变动]=====

  ---3.1 锁渠道账户
  select a.inner_balance
    into l_inner_balance
   from yx_base_up_account a
   where a.account_id=v_account_id
   for update;

   ---3.2 更新渠道账户余额
   update yx_base_up_account t
      set t.inner_balance  = t.inner_balance - v_amount,
          t.last_edit_user = v_create_user,
          t.last_edit_time =sysdate
     where t.account_id = v_account_id;

   ---3.3 添加上游手工扣款记录
      insert into yx_trade_up_manual
        (record_id,
         channel_no,
         account_id,
         adjust_id,
         has_adjust,
         add_type,
         change_amount,
         change_time,
         balance,
         create_user,
         memo,
         bank_fund_id)
      values
        (l_fund_id,
         v_up_channel_no,
         v_account_id,
         0,
         1,
         pkg_up_manual_change_type.draw_amount,
         v_amount,
         sysdate,
         l_inner_balance-v_amount,
         v_create_user,
         v_memo,
         l_bank_batch_id);


   ---3.4 添加上游资金变动
      insert into yx_trade_up_fund
        (record_id,
         up_channel_no,
         up_account_id,
         up_company_id,
         up_draw_unit,
         up_draw_face,
         up_draw_amount,
         up_real_amount,
         up_servie_fee,
         change_time,
         change_type,
         balance,
         memo,
         out_change_time)
      values
        (l_fund_id,
         v_up_channel_no,
         v_account_id,
         v_up_company_id,
         0,
         0,
         v_amount,
         v_amount,
         0,
         sysdate,
         pkg_up_fund_change_type.draw_amount,
         (l_inner_balance - v_amount),
         v_memo,
         sysdate);

   return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_DRAW_CHECK
prompt ===========================================
prompt
create or replace function fd_account.yx_f_manual_up_draw_check(v_up_channel_no    in  varchar2,  ---上游渠道编号
                                                    v_bank_account_id  in  number,    ---实体卡账户编号
                                                    v_amount           in  number,    ---加款金额
                                                    v_account_id       out number,    ---账户编号
                                                    v_account_name     out varchar2,  ---账户名称
                                                    v_channel_name     out varchar2,  ---渠道名称
                                                    v_up_company_id    out number     ---上游渠道公司编号
                                                    )

       return varchar2 is
       /*
              功能介绍:上游加款校验
              创建人:邓孝辉
              创建时间:2019/3/19
       */
       l_bank_count       number;
       l_company_id       number;
       l_bank_company_id  number;
begin
       --1. 校验金额
       if(v_amount<=0)then
        return pkg_error_code.amount_error;
       end if;

       --2. 检查上游账户信息
       select c.company_id,c.channel_name,c.account_id,a.account_name,c.company_id
         into v_up_company_id,v_channel_name,v_account_id,v_account_name,l_company_id
        from yx_base_up_channel c
        inner join yx_base_up_account a on c.account_id=a.account_id
        where c.channel_no=v_up_channel_no;

       ---3. 检查实体账户信息
       select count(1),max(t.company_id)
         into l_bank_count,l_bank_company_id
        from fd_bank_account_info t
        where t.account_id=v_bank_account_id;

        if(l_bank_count=0) then
         return pkg_error_code.bank_account_id_error;
        end if;
        
        if(l_company_id!=l_bank_company_id) then
         return pkg_error_code.company_error;
        end if;

        return pkg_error_code.success;

exception
  when others then
    return pkg_error_code.channel_error;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_INNER_CHECK
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_up_inner_check(v_out_channel_no  in  varchar2, --转出渠道
                                                        v_in_channel_no   in  varchar2, --转入渠道
                                                        v_amount          in  number,   --转出金额
                                                        v_out_account_id  out number,
                                                        v_in_account_id   out number)
       return varchar2 is
    l_out_balance      number;
    l_out_company_id    number;
    l_out_system       number;
    l_in_company_id     number;
    l_in_system        number;
begin

--- 校验转出金额是否正确,公司及系统是否正确
select a.inner_balance, t.company_id, t.source_system_id,t.account_id
  into l_out_balance, l_out_company_id, l_out_system,v_out_account_id
  from yx_base_up_channel t
  left join yx_base_up_account a
    on a.account_id = t.account_id
 where t.channel_no = v_out_channel_no;

select i.company_id, i.source_system_id,i.account_id
  into l_in_company_id, l_in_system,v_in_account_id
  from yx_base_up_channel i
 where i.channel_no = v_in_channel_no;

if (v_amount <= 0 or v_amount > l_out_balance) then
  return pkg_error_code.amount_error;
end if;

if (l_out_company_id != l_in_company_id or l_out_system != l_in_system or
   v_out_channel_no = v_in_channel_no or
   v_out_account_id = v_in_account_id) then
  return pkg_error_code.channel_error;
end if;

return pkg_error_code.success;
exception when others then return pkg_error_code.channel_or_account_error; end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_INNER_TSFER
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_up_inner_tsfer(v_out_channel_no  in  varchar2,
                                                        v_in_channel_no   in  varchar2,
                                                        v_amount          in  number,
                                                        v_hander_user     in  varchar2,
                                                        v_memo            in  varchar2,
                                                        v_out_account_id  in  number,
                                                        v_in_account_id   in  number)
    return varchar2 is
    -------------------上游渠道内部转账--------------------
    l_out_balance            number;
    l_out_fund_id            number;

    l_in_balance             number;
    l_in_fund_id             number;
begin
  --===========================1.出账渠道操作=============================

--1.1 锁出账渠道账户
 select oa.inner_balance
   into l_out_balance
  from yx_base_up_account oa
  where oa.account_id=v_out_account_id;


--1.2 修改出账渠道余额
 update yx_base_up_account oa
    set oa.inner_balance=oa.inner_balance-v_amount
   where oa.account_id=v_out_account_id
    and oa.inner_balance>=v_amount;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

select seq_yxtradeupfund_auto_id.nextval into l_out_fund_id from dual;

--1.3 添加出账账户提款记录
insert into yx_trade_up_manual
  (record_id,
   channel_no,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_out_fund_id,
   v_out_channel_no,
   v_out_account_id,
   0,
   pkg_up_manual_change_type.draw_amount,
   v_amount,
   sysdate,
   l_out_balance-v_amount,
   v_hander_user,
   v_memo);


if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--1.4 添加出账账户资金变动
insert into yx_trade_up_fund
  (record_id,
   up_channel_no,
   up_account_id,
   up_company_id,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   balance,
   memo)
select l_out_fund_id,
       v_out_channel_no,
       v_out_account_id,
       oc.company_id,
       0,
       0,
       v_amount,
       v_amount,
       0,
       sysdate,
       pkg_up_fund_change_type.draw_amount,
       l_out_balance-v_amount,
       v_memo
   from yx_base_up_channel oc
   where oc.channel_no=v_out_channel_no;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--===========================2.入账渠道操作=============================
--2.1 锁入账渠道账户
select ia.inner_balance
  into l_in_balance
  from yx_base_up_account ia
  where ia.account_id=v_in_account_id;

--2.2 修改入账渠道余额
update yx_base_up_account ia
   set ia.inner_balance=ia.inner_balance+v_amount
   where ia.account_id=v_in_account_id;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

select seq_yxtradeupfund_auto_id.nextval into l_in_fund_id from dual;

--2.3 添加入账账户提款记录
insert into yx_trade_up_manual
  (record_id,
   channel_no,
   account_id,
   has_adjust,
   add_type,
   change_amount,
   change_time,
   balance,
   create_user,
   memo)
values
  (l_in_fund_id,
   v_in_channel_no,
   v_in_account_id,
   0,
   pkg_up_manual_change_type.add_amount,
   v_amount,
   sysdate,
   l_in_balance+v_amount,
   v_hander_user,
   v_memo);

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

--2.4 添加入账账户资金变动
insert into yx_trade_up_fund
  (record_id,
   up_channel_no,
   up_account_id,
   up_company_id,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   balance,
   memo)
select l_in_fund_id,
       v_in_channel_no,
       v_in_account_id,
       oc.company_id,
       0,
       0,
       v_amount,
       v_amount,
       0,
       sysdate,
       pkg_up_fund_change_type.add_amount,
       l_in_balance+v_amount,
       v_memo
   from yx_base_up_channel oc
   where oc.channel_no=v_in_channel_no;

if (sql%rowcount = 0) then
  rollback;
  return pkg_error_code.data_error;
end if;

return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_ISSUE_ADD
prompt ==========================================
prompt
create or replace function fd_account.yx_f_manual_up_issue_add(v_up_channel_no   in varchar2, ---上有渠道编号
                                                    v_up_company_id   in number, ---上游渠道公司编号
                                                    v_account_id      in number, ---上游账户编号
                                                    v_bank_account_id in number, ---实体账户编号
                                                    v_service_fee     in number, ---手续费
                                                    v_amount          in number, ---加款金额
                                                    v_create_user     in varchar2, ---创建人
                                                    v_memo            in varchar2, ---备注
                                                    v_account_name    in varchar2, ---账户名称
                                                    v_channel_name    in varchar2 ---渠道名称
                                                    )

 return varchar2 is
  ----------------------------------------------------
  --功能介绍:上游发行加款
  --创建人:周荣省
  --创建时间:2019-06-18
  ----------------------------------------------------
  l_bank_balance    number;
  l_bank_company_id number;
  l_fund_id         number;
  l_bank_batch_id   number;
  l_inner_balance   number;
begin
  --===============1.锁实体账户获取参数==========================================
  --=========[锁实体卡账户、检查实体卡余额、获取seq]=======
  ---1.1 锁实体卡账户
  select t.balance, t.company_id
    into l_bank_balance, l_bank_company_id
    from fd_bank_account_info t
   where t.account_id = v_bank_account_id
     for update;

  ---1.2 检查相关金额（银行余额需大于加款金额）
  if (l_bank_balance < v_amount + v_service_fee) then
    return pkg_error_code.balance_low;
  end if;

  ---1.3 获取资金变动主键
  ---手续费手费资金变动编号，小于人工加款的资金变动编号
  select seq_yxtradeupfund_auto_id.nextval into l_fund_id from dual;
  ---获取银行卡加款变动统一流水号(用于红冲查询)
  select seq_bank_service_id.nextval into l_bank_batch_id from dual;

  --====================2.实体卡账户=====================================
  --==[更新实体卡账户余额、添加实体卡加款的资金变动]===
  ---2.1 更新实体卡账户余额
  update fd_bank_account_info t
     set t.balance        = (l_bank_balance - v_amount - v_service_fee),
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.account_id = v_bank_account_id;

  ---2.2 添加实体卡加款资金变动
  if (v_service_fee > 0) then
    insert into fd_bank_cash_fund
      (record_id,
       account_id,
       company_id,
       change_amount,
       balance,
       change_time,
       created_user,
       change_type,
       use_type,
       link_channel_name,
       link_account_name,
       remark,
       service_id)
    values
      (seq_bank_cash_fund_id.nextval,
       v_bank_account_id,
       l_bank_company_id,
       v_service_fee,
       (l_bank_balance - v_service_fee),
       sysdate,
       v_create_user,
       pkg_bank_fund_change_type.minus_amount,
       pkg_bank_use_type.service_fee,
       v_channel_name,
       v_account_name,
       v_memo,
       l_bank_batch_id);
  end if;
  insert into fd_bank_cash_fund
    (record_id,
     account_id,
     company_id,
     change_amount,
     balance,
     change_time,
     created_user,
     change_type,
     use_type,
     link_channel_name,
     link_account_name,
     remark,
     service_id)
  values
    (seq_bank_cash_fund_id.nextval,
     v_bank_account_id,
     l_bank_company_id,
     v_amount,
     (l_bank_balance - v_amount - v_service_fee),
     sysdate,
     v_create_user,
     pkg_bank_fund_change_type.minus_amount,
     pkg_bank_use_type.up_add,
     v_channel_name,
     v_account_name,
     v_memo,
     l_bank_batch_id);

  --==============3.更新渠道发行账户==================================================
  --===[锁渠道账户、更新渠道账户余额、添加上游手工加款记录、添加上游资金变动]=====

  ---3.1 锁渠道账户
  select a.issue_balance
    into l_inner_balance
    from yx_base_up_channel a
   where a.channel_no = v_up_channel_no
     for update;

  ---3.2 更新渠道账户余额
  update yx_base_up_channel t
     set t.issue_balance  = t.issue_balance + v_amount,
         t.last_edit_user = v_create_user,
         t.last_edit_time = sysdate
   where t.channel_no = v_up_channel_no;

  ---3.3 添加上游手工加款记录
  insert into yx_trade_up_manual
    (record_id,
     channel_no,
     account_id,
     adjust_id,
     has_adjust,
     add_type,
     change_amount,
     change_time,
     balance,
     create_user,
     memo,
     bank_fund_id)
  values
    (l_fund_id,
     v_up_channel_no,
     v_account_id,
     0,
     1,
     pkg_up_manual_change_type.issue_add,
     v_amount,
     sysdate,
     l_inner_balance + v_amount,
     v_create_user,
     v_memo,
     l_bank_batch_id);

  ---3.4 添加上游资金变动
  insert into yx_trade_up_fund
    (record_id,
     up_channel_no,
     up_account_id,
     up_company_id,
     up_draw_unit,
     up_draw_face,
     up_draw_amount,
     up_real_amount,
     up_servie_fee,
     change_time,
     change_type,
     balance,
     memo,
     out_change_time)
  values
    (l_fund_id,
     v_up_channel_no,
     v_account_id,
     v_up_company_id,
     0,
     0,
     v_amount,
     v_amount,
     0,
     sysdate,
     pkg_up_fund_change_type.add_issue,
     (l_inner_balance + v_amount),
     v_memo,
     sysdate);

  return pkg_error_code.success;
end;
/

prompt
prompt Creating function YX_F_MANUAL_UP_REV_SERVICE
prompt ============================================
prompt
create or replace function fd_account.yx_f_manual_up_rev_service(v_channel_no    in    varchar2,
                                                      v_company_id    in    number,
                                                      v_business_type in    number,
                                                      v_service_fee   in    number,
                                                      v_create_user   in    varchar2,
