 end if;

--4. 创建资金变动
insert into yx_trade_up_fund
  (record_id,
   up_channel_no,
   up_account_id,
   up_company_id,
   up_draw_unit,
   up_draw_face,
   up_draw_amount,
   up_real_amount,
   up_servie_fee,
   change_time,
   change_type,
   business_type,
   balance,
   memo)
values
  (l_fund_id,
   v_channel_no,
   0,
   v_company_id,
   0,
   0,
   0,
   0,
   v_service_fee,
   sysdate,
   pkg_up_fund_change_type.debit_amount,
   v_business_type,
   0,
   v_memo);

if(sql%rowcount=0) then
 return pkg_error_code.data_error;
end if;
  return pkg_error_code.success;
end;
/

