  ---------------收卡系统下游预付结算--------------
  l_channel_name varchar2(100);
  l_account_name varchar2(100);
begin
  ---1.检查金额、收卡下游是否正确
  v_result := dk_f_sup_down_settle_pre_ck_t(v_down_channel_no,
