                                            v_record_no,
                                            v_settle_amount,
                                            v_service_fee,
                                            l_channel_name,
                                            l_account_name);
  if v_result != pkg_error_code.success then
    rollback;
    return;
  end if;

  ---2.下游渠道加款
  v_result := dk_f_sup_down_settle_t(v_down_channel_no,
                                     v_down_account_id,
