                   item.account_id,
                   '-',
                   t.balance,
                   item.account_name,
                   '-',
                   0,
                   item.status,
                   item.company_id
              from fd_trade_co_imp_fund t
             where t.change_id = l_max_change_id;
        else
          ---4.没有资金变动，获取前一天的零点余额
          insert into fd_report_zero_balance
            (record_id,
             record_date,
             created_time,
             account_type,
             account_id,
             channel_no,
             balance,
             account_name,
             channel_name,
             channel_status,
             account_status,
             company_id)
            select seq_report_zero_balance_id.nextval,
                   l_report_date,
                   sysdate,
                   pkg_report_zero_acc_type.imp_account,
                   item.account_id,
                   '-',
                   tab.zero_balance,
                   item.account_name,
                   '-',
                   0,
                   item.status,
                   item.company_id
              from (select nvl(max(t.balance), item.balance) zero_balance
                      from fd_report_zero_balance t
                     where t.record_date = (l_report_date - 1)
                       and t.account_type =
                           pkg_report_zero_acc_type.imp_account
                       and t.account_id = item.account_id
                       and rownum <= 1) tab;

        end if;
      end if;
      commit;
    exception
      when others then
        rollback;
        fd_p_write_log('fd_p_auto_zero_imp_get', sqlerrm, 1);
    end;
  end loop;
end;
