prompt
create or replace procedure fd_account.fd_p_manual_rv_apply_add(v_channel_no    in varchar2, ---渠道编号
                                                     v_channel_type  in number, --渠道类型 1：上游 2：下游
                                                     v_business_type in number, --业务类型
