                                  v_create_user,
                                  v_partner_account,
                                  v_memo,
                                  l_account_name,
                                  v_remark_name,
                                  l_channel_name);
  if (v_result != pkg_error_code.success) then
    rollback;
    return;
  end if;

  ---4.空充系统对应下游和关联的18上游关联提款
  v_result := fd_f_manual_up_bind_draw(v_up_channel_no,
                                       l_company_id,
                                       v_amount,
                                       v_create_user,
                                       v_memo);
  if (v_result != pkg_error_code.success) then
    rollback;
    return;
  end if;

  commit;

exception
  when others then
    rollback;
    v_result := pkg_error_code.system_busy;
    fd_p_write_log('fd_p_manual_up_draw', sqlerrm, 1);
end;
/

