begin
  ---1.获取记账系统业务类型
  l_fd_business_type := fd_f_trade_business_type_get(v_order_source,
                                                     v_business_type);

  ---2.检查上游渠道、账户及其关联情况
  v_result := fd_f_trade_pay_account_get(v_order_source,
                                         v_up_channel_no,
                                         l_up_account_id);
