    return;
  end if;

  ---3.历史库获取订单
  v_result := fd_f_trade_pay_hs_move2(v_order_source,
                                      v_trade_order_no,
                                      v_order_time);
  if (v_result != pkg_error_code.success) then
    rollback;
    return;
  else
    commit;
  end if;

  ---4.检查订单是否存在，不存在则创建订单
  v_result := fd_f_trade_order_create_ii(v_order_source,
                                         v_down_channel_no,
                                         v_trade_order_no,
                                         v_ext_order_no,
                                         v_recharge_account_no,
                                         l_fd_business_type,
                                         v_carrier_no,
                                         v_province_no,
                                         v_city_no,
                                         v_down_order_face,
                                         v_down_order_unit,
