prompt
prompt Creating procedure ZREPORT_COMMI_PROFIT_ST_HS
prompt =============================================
prompt
create or replace procedure fd_account.zreport_commi_profit_st_hs(v_collect_date in varchar2 default null) as
  --------------------收集上游资金变动结算信息---------------------
  l_collect_date date := to_date(v_collect_date, 'yyyymmddhh24miss');
  --l_collect_date    date := trunc(to_date('20160602000000',
  --                                        'yyyymmddhh24miss')) - 1;
  l_statistics_type   number := 0; --数据出现错误，统计类型为0
  l_wait_count        number := 0;
  l_start_time        date := sysdate;
  l_down_company_name varchar2(128);
  l_up_company_name   varchar2(128);
begin
  if l_collect_date is null then
    l_collect_date := trunc(sysdate) - 1;
  end if;

  ---0.未结算数据检查
  select count(1)
    into l_wait_count
    from fd_trade_up_fund_hs t
   where t.change_time >= l_collect_date
     and t.change_time < (l_collect_date + 1)
     and t.settle_status = pkg_settle_status.wait_settle
     and rownum <= 2;

  if l_wait_count = 0 then

    ---1.收集上游资金变动信息
    for item in (select u.down_company_id,
                        u.up_company_id,
                        u.bill_type,
                        u.business_type,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   u.up_draw_unit,
                                   0)) draw_unit,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   u.up_draw_face,
                                   0)) draw_face,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   decode(u.settle_amount,
                                          0,
                                          u.up_real_amount,
                                          u.settle_amount),
                                   0)) draw_amount,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   decode(u.settle_amount,
                                          0,
                                          u.up_real_amount,
                                          u.settle_amount),
                                   0)) real_amount,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   0,
                                   u.up_draw_unit)) unit_ref,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   0,
                                   u.up_draw_face)) face_ref,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   0,
                                   decode(u.settle_amount,
                                          0,
                                          u.up_real_amount,
                                          u.settle_amount))) amount_ref,
                        sum(decode(u.change_type,
                                   pkg_up_fund_change_type.debit_amount,
                                   0,
                                   decode(u.settle_amount,
                                          0,
                                          u.up_real_amount,
                                          u.settle_amount))) real_ref
                   from fd_trade_up_fund_hs u
                  where u.change_time >= l_collect_date
                    and u.change_time < (l_collect_date + 1)
                    and u.down_company_id != u.up_company_id
                    and u.down_company_id != 0
                    and u.up_company_id != 0
                    and (u.change_type =
                        pkg_up_fund_change_type.debit_amount or
                        u.change_type =
                        pkg_up_fund_change_type.refund_amount)
                  group by u.down_company_id,
                           u.up_company_id,
                           u.bill_type,
                           u.business_type) loop
      begin

        --2.计算统计类型
        l_statistics_type := 0;
        if item.bill_type = pkg_bill_type.back then

          l_statistics_type := pkg_statistics_type.FlowBag;
        else
          --***从业务类型与分类表中获取分类***
          select t.statistics_type
            into l_statistics_type
            from fd_system_business_statis_map t
           where t.local_business_type = item.business_type
             and rownum <= 1;
        end if;

        ---3.获取公司名称
        select t.sort_name
          into l_down_company_name
          from fd_base_company_info t
         where t.company_id = item.down_company_id;

        select t.sort_name
          into l_up_company_name
          from fd_base_company_info t
         where t.company_id = item.up_company_id;

        ---4.添加作为下游渠道的结算信息
        insert into fd_report_commi_profit_redo
          (record_id,
           company_id,
           channel_no,
           account_id,
           channel_type,
           business_type,
           bill_type,
           total_unit,
           total_face,
           draw_amount,
           real_amount,
           commission,
           profits,
           service_fee,
           refund_total_unit,
           refund_total_face,
           refund_draw_amount,
           refund_real_amount,
           refund_commission,
           refund_profits,
           refund_service_fee,
           up_many_face,
           up_many_amount,
           snap_time,
           statistics_type,
           channel_name,
           account_name)
        values
          (seq_report_commi_profit_id.nextval,
           item.up_company_id,
           '-',
           item.down_company_id,
           pkg_channel_type.settle_down,
           item.business_type,
           item.bill_type,
           item.draw_unit,
           item.draw_face,
           item.draw_amount,
           item.real_amount,
           0,
           0,
           0,
           item.unit_ref,
           item.face_ref,
           item.amount_ref,
           item.real_ref,
           0,
           0,
           0,
           0,
           0,
           l_collect_date,
           l_statistics_type,
           l_down_company_name,
           l_down_company_name);

        ---3.添加作为上游渠道的结算信息
        insert into fd_report_commi_profit_redo
          (record_id,
           company_id,
           channel_no,
           account_id,
           channel_type,
           business_type,
           bill_type,
           total_unit,
           total_face,
           draw_amount,
           real_amount,
           commission,
           profits,
           service_fee,
           refund_total_unit,
           refund_total_face,
           refund_draw_amount,
           refund_real_amount,
           refund_commission,
           refund_profits,
           refund_service_fee,
           up_many_face,
           up_many_amount,
           snap_time,
           statistics_type,
           channel_name,
           account_name)
        values
          (seq_report_commi_profit_id.nextval,
           item.down_company_id,
           '-',
           item.up_company_id,
           pkg_channel_type.settle_up,
           item.business_type,
           item.bill_type,
           item.draw_unit,
           item.draw_face,
           item.draw_amount,
           item.real_amount,
           0,
           0,
           0,
           item.unit_ref,
           item.face_ref,
           item.amount_ref,
           item.real_ref,
           0,
           0,
           0,
           0,
           0,
           l_collect_date,
           l_statistics_type,
           l_up_company_name,
           l_up_company_name);

        commit;

      exception
        when others then
          rollback;
          fd_p_write_log('fd_p_auto_commi_profit_settle', sqlerrm, 1);
      end;
    end loop;
  else
    insert into fd_system_warn
      (warn_id,
       uniq_id,
       warn_level,
       warn_type,
       title,
       warn_date,
       content,
       sync_status,
       key_word)
    values
      (seq_system_warn_id.nextval,
       'co_settle_fail',
       5,
       pkg_warn_into_type.settle_warn,
       '公司间交易未进行结算',
       sysdate,
       '公司间交易未进行结算',
       pkg_balance_sync_status.wait,
       '交易未结算');

    commit;
  end if;
  fd_p_proc_log('fd_p_auto_commi_profit_down', l_start_time, 'T', '');
exception
  when others then
    rollback;
    fd_p_write_log('fd_p_auto_commi_profit_settle', sqlerrm, 1);
end;
/

