mapper.select:
select
   bridge_info.bridge_length,
   bridge_info.bridge_name,
   bridge_info.bridge_no,
   bridge_info.create_time,
   bridge_info.cross_hole_style,
   bridge_info.direction,
   bridge_info.end_miles,
   bridge_info.flyover_category,
   bridge_info.hole_length,
   bridge_info.id,
   bridge_info.is_delete,
   bridge_info.line_id,
   bridge_info.long_chain,
   bridge_info.middle_miles,
   bridge_info.operator,
   bridge_info.remark,
   bridge_info.side,
   bridge_info.start_miles,
   bridge_info.status,
   bridge_info.track_division_id,
   bridge_info.track_weigth,
   bridge_info.update_time
form bridge_info


        bridge_info.bridge_length = #{bridgeLength}


        and bridge_info.bridge_no = #{bridgeNo}


        and bridge_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and bridge_info.cross_hole_style = #{crossHoleStyle}


        and bridge_info.direction = #{direction}


        and bridge_info.end_miles = #{endMiles}


        and bridge_info.flyover_category = #{flyoverCategory}


        and bridge_info.hole_length = #{holeLength}


        and bridge_info.is_delete = #{isDelete}


        and bridge_info.line_id = #{lineId}


        and bridge_info.long_chain = #{longChain}


        and bridge_info.middle_miles = #{middleMiles}


        and bridge_info.operator = #{operator}


        and bridge_info.side = #{side}


        and bridge_info.start_miles = #{startMiles}


        and bridge_info.status = #{status}


        and bridge_info.track_division_id = #{trackDivisionId}


        and bridge_info.track_weigth = #{trackWeigth}


        and bridge_info.update_time between #{updateTimeStart} and #{updateTimeEnd}
