mapper.select:
select
   bureau_info.bureau_code,
   bureau_info.bureau_name,
   bureau_info.create_time,
   bureau_info.id,
   bureau_info.input_code,
   bureau_info.is_delete,
   bureau_info.operator,
   bureau_info.update_time
form bureau_info


        bureau_info.bureau_code = #{bureauCode}


        and bureau_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and bureau_info.input_code = #{inputCode}


        and bureau_info.is_delete = #{isDelete}


        and bureau_info.operator = #{operator}


        and bureau_info.update_time between #{updateTimeStart} and #{updateTimeEnd}
