mapper.select:
select
   curve_info.average_speed,
   curve_info.create_time,
   curve_info.curve_direction,
   curve_info.curve_id,
   curve_info.curve_length,
   curve_info.curve_readius,
   curve_info.direction,
   curve_info.end_miles,
   curve_info.end_slow_length,
   curve_info.end_tangent_length,
   curve_info.is_delete,
   curve_info.line_id,
   curve_info.long_chain,
   curve_info.operator,
   curve_info.over_height,
   curve_info.remark,
   curve_info.side,
   curve_info.slope_rate,
   curve_info.start_miles,
   curve_info.start_slow_length,
   curve_info.start_tangent_length,
   curve_info.status,
   curve_info.steering_angle,
   curve_info.track_dis_type,
   curve_info.track_dis_wide,
   curve_info.track_division_id,
   curve_info.update_time
form curve_info 


        curve_info.average_speed = #{averageSpeed}


        and curve_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and curve_info.curve_direction = #{curveDirection}


        and curve_info.curve_length = #{curveLength}


        and curve_info.curve_readius = #{curveReadius}


        and curve_info.direction = #{direction}


        and curve_info.end_miles = #{endMiles}


        and curve_info.end_slow_length = #{endSlowLength}


        and curve_info.end_tangent_length = #{endTangentLength}


        and curve_info.is_delete = #{isDelete}


        and curve_info.line_id = #{lineId}


        and curve_info.long_chain = #{longChain}


        and curve_info.operator = #{operator}


        and curve_info.over_height = #{overHeight}


        and curve_info.side = #{side}


        and curve_info.slope_rate = #{slopeRate}


        and curve_info.start_miles = #{startMiles}


        and curve_info.start_slow_length = #{startSlowLength}


        and curve_info.start_tangent_length = #{startTangentLength}


        and curve_info.status = #{status}


        and curve_info.steering_angle = #{steeringAngle}


        and curve_info.track_dis_type = #{trackDisType}


        and curve_info.track_dis_wide = #{trackDisWide}


        and curve_info.track_division_id = #{trackDivisionId}


        and curve_info.update_time between #{updateTimeStart} and #{updateTimeEnd}
