mapper.select:
select
   deal_record.cmd_no,
   deal_record.create_time,
   deal_record.deal_remark,
   deal_record.deal_result,
   deal_record.deal_time,
   deal_record.delete_audit_status,
   deal_record.delete_time,
   deal_record.id,
   deal_record.is_delete,
   deal_record.operator,
   deal_record.order_detail_id,
   deal_record.update_time,
   deal_record.workarea_id,
   deal_record.workshop_info_id
form deal_record 


        deal_record.cmd_no = #{cmdNo}


        and deal_record.create_time between #{createTimeStart} and #{createTimeEnd}


        and deal_record.deal_result = #{dealResult}


        and deal_record.deal_time between #{dealTimeStart} and #{dealTimeEnd}


        and deal_record.delete_audit_status = #{deleteAuditStatus}


        and deal_record.delete_time between #{deleteTimeStart} and #{deleteTimeEnd}


        and deal_record.is_delete = #{isDelete}


        and deal_record.operator = #{operator}


        and deal_record.order_detail_id = #{orderDetailId}


        and deal_record.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and deal_record.workarea_id = #{workareaId}


        and deal_record.workshop_info_id = #{workshopInfoId}
