mapper.select:
select
   t.birthday,
   t.contact,
   t.create_time,
   t.edu_degree,
   t.gender,
   t.grade_gain_date,
   t.grade_id,
   t.id,
   t.id_card_no,
   t.is_delete,
   t.name,
   t.operator,
   t.position_id,
   t.qualification_audit_date,
   t.qualification_gain_date,
   t.qualification_grade,
   t.qualification_invalid_date,
   t.qualification_valid_time,
   t.remark,
   t.status,
   t.tec_category,
   t.update_time,
   t.workarea_id
form detect_staff_info_copy t


        t.birthday between #{birthdayStart} and #{birthdayEnd}


        and t.contact = #{contact}


        and t.create_time between #{createTimeStart} and #{createTimeEnd}


        and t.edu_degree = #{eduDegree}


        and t.gender = #{gender}


        and t.grade_gain_date between #{gradeGainDateStart} and #{gradeGainDateEnd}


        and t.grade_id = #{gradeId}


        and t.id_card_no = #{idCardNo}


        and t.is_delete = #{isDelete}


        and t.operator = #{operator}


        and t.position_id = #{positionId}


        and t.qualification_audit_date between #{qualificationAuditDateStart} and #{qualificationAuditDateEnd}


        and t.qualification_gain_date between #{qualificationGainDateStart} and #{qualificationGainDateEnd}


        and t.qualification_grade = #{qualificationGrade}


        and t.qualification_invalid_date between #{qualificationInvalidDateStart} and #{qualificationInvalidDateEnd}


        and t.qualification_valid_time = #{qualificationValidTime}


        and t.status = #{status}


        and t.tec_category = #{tecCategory}


        and t.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and t.workarea_id = #{workareaId}
