mapper.select:
select
   division_user.create_time,
   division_user.division_id,
   division_user.id,
   division_user.is_delete,
   division_user.operator,
   division_user.update_time,
   division_user.user_id
form division_user


        division_user.create_time between #{createTimeStart} and #{createTimeEnd}


        and division_user.division_id = #{divisionId}


        and division_user.is_delete = #{isDelete}


        and division_user.operator = #{operator}


        and division_user.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and division_user.user_id = #{userId}
