mapper.select:
select
   line_info.ballast_type,
   line_info.commissioning_date,
   line_info.create_time,
   line_info.distance,
   line_info.full_name,
   line_info.gauge_type,
   line_info.grade,
   line_info.id,
   line_info.input_code,
   line_info.is_delete,
   line_info.limit_grad,
   line_info.line_branches,
   line_info.line_code,
   line_info.line_num,
   line_info.min_radius_curve,
   line_info.nature,
   line_info.operate_type,
   line_info.operator,
   line_info.short_name,
   line_info.status,
   line_info.track_type,
   line_info.traction_type,
   line_info.update_time,
   line_info.year_volume
form line_info


        line_info.ballast_type = #{ballastType}


        and line_info.commissioning_date between #{commissioningDateStart} and #{commissioningDateEnd}


        and line_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and line_info.distance = #{distance}


        and line_info.gauge_type = #{gaugeType}


        and line_info.grade = #{grade}


        and line_info.input_code = #{inputCode}


        and line_info.is_delete = #{isDelete}


        and line_info.limit_grad = #{limitGrad}


        and line_info.line_branches = #{lineBranches}


        and line_info.line_code = #{lineCode}


        and line_info.line_num = #{lineNum}


        and line_info.min_radius_curve = #{minRadiusCurve}


        and line_info.nature = #{nature}


        and line_info.operate_type = #{operateType}


        and line_info.operator = #{operator}


        and line_info.status = #{status}


        and line_info.track_type = #{trackType}


        and line_info.traction_type = #{tractionType}


        and line_info.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and line_info.year_volume = #{yearVolume}
