mapper.select:
select
   station_info.create_time,
   station_info.full_name,
   station_info.id,
   station_info.input_code,
   station_info.is_delete,
   station_info.job_type,
   station_info.operator,
   station_info.short_name,
   station_info.station_code,
   station_info.station_grade,
   station_info.station_type,
   station_info.status,
   station_info.ternout_num,
   station_info.thigh_track_num,
   station_info.track_division_id,
   station_info.update_time
form station_info 


        station_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and station_info.input_code = #{inputCode}


        and station_info.is_delete = #{isDelete}


        and station_info.job_type = #{jobType}


        and station_info.operator = #{operator}


        and station_info.station_code = #{stationCode}


        and station_info.station_grade = #{stationGrade}


        and station_info.station_type = #{stationType}


        and station_info.status = #{status}


        and station_info.ternout_num = #{ternoutNum}


        and station_info.thigh_track_num = #{thighTrackNum}


        and station_info.track_division_id = #{trackDivisionId}


        and station_info.update_time between #{updateTimeStart} and #{updateTimeEnd}
