mapper.select:
select
   thigh_track_info.create_time,
   thigh_track_info.curve_radius,
   thigh_track_info.direction,
   thigh_track_info.grade_section_length,
   thigh_track_info.id,
   thigh_track_info.is_delete,
   thigh_track_info.line_id,
   thigh_track_info.max_gradient,
   thigh_track_info.operator,
   thigh_track_info.sort_num,
   thigh_track_info.station_id,
   thigh_track_info.status,
   thigh_track_info.thigh_track_length,
   thigh_track_info.thigh_track_no,
   thigh_track_info.thigh_track_type,
   thigh_track_info.tie_category,
   thigh_track_info.update_time,
   thigh_track_info.workarea_id
form thigh_track_info


        thigh_track_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and thigh_track_info.curve_radius = #{curveRadius}


        and thigh_track_info.direction = #{direction}


        and thigh_track_info.grade_section_length = #{gradeSectionLength}


        and thigh_track_info.is_delete = #{isDelete}


        and thigh_track_info.line_id = #{lineId}


        and thigh_track_info.max_gradient = #{maxGradient}


        and thigh_track_info.operator = #{operator}


        and thigh_track_info.sort_num = #{sortNum}


        and thigh_track_info.station_id = #{stationId}


        and thigh_track_info.status = #{status}


        and thigh_track_info.thigh_track_length = #{thighTrackLength}


        and thigh_track_info.thigh_track_no = #{thighTrackNo}


        and thigh_track_info.thigh_track_type = #{thighTrackType}


        and thigh_track_info.tie_category = #{tieCategory}


        and thigh_track_info.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and thigh_track_info.workarea_id = #{workareaId}
