mapper.select:
select
   turnout_info.commissioning_date,
   turnout_info.create_time,
   turnout_info.direction,
   turnout_info.frog_no,
   turnout_info.frog_structure,
   turnout_info.has_movable_point_frog,
   turnout_info.has_switch_rail,
   turnout_info.id,
   turnout_info.is_delete,
   turnout_info.line_id,
   turnout_info.miles,
   turnout_info.operator,
   turnout_info.position,
   turnout_info.remark,
   turnout_info.sort_num,
   turnout_info.station_id,
   turnout_info.status,
   turnout_info.switch_rail_type,
   turnout_info.thigh_track_id,
   turnout_info.track_type,
   turnout_info.turnout_kind,
   turnout_info.turnout_length,
   turnout_info.turnout_no,
   turnout_info.turnout_type,
   turnout_info.update_time,
   turnout_info.workarea_id
form turnout_info 


        turnout_info.commissioning_date between #{commissioningDateStart} and #{commissioningDateEnd}


        and turnout_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and turnout_info.direction = #{direction}


        and turnout_info.frog_no = #{frogNo}


        and turnout_info.frog_structure = #{frogStructure}


        and turnout_info.has_movable_point_frog = #{hasMovablePointFrog}


        and turnout_info.has_switch_rail = #{hasSwitchRail}


        and turnout_info.is_delete = #{isDelete}


        and turnout_info.line_id = #{lineId}


        and turnout_info.miles = #{miles}


        and turnout_info.operator = #{operator}


        and turnout_info.position = #{position}


        and turnout_info.sort_num = #{sortNum}


        and turnout_info.station_id = #{stationId}


        and turnout_info.status = #{status}


        and turnout_info.switch_rail_type = #{switchRailType}


        and turnout_info.thigh_track_id = #{thighTrackId}


        and turnout_info.track_type = #{trackType}


        and turnout_info.turnout_kind = #{turnoutKind}


        and turnout_info.turnout_length = #{turnoutLength}


        and turnout_info.turnout_no = #{turnoutNo}


        and turnout_info.turnout_type = #{turnoutType}


        and turnout_info.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and turnout_info.workarea_id = #{workareaId}
