mapper.select:
select
   welding_info.create_time,
   welding_info.direction,
   welding_info.hole_num,
   welding_info.id,
   welding_info.inner_glancing_flatness,
   welding_info.is_delete,
   welding_info.line_id,
   welding_info.miles,
   welding_info.operator,
   welding_info.position,
   welding_info.position_category,
   welding_info.reinforce_welding,
   welding_info.remark,
   welding_info.side,
   welding_info.station_id,
   welding_info.status,
   welding_info.thigh_track_id,
   welding_info.thign_track_category,
   welding_info.track_glancing_flatness,
   welding_info.track_id,
   welding_info.turnout_id,
   welding_info.turnout_thign_category,
   welding_info.update_time,
   welding_info.weld_date,
   welding_info.weld_track_temperature,
   welding_info.weld_user,
   welding_info.welder_unit,
   welding_info.welding_no,
   welding_info.welding_type,
   welding_info.welding_way,
   welding_info.workarea_id
form welding_info 


        welding_info.create_time between #{createTimeStart} and #{createTimeEnd}


        and welding_info.direction = #{direction}


        and welding_info.hole_num = #{holeNum}


        and welding_info.inner_glancing_flatness = #{innerGlancingFlatness}


        and welding_info.is_delete = #{isDelete}


        and welding_info.line_id = #{lineId}


        and welding_info.miles = #{miles}


        and welding_info.operator = #{operator}


        and welding_info.position = #{position}


        and welding_info.position_category = #{positionCategory}


        and welding_info.reinforce_welding = #{reinforceWelding}


        and welding_info.side = #{side}


        and welding_info.station_id = #{stationId}


        and welding_info.status = #{status}


        and welding_info.thigh_track_id = #{thighTrackId}


        and welding_info.thign_track_category = #{thignTrackCategory}


        and welding_info.track_glancing_flatness = #{trackGlancingFlatness}


        and welding_info.track_id = #{trackId}


        and welding_info.turnout_id = #{turnoutId}


        and welding_info.turnout_thign_category = #{turnoutThignCategory}


        and welding_info.update_time between #{updateTimeStart} and #{updateTimeEnd}


        and welding_info.weld_date between #{weldDateStart} and #{weldDateEnd}


        and welding_info.weld_track_temperature = #{weldTrackTemperature}


        and welding_info.weld_user = #{weldUser}


        and welding_info.welder_unit = #{welderUnit}


        and welding_info.welding_no = #{weldingNo}


        and welding_info.welding_type = #{weldingType}


        and welding_info.welding_way = #{weldingWay}


        and welding_info.workarea_id = #{workareaId}
