mapper.update:
update domain_model


      domain_model.domain_id = #{domainId},


      domain_model.model = #{model},


      domain_model.task_id = #{taskId}




      domain_model.id = #{id}

mapper.select:
select
    domain_model.domain_id,
    domain_model.id,
    domain_model.model,
    domain_model.task_id
form domain_model


    domain_model.domain_id = #{domainId}


    and domain_model.model = #{model}


    and domain_model.task_id = #{taskId}
