mapper.select:
select
    task_cookie_config.create_time,
    task_cookie_config.domain_id,
    task_cookie_config.expiration,
    task_cookie_config.id,
    task_cookie_config.key,
    task_cookie_config.task_config_main_id,
    task_cookie_config.value
form task_cookie_config


    task_cookie_config.create_time = #{createTime}


    and task_cookie_config.domain_id = #{domainId}


    and task_cookie_config.expiration = #{expiration}


    and task_cookie_config.key = #{key}


    and task_cookie_config.task_config_main_id = #{taskConfigMainId}


    and task_cookie_config.value = #{value}
