mapper.update:
update task_http_header_config


          task_http_header_config.task_config_main_id = #{TaskConfigMainId},


          task_http_header_config.key = #{Key},


          task_http_header_config.value = #{Value},


          task_http_header_config.create_time = #{CreateTime}




          task_http_header_config.id = #{id}

mapper.select:
select
   task_http_header_config.id,
   task_http_header_config.task_config_main_id,
   task_http_header_config.key,
   task_http_header_config.value,
   task_http_header_config.create_time
form task_http_header_config


        task_http_header_config.task_config_main_id = #{taskConfigMainId}


        and task_http_header_config.value = #{value}


        and task_http_header_config.create_time = #{createTime}
