mapper.select:
select
    task_info.create_time,
    task_info.description,
    task_info.end_time,
    task_info.id,
    task_info.level,
    task_info.main_config_id,
    task_info.queue_id,
    task_info.status,
    task_info.topic_id,
    task_info.user_id
form task_info


    task_info.create_time = #{createTime}


    and task_info.description = #{description}


    and task_info.end_time = #{endTime}


    and task_info.level = #{level}


    and task_info.main_config_id = #{mainConfigId}


    and task_info.queue_id = #{queueId}


    and task_info.status = #{status}


    and task_info.topic_id = #{topicId}


    and task_info.user_id = #{userId}
