mapper.select:
select
    task_run_record.end_time,
    task_run_record.id,
    task_run_record.page_downloaded,
    task_run_record.start_time,
    task_run_record.success,
    task_run_record.task_id,
    task_run_record.worker_joined
form task_run_record


    task_run_record.end_time = #{endTime}


    and task_run_record.page_downloaded = #{pageDownloaded}


    and task_run_record.start_time = #{startTime}


    and task_run_record.success = #{success}


    and task_run_record.task_id = #{taskId}


    and task_run_record.worker_joined = #{workerJoined}
