mapper.update:
update task_script_config


          task_script_config.task_config_main_id = #{TaskConfigMainId},


          task_script_config.file = #{File},


          task_script_config.version = #{Version},


          task_script_config.create_time = #{CreateTime}




          task_script_config.id = #{id}

mapper.select:
select
   task_script_config.id,
   task_script_config.task_config_main_id,
   task_script_config.file,
   task_script_config.version,
   task_script_config.create_time
form task_script_config


        task_script_config.task_config_main_id = #{taskConfigMainId}


        and task_script_config.version = #{version}


        and task_script_config.create_time = #{createTime}
