mapper.update:
update task_script_config_record


          task_script_config_record.file = #{File},


          task_script_config_record.version = #{Version},


          task_script_config_record.action = #{Action}




          task_script_config_record.id = #{id}

mapper.select:
select task_script_config_record.id, task_script_config_record.file, task_script_config_record.version, task_script_config_record.action form task_script_config_record
task_script_config_record.version = #{version}
and task_script_config_record.action = #{action}
