mapper.update:
update task_script_config_record


      task_script_config_record.action = #{action},


      task_script_config_record.file = #{file},


      task_script_config_record.version = #{version}




      task_script_config_record.id = #{id}

mapper.select:
select
    task_script_config_record.action,
    task_script_config_record.file,
    task_script_config_record.id,
    task_script_config_record.version
form task_script_config_record


    task_script_config_record.action = #{action}


    and task_script_config_record.file = #{file}


    and task_script_config_record.version = #{version}
