mapper.update:
update task_site_relation


          task_site_relation.site_id = #{SiteId},


          task_site_relation.task_id = #{TaskId},


          task_site_relation.user_id = #{UserId},


          task_site_relation.domain = #{Domain}




          task_site_relation.id = #{id}

mapper.select:
select
   task_site_relation.id,
   task_site_relation.site_id,
   task_site_relation.task_id,
   task_site_relation.user_id,
   task_site_relation.domain
form task_site_relation


        task_site_relation.site_id = #{siteId}


        and task_site_relation.task_id = #{taskId}


        and task_site_relation.user_id = #{userId}


        and task_site_relation.domain = #{domain}
