mapper.update:
update user_deduct_record


      user_deduct_record.amount = #{amount},


      user_deduct_record.deduct_time = #{deductTime},


      user_deduct_record.price = #{price},


      user_deduct_record.task_id = #{taskId}




      user_deduct_record.id = #{id}

mapper.select:
select
    user_deduct_record.amount,
    user_deduct_record.deduct_time,
    user_deduct_record.id,
    user_deduct_record.price,
    user_deduct_record.task_id
form user_deduct_record


    user_deduct_record.amount = #{amount}


    and user_deduct_record.deduct_time = #{deductTime}


    and user_deduct_record.price = #{price}


    and user_deduct_record.task_id = #{taskId}
