mapper.select:
select
   work_task_record.id,
   work_task_record.task_id,
   work_task_record.worker_id,
   work_task_record.task_main_config_id,
   work_task_record.page_downloaded,
   work_task_record.download_sucess,
   work_task_record.success,
   work_task_record.exit_code,
   work_task_record.start_time,
   work_task_record.end_time
form work_task_record


        work_task_record.task_id = #{taskId}


        and work_task_record.worker_id = #{workerId}


        and work_task_record.task_main_config_id = #{taskMainConfigId}


        range            

        and work_task_record.download_sucess = #{downloadSucess}


        and work_task_record.success = #{success}


        and work_task_record.exit_code = #{exitCode}


        and work_task_record.start_time = #{startTime}


        and work_task_record.end_time = #{endTime}
