mapper.update:
update worker_info


      worker_info.description = #{description},


      worker_info.ip = #{ip},


      worker_info.join_time = #{joinTime},


      worker_info.last_run_time = #{lastRunTime}




      worker_info.id = #{id}

mapper.select:
select
    worker_info.description,
    worker_info.id,
    worker_info.ip,
    worker_info.join_time,
    worker_info.last_run_time
form worker_info


    worker_info.description = #{description}


    and worker_info.ip = #{ip}


    and worker_info.join_time = #{joinTime}


    and worker_info.last_run_time = #{lastRunTime}
