mapper.select:
select
    bureau_line_order.bureau_id,
    bureau_line_order.create_time,
    bureau_line_order.id,
    bureau_line_order.line_id,
    bureau_line_order.operator,
    bureau_line_order.sort_num,
    bureau_line_order.update_time
form bureau_line_order


    bureau_line_order.bureau_id = #{bureauId}


    and bureau_line_order.create_time = #{createTime}


    and bureau_line_order.line_id = #{lineId}


    and bureau_line_order.operator = #{operator}


    and bureau_line_order.sort_num = #{sortNum}


    and bureau_line_order.update_time = #{updateTime}
