mapper.select:
select
    damage_deal_user.birthday,
    damage_deal_user.contact,
    damage_deal_user.create_time,
    damage_deal_user.deal_type,
    damage_deal_user.edu_degree,
    damage_deal_user.gender,
    damage_deal_user.grade_gain_date,
    damage_deal_user.grade_id,
    damage_deal_user.id,
    damage_deal_user.id_card_no,
    damage_deal_user.is_delete,
    damage_deal_user.name,
    damage_deal_user.operator,
    damage_deal_user.position_id,
    damage_deal_user.qualification_audit_date,
    damage_deal_user.qualification_gain_date,
    damage_deal_user.qualification_grade,
    damage_deal_user.qualification_invalid_date,
    damage_deal_user.qualification_valid_time,
    damage_deal_user.remark,
    damage_deal_user.status,
    damage_deal_user.tec_category,
    damage_deal_user.update_time,
    damage_deal_user.workarea_id
form damage_deal_user


    damage_deal_user.birthday = #{birthday}


    and damage_deal_user.contact = #{contact}


    and damage_deal_user.create_time = #{createTime}


    and damage_deal_user.deal_type = #{dealType}


    and damage_deal_user.edu_degree = #{eduDegree}


    and damage_deal_user.gender = #{gender}


    and damage_deal_user.grade_gain_date = #{gradeGainDate}


    and damage_deal_user.grade_id = #{gradeId}


    and damage_deal_user.id_card_no = #{idCardNo}


    and damage_deal_user.is_delete = #{isDelete}


    and damage_deal_user.name = #{name}


    and damage_deal_user.operator = #{operator}


    and damage_deal_user.position_id = #{positionId}


    and damage_deal_user.qualification_audit_date = #{qualificationAuditDate}


    and damage_deal_user.qualification_gain_date = #{qualificationGainDate}


    and damage_deal_user.qualification_grade = #{qualificationGrade}


    and damage_deal_user.qualification_invalid_date = #{qualificationInvalidDate}


    and damage_deal_user.qualification_valid_time = #{qualificationValidTime}


    and damage_deal_user.remark = #{remark}


    and damage_deal_user.status = #{status}


    and damage_deal_user.tec_category = #{tecCategory}


    and damage_deal_user.update_time = #{updateTime}


    and damage_deal_user.workarea_id = #{workareaId}
