mapper.select:
select
    damage_order.create_time,
    damage_order.is_delete,
    damage_order.order_no,
    damage_order.submit_staff,
    damage_order.submit_time,
    damage_order.update_time,
    damage_order.workarea_id
form damage_order


    damage_order.create_time = #{createTime}


    and damage_order.is_delete = #{isDelete}


    and damage_order.submit_staff = #{submitStaff}


    and damage_order.submit_time = #{submitTime}


    and damage_order.update_time = #{updateTime}


    and damage_order.workarea_id = #{workareaId}
