mapper.select:
select
    damage_repository_device.create_time,
    damage_repository_device.device_code,
    damage_repository_device.device_name,
    damage_repository_device.device_type,
    damage_repository_device.device_type_code,
    damage_repository_device.factory,
    damage_repository_device.nature,
    damage_repository_device.order_detail_id,
    damage_repository_device.radius,
    damage_repository_device.rail_type,
    damage_repository_device.seal_number,
    damage_repository_device.sleeper,
    damage_repository_device.total_weight,
    damage_repository_device.track_no,
    damage_repository_device.turnout_no,
    damage_repository_device.update_time,
    damage_repository_device.user_date
form damage_repository_device


    damage_repository_device.create_time = #{createTime}


    and damage_repository_device.device_code = #{deviceCode}


    and damage_repository_device.device_name = #{deviceName}


    and damage_repository_device.device_type = #{deviceType}


    and damage_repository_device.device_type_code = #{deviceTypeCode}


    and damage_repository_device.factory = #{factory}


    and damage_repository_device.nature = #{nature}


    and damage_repository_device.radius = #{radius}


    and damage_repository_device.rail_type = #{railType}


    and damage_repository_device.seal_number = #{sealNumber}


    and damage_repository_device.sleeper = #{sleeper}


    and damage_repository_device.total_weight = #{totalWeight}


    and damage_repository_device.track_no = #{trackNo}


    and damage_repository_device.turnout_no = #{turnoutNo}


    and damage_repository_device.update_time = #{updateTime}


    and damage_repository_device.user_date = #{userDate}
