mapper.select:
select
    damage_repository_position.bridge_tunnel,
    damage_repository_position.create_time,
    damage_repository_position.damage_position,
    damage_repository_position.direction,
    damage_repository_position.hole_position,
    damage_repository_position.line_category,
    damage_repository_position.line_id,
    damage_repository_position.line_name,
    damage_repository_position.miles,
    damage_repository_position.order_detail_id,
    damage_repository_position.station_id,
    damage_repository_position.stock_exchange,
    damage_repository_position.thigh_track_id,
    damage_repository_position.update_time
form damage_repository_position


    damage_repository_position.bridge_tunnel = #{bridgeTunnel}


    and damage_repository_position.create_time = #{createTime}


    and damage_repository_position.damage_position = #{damagePosition}


    and damage_repository_position.direction = #{direction}


    and damage_repository_position.hole_position = #{holePosition}


    and damage_repository_position.line_category = #{lineCategory}


    and damage_repository_position.line_id = #{lineId}


    and damage_repository_position.line_name = #{lineName}


    and damage_repository_position.miles = #{miles}


    and damage_repository_position.station_id = #{stationId}


    and damage_repository_position.stock_exchange = #{stockExchange}


    and damage_repository_position.thigh_track_id = #{thighTrackId}


    and damage_repository_position.update_time = #{updateTime}
