mapper.select:
select
    damage_type.create_time,
    damage_type.damage_desc,
    damage_type.damage_no,
    damage_type.damage_type,
    damage_type.id,
    damage_type.is_delete,
    damage_type.operator,
    damage_type.sort_num,
    damage_type.status,
    damage_type.update_time
form damage_type


    damage_type.create_time = #{createTime}


    and damage_type.damage_desc = #{damageDesc}


    and damage_type.damage_no = #{damageNo}


    and damage_type.damage_type = #{damageType}


    and damage_type.is_delete = #{isDelete}


    and damage_type.operator = #{operator}


    and damage_type.sort_num = #{sortNum}


    and damage_type.status = #{status}


    and damage_type.update_time = #{updateTime}
