mapper.select:
select
    detect_goup_user.create_time,
    detect_goup_user.group_id,
    detect_goup_user.id,
    detect_goup_user.is_delete,
    detect_goup_user.member_id,
    detect_goup_user.operator,
    detect_goup_user.status,
    detect_goup_user.update_time
form detect_goup_user


    detect_goup_user.create_time = #{createTime}


    and detect_goup_user.group_id = #{groupId}


    and detect_goup_user.is_delete = #{isDelete}


    and detect_goup_user.member_id = #{memberId}


    and detect_goup_user.operator = #{operator}


    and detect_goup_user.status = #{status}


    and detect_goup_user.update_time = #{updateTime}
