mapper.select:
select
    detect_staff_info_copy.birthday,
    detect_staff_info_copy.contact,
    detect_staff_info_copy.create_time,
    detect_staff_info_copy.edu_degree,
    detect_staff_info_copy.gender,
    detect_staff_info_copy.grade_gain_date,
    detect_staff_info_copy.grade_id,
    detect_staff_info_copy.id,
    detect_staff_info_copy.id_card_no,
    detect_staff_info_copy.is_delete,
    detect_staff_info_copy.name,
    detect_staff_info_copy.operator,
    detect_staff_info_copy.position_id,
    detect_staff_info_copy.qualification_audit_date,
    detect_staff_info_copy.qualification_gain_date,
    detect_staff_info_copy.qualification_grade,
    detect_staff_info_copy.qualification_invalid_date,
    detect_staff_info_copy.qualification_valid_time,
    detect_staff_info_copy.remark,
    detect_staff_info_copy.status,
    detect_staff_info_copy.tec_category,
    detect_staff_info_copy.update_time,
    detect_staff_info_copy.workarea_id
form detect_staff_info_copy


    detect_staff_info_copy.birthday = #{birthday}


    and detect_staff_info_copy.contact = #{contact}


    and detect_staff_info_copy.create_time = #{createTime}


    and detect_staff_info_copy.edu_degree = #{eduDegree}


    and detect_staff_info_copy.gender = #{gender}


    and detect_staff_info_copy.grade_gain_date = #{gradeGainDate}


    and detect_staff_info_copy.grade_id = #{gradeId}


    and detect_staff_info_copy.id_card_no = #{idCardNo}


    and detect_staff_info_copy.is_delete = #{isDelete}


    and detect_staff_info_copy.name = #{name}


    and detect_staff_info_copy.operator = #{operator}


    and detect_staff_info_copy.position_id = #{positionId}


    and detect_staff_info_copy.qualification_audit_date = #{qualificationAuditDate}


    and detect_staff_info_copy.qualification_gain_date = #{qualificationGainDate}


    and detect_staff_info_copy.qualification_grade = #{qualificationGrade}


    and detect_staff_info_copy.qualification_invalid_date = #{qualificationInvalidDate}


    and detect_staff_info_copy.qualification_valid_time = #{qualificationValidTime}


    and detect_staff_info_copy.remark = #{remark}


    and detect_staff_info_copy.status = #{status}


    and detect_staff_info_copy.tec_category = #{tecCategory}


    and detect_staff_info_copy.update_time = #{updateTime}


    and detect_staff_info_copy.workarea_id = #{workareaId}
